value.string:
// CodeMirror version 3.22
 //
 // CodeMirror is the only global var we claim
 window.CodeMirror = (function() {
 "use strict";

 // BROWSER SNIFFING

 // Crude, but necessary to handle a number of hard-to-feature-detect
 // bugs and behavior differences.
 var gecko = /gecko\/\d/i.test(navigator.userAgent);
 // IE11 currently doesn't count as 'ie', since it has almost none of
 // the same bugs as earlier versions. Use ie_gt10 to handle
 // incompatibilities in that version.
 var old_ie = /MSIE \d/.test(navigator.userAgent);
 var ie_lt8 = old_ie && (document.documentMode == null || document.documentMode < 8);
 var ie_lt9 = old_ie && (document.documentMode == null || document.documentMode < 9);
 var ie_lt10 = old_ie && (document.documentMode == null || document.documentMode < 10);
 var ie_gt10 = /Trident\/([7-9]|\d{2,})\./.test(navigator.userAgent);
 var ie = old_ie || ie_gt10;
 var webkit = /WebKit\//.test(navigator.userAgent);
 var qtwebkit = webkit && /Qt\/\d+\.\d+/.test(navigator.userAgent);
 var chrome = /Chrome\//.test(navigator.userAgent);
 var opera = /Opera\//.test(navigator.userAgent);
 var safari = /Apple Computer/.test(navigator.vendor);
 var khtml = /KHTML\//.test(navigator.userAgent);
 var mac_geLion = /Mac OS X 1\d\D([7-9]|\d\d)\D/.test(navigator.userAgent);
 var mac_geMountainLion = /Mac OS X 1\d\D([8-9]|\d\d)\D/.test(navigator.userAgent);
 var phantom = /PhantomJS/.test(navigator.userAgent);

 var ios = /AppleWebKit/.test(navigator.userAgent) && /Mobile\/\w+/.test(navigator.userAgent);
 // This is woefully incomplete. Suggestions for alternative methods welcome.
 var mobile = ios || /Android|webOS|BlackBerry|Opera Mini|Opera Mobi|IEMobile/i.test(navigator.userAgent);
 var mac = ios || /Mac/.test(navigator.platform);
 var windows = /win/i.test(navigator.platform);

 var opera_version = opera && navigator.userAgent.match(/Version\/(\d*\.\d*)/);
 if (opera_version) opera_version = Number(opera_version[1]);
 if (opera_version && opera_version >= 15) { opera = false; webkit = true; }
 // Some browsers use the wrong event properties to signal cmd/ctrl on OS X
 var flipCtrlCmd = mac && (qtwebkit || opera && (opera_version == null || opera_version < 12.11));
 var captureMiddleClick = gecko || (ie && !ie_lt9);

 // Optimize some code when these features are not used
 var sawReadOnlySpans = false, sawCollapsedSpans = false;

 // CONSTRUCTOR

 function CodeMirror(place, options) {
 if (!(this instanceof CodeMirror)) return new CodeMirror(place, options);

 this.options = options = options || {};
 // Determine effective options based on given values and defaults.
 for (var opt in defaults) if (!options.hasOwnProperty(opt) && defaults.hasOwnProperty(opt))
 options[opt] = defaults[opt];
 setGuttersForLineNumbers(options);

 var docStart = typeof options.value == "string" ? 0 : options.value.first;
 var display = this.display = makeDisplay(place, docStart);
 display.wrapper.CodeMirror = this;
 updateGutters(this);
 if (options.autofocus && !mobile) focusInput(this);

 this.state = {keyMaps: [],
 overlays: [],
 modeGen: 0,
 overwrite: false, focused: false,
 suppressEdits: false,
 pasteIncoming: false, cutIncoming: false,
 draggingText: false,
 highlight: new Delayed()};

 themeChanged(this);
 if (options.lineWrapping)
 this.display.wrapper.className += " CodeMirror-wrap";

 var doc = options.value;
 if (typeof doc == "string") doc = new Doc(options.value, options.mode);
 operation(this, attachDoc)(this, doc);

 // Override magic textarea content restore that IE sometimes does
 // on our hidden textarea on reload
 if (old_ie) setTimeout(bind(resetInput, this, true), 20);

 registerEventHandlers(this);
 // IE throws unspecified error in certain cases, when
 // trying to access activeElement before onload
 var hasFocus; try { hasFocus = (document.activeElement == display.input); } catch(e) { }
 if (hasFocus || (options.autofocus && !mobile)) setTimeout(bind(onFocus, this), 20);
 else onBlur(this);

 operation(this, function() {
 for (var opt in optionHandlers)
 if (optionHandlers.propertyIsEnumerable(opt))
 optionHandlers[opt](this, options[opt], Init);
 for (var i = 0; i < initHooks.length; ++i) initHooks[i](this);
 })();
 }

 // DISPLAY CONSTRUCTOR

 function makeDisplay(place, docStart) {
 var d = {};

 var input = d.input = elt("textarea", null, null, "position: absolute; padding: 0; width: 1px; height: 1em; outline: none");
 if (webkit) input.style.width = "1000px";
 else input.setAttribute("wrap", "off");
 // if border: 0; -- iOS fails to open keyboard (issue #1287)
 if (ios) input.style.border = "1px solid black";
 input.setAttribute("autocorrect", "off"); input.setAttribute("autocapitalize", "off"); input.setAttribute("spellcheck", "false");

 // Wraps and hides input textarea
 d.inputDiv = elt("div", [input], null, "overflow: hidden; position: relative; width: 3px; height: 0px;");
 // The actual fake scrollbars.
 d.scrollbarH = elt("div", [elt("div", null, null, "height: 1px")], "CodeMirror-hscrollbar");
 d.scrollbarV = elt("div", [elt("div", null, null, "width: 1px")], "CodeMirror-vscrollbar");
 d.scrollbarFiller = elt("div", null, "CodeMirror-scrollbar-filler");
 d.gutterFiller = elt("div", null, "CodeMirror-gutter-filler");
 // DIVs containing the selection and the actual code
 d.lineDiv = elt("div", null, "CodeMirror-code");
 d.selectionDiv = elt("div", null, null, "position: relative; z-index: 1");
 // Blinky cursor, and element used to ensure cursor fits at the end of a line
 d.cursor = elt("div", "\u00a0", "CodeMirror-cursor");
 // Secondary cursor, shown when on a 'jump' in bi-directional text
 d.otherCursor = elt("div", "\u00a0", "CodeMirror-cursor CodeMirror-secondarycursor");
 // Used to measure text size
 d.measure = elt("div", null, "CodeMirror-measure");
 // Wraps everything that needs to exist inside the vertically-padded coordinate system
 d.lineSpace = elt("div", [d.measure, d.selectionDiv, d.lineDiv, d.cursor, d.otherCursor],
 null, "position: relative; outline: none");
 // Moved around its parent to cover visible view
 d.mover = elt("div", [elt("div", [d.lineSpace], "CodeMirror-lines")], null, "position: relative");
 // Set to the height of the text, causes scrolling
 d.sizer = elt("div", [d.mover], "CodeMirror-sizer");
 // D is needed because behavior of elts with overflow: auto and padding is inconsistent across browsers
 d.heightForcer = elt("div", null, null, "position: absolute; height: " + scrollerCutOff + "px; width: 1px;");
 // Will contain the gutters, if any
 d.gutters = elt("div", null, "CodeMirror-gutters");
 d.lineGutter = null;
 // Provides scrolling
 d.scroller = elt("div", [d.sizer, d.heightForcer, d.gutters], "CodeMirror-scroll");
 d.scroller.setAttribute("tabIndex", "-1");
 // The element in which the editor lives.
 d.wrapper = elt("div", [d.inputDiv, d.scrollbarH, d.scrollbarV,
 d.scrollbarFiller, d.gutterFiller, d.scroller], "CodeMirror");
 // Work around IE7 z-index bug
 if (ie_lt8) { d.gutters.style.zIndex = -1; d.scroller.style.paddingRight = 0; }
 if (place.appendChild) place.appendChild(d.wrapper); else place(d.wrapper);

 // Needed to hide big blue blinking cursor on Mobile Safari
 if (ios) input.style.width = "0px";
 if (!webkit) d.scroller.draggable = true;
 // Needed to handle Tab key in KHTML
 if (khtml) { d.inputDiv.style.height = "1px"; d.inputDiv.style.position = "absolute"; }
 // Need to set a minimum width to see the scrollbar on IE7 (but must not set it on IE8).
 else if (ie_lt8) d.scrollbarH.style.minWidth = d.scrollbarV.style.minWidth = "18px";

 // Current visible range (may be bigger than the view window).
 d.viewOffset = d.lastSizeC = 0;
 d.showingFrom = d.showingTo = docStart;

 // Used to only resize the line number gutter when necessary (when
 // the amount of lines crosses a boundary that makes its width change)
 d.lineNumWidth = d.lineNumInnerWidth = d.lineNumChars = null;
 // See readInput and resetInput
 d.prevInput = "";
 // Set to true when a non-horizontal-scrolling widget is added. As
 // an optimization, widget aligning is skipped when d is false.
 d.alignWidgets = false;
 // Flag that indicates whether we currently expect input to appear
 // (after some event like 'keypress' or 'input') and are polling
 // intensively.
 d.pollingFast = false;
 // Self-resetting timeout for the poller
 d.poll = new Delayed();

 d.cachedCharWidth = d.cachedTextHeight = d.cachedPaddingH = null;
 d.measureLineCache = [];
 d.measureLineCachePos = 0;

 // Tracks when resetInput has punted to just putting a short
 // string instead of the (large) selection.
 d.inaccurateSelection = false;

 // Tracks the maximum line length so that the horizontal scrollbar
 // can be kept static when scrolling.
 d.maxLine = null;
 d.maxLineLength = 0;
 d.maxLineChanged = false;

 // Used for measuring wheel scrolling granularity
 d.wheelDX = d.wheelDY = d.wheelStartX = d.wheelStartY = null;

 return d;
 }

 // STATE UPDATES

 // Used to get the editor into a consistent state again when options change.

 function loadMode(cm) {
 cm.doc.mode = CodeMirror.getMode(cm.options, cm.doc.modeOption);
 resetModeState(cm);
 }

 function resetModeState(cm) {
 cm.doc.iter(function(line) {
 if (line.stateAfter) line.stateAfter = null;
 if (line.styles) line.styles = null;
 });
 cm.doc.frontier = cm.doc.first;
 startWorker(cm, 100);
 cm.state.modeGen++;
 if (cm.curOp) regChange(cm);
 }

 function wrappingChanged(cm) {
 if (cm.options.lineWrapping) {
 cm.display.wrapper.className += " CodeMirror-wrap";
 cm.display.sizer.style.minWidth = "";
 } else {
 cm.display.wrapper.className = cm.display.wrapper.className.replace(" CodeMirror-wrap", "");
 computeMaxLength(cm);
 }
 estimateLineHeights(cm);
 regChange(cm);
 clearCaches(cm);
 setTimeout(function(){updateScrollbars(cm);}, 100);
 }

 function estimateHeight(cm) {
 var th = textHeight(cm.display), wrapping = cm.options.lineWrapping;
 var perLine = wrapping && Math.max(5, cm.display.scroller.clientWidth / charWidth(cm.display) - 3);
 return function(line) {
 if (lineIsHidden(cm.doc, line))
 return 0;
 else if (wrapping)
 return (Math.ceil(line.text.length / perLine) || 1) * th;
 else
 return th;
 };
 }

 function estimateLineHeights(cm) {
 var doc = cm.doc, est = estimateHeight(cm);
 doc.iter(function(line) {
 var estHeight = est(line);
 if (estHeight != line.height) updateLineHeight(line, estHeight);
 });
 }

 function keyMapChanged(cm) {
 var map = keyMap[cm.options.keyMap], style = map.style;
 cm.display.wrapper.className = cm.display.wrapper.className.replace(/\s*cm-keymap-\S+/g, "") +
 (style ? " cm-keymap-" + style : "");
 }

 function themeChanged(cm) {
 cm.display.wrapper.className = cm.display.wrapper.className.replace(/\s*cm-s-\S+/g, "") +
 cm.options.theme.replace(/(^|\s)\s*/g, " cm-s-");
 clearCaches(cm);
 }

 function guttersChanged(cm) {
 updateGutters(cm);
 regChange(cm);
 setTimeout(function(){alignHorizontally(cm);}, 20);
 }

 function updateGutters(cm) {
 var gutters = cm.display.gutters, specs = cm.options.gutters;
 removeChildren(gutters);
 for (var i = 0; i < specs.length; ++i) {
 var gutterClass = specs[i];
 var gElt = gutters.appendChild(elt("div", null, "CodeMirror-gutter " + gutterClass));
 if (gutterClass == "CodeMirror-linenumbers") {
 cm.display.lineGutter = gElt;
 gElt.style.width = (cm.display.lineNumWidth || 1) + "px";
 }
 }
 gutters.style.display = i ? "" : "none";
 }

 function lineLength(doc, line) {
 if (line.height == 0) return 0;
 var len = line.text.length, merged, cur = line;
 while (merged = collapsedSpanAtStart(cur)) {
 var found = merged.find();
 cur = getLine(doc, found.from.line);
 len += found.from.ch - found.to.ch;
 }
 cur = line;
 while (merged = collapsedSpanAtEnd(cur)) {
 var found = merged.find();
 len -= cur.text.length - found.from.ch;
 cur = getLine(doc, found.to.line);
 len += cur.text.length - found.to.ch;
 }
 return len;
 }

 function computeMaxLength(cm) {
 var d = cm.display, doc = cm.doc;
 d.maxLine = getLine(doc, doc.first);
 d.maxLineLength = lineLength(doc, d.maxLine);
 d.maxLineChanged = true;
 doc.iter(function(line) {
 var len = lineLength(doc, line);
 if (len > d.maxLineLength) {
 d.maxLineLength = len;
 d.maxLine = line;
 }
 });
 }

 // Make sure the gutters options contains the element
 // "CodeMirror-linenumbers" when the lineNumbers option is true.
 function setGuttersForLineNumbers(options) {
 var found = indexOf(options.gutters, "CodeMirror-linenumbers");
 if (found == -1 && options.lineNumbers) {
 options.gutters = options.gutters.concat(["CodeMirror-linenumbers"]);
 } else if (found > -1 && !options.lineNumbers) {
 options.gutters = options.gutters.slice(0);
 options.gutters.splice(found, 1);
 }
 }

 // SCROLLBARS

 // Re-synchronize the fake scrollbars with the actual size of the
 // content. Optionally force a scrollTop.
 function updateScrollbars(cm) {
 var d = cm.display, docHeight = cm.doc.height;
 var totalHeight = docHeight + paddingVert(d);
 d.sizer.style.minHeight = d.heightForcer.style.top = totalHeight + "px";
 d.gutters.style.height = Math.max(totalHeight, d.scroller.clientHeight - scrollerCutOff) + "px";
 var scrollHeight = Math.max(totalHeight, d.scroller.scrollHeight);
 var needsH = d.scroller.scrollWidth > (d.scroller.clientWidth + 1);
 var needsV = scrollHeight > (d.scroller.clientHeight + 1);
 if (needsV) {
 d.scrollbarV.style.display = "block";
 d.scrollbarV.style.bottom = needsH ? scrollbarWidth(d.measure) + "px" : "0";
 // A bug in IE8 can cause this value to be negative, so guard it.
 d.scrollbarV.firstChild.style.height =
 Math.max(0, scrollHeight - d.scroller.clientHeight + d.scrollbarV.clientHeight) + "px";
 } else {
 d.scrollbarV.style.display = "";
 d.scrollbarV.firstChild.style.height = "0";
 }
 if (needsH) {
 d.scrollbarH.style.display = "block";
 d.scrollbarH.style.right = needsV ? scrollbarWidth(d.measure) + "px" : "0";
 d.scrollbarH.firstChild.style.width =
 (d.scroller.scrollWidth - d.scroller.clientWidth + d.scrollbarH.clientWidth) + "px";
 } else {
 d.scrollbarH.style.display = "";
 d.scrollbarH.firstChild.style.width = "0";
 }
 if (needsH && needsV) {
 d.scrollbarFiller.style.display = "block";
 d.scrollbarFiller.style.height = d.scrollbarFiller.style.width = scrollbarWidth(d.measure) + "px";
 } else d.scrollbarFiller.style.display = "";
 if (needsH && cm.options.coverGutterNextToScrollbar && cm.options.fixedGutter) {
 d.gutterFiller.style.display = "block";
 d.gutterFiller.style.height = scrollbarWidth(d.measure) + "px";
 d.gutterFiller.style.width = d.gutters.offsetWidth + "px";
 } else d.gutterFiller.style.display = "";

 if (mac_geLion && scrollbarWidth(d.measure) === 0) {
 d.scrollbarV.style.minWidth = d.scrollbarH.style.minHeight = mac_geMountainLion ? "18px" : "12px";
 d.scrollbarV.style.pointerEvents = d.scrollbarH.style.pointerEvents = "none";
 }
 }

 function visibleLines(display, doc, viewPort) {
 var top = display.scroller.scrollTop, height = display.wrapper.clientHeight;
 if (typeof viewPort == "number") top = viewPort;
 else if (viewPort) {top = viewPort.top; height = viewPort.bottom - viewPort.top;}
 top = Math.floor(top - paddingTop(display));
 var bottom = Math.ceil(top + height);
 return {from: lineAtHeight(doc, top), to: lineAtHeight(doc, bottom)};
 }

 // LINE NUMBERS

 function alignHorizontally(cm) {
 var display = cm.display;
 if (!display.alignWidgets && (!display.gutters.firstChild || !cm.options.fixedGutter)) return;
 var comp = compensateForHScroll(display) - display.scroller.scrollLeft + cm.doc.scrollLeft;
 var gutterW = display.gutters.offsetWidth, l = comp + "px";
 for (var n = display.lineDiv.firstChild; n; n = n.nextSibling) if (n.alignable) {
 for (var i = 0, a = n.alignable; i < a.length; ++i) a[i].style.left = l;
 }
 if (cm.options.fixedGutter)
 display.gutters.style.left = (comp + gutterW) + "px";
 }

 function maybeUpdateLineNumberWidth(cm) {
 if (!cm.options.lineNumbers) return false;
 var doc = cm.doc, last = lineNumberFor(cm.options, doc.first + doc.size - 1), display = cm.display;
 if (last.length != display.lineNumChars) {
 var test = display.measure.appendChild(elt("div", [elt("div", last)],
 "CodeMirror-linenumber CodeMirror-gutter-elt"));
 var innerW = test.firstChild.offsetWidth, padding = test.offsetWidth - innerW;
 display.lineGutter.style.width = "";
 display.lineNumInnerWidth = Math.max(innerW, display.lineGutter.offsetWidth - padding);
 display.lineNumWidth = display.lineNumInnerWidth + padding;
 display.lineNumChars = display.lineNumInnerWidth ? last.length : -1;
 display.lineGutter.style.width = display.lineNumWidth + "px";
 return true;
 }
 return false;
 }

 function lineNumberFor(options, i) {
 return String(options.lineNumberFormatter(i + options.firstLineNumber));
 }
 function compensateForHScroll(display) {
 return getRect(display.scroller).left - getRect(display.sizer).left;
 }

 // DISPLAY DRAWING

 function updateDisplay(cm, changes, viewPort, forced) {
 var oldFrom = cm.display.showingFrom, oldTo = cm.display.showingTo, updated;
 var visible = visibleLines(cm.display, cm.doc, viewPort);
 for (var first = true;; first = false) {
 var oldWidth = cm.display.scroller.clientWidth;
 if (!updateDisplayInner(cm, changes, visible, forced)) break;
 updated = true;
 changes = [];
 updateSelection(cm);
 updateScrollbars(cm);
 if (first && cm.options.lineWrapping && oldWidth != cm.display.scroller.clientWidth) {
 forced = true;
 continue;
 }
 forced = false;

 // Clip forced viewport to actual scrollable area
 if (viewPort)
 viewPort = Math.min(cm.display.scroller.scrollHeight - cm.display.scroller.clientHeight,
 typeof viewPort == "number" ? viewPort : viewPort.top);
 visible = visibleLines(cm.display, cm.doc, viewPort);
 if (visible.from >= cm.display.showingFrom && visible.to <= cm.display.showingTo)
 break;
 }

 if (updated) {
 signalLater(cm, "update", cm);
 if (cm.display.showingFrom != oldFrom || cm.display.showingTo != oldTo)
 signalLater(cm, "viewportChange", cm, cm.display.showingFrom, cm.display.showingTo);
 }
 return updated;
 }

 // Uses a set of changes plus the current scroll position to
 // determine which DOM updates have to be made, and makes the
 // updates.
 function updateDisplayInner(cm, changes, visible, forced) {
 var display = cm.display, doc = cm.doc;
 if (!display.wrapper.offsetWidth) {
 display.showingFrom = display.showingTo = doc.first;
 display.viewOffset = 0;
 return;
 }

 // Bail out if the visible area is already rendered and nothing changed.
 if (!forced && changes.length == 0 &&
 visible.from > display.showingFrom && visible.to < display.showingTo)
 return;

 if (maybeUpdateLineNumberWidth(cm))
 changes = [{from: doc.first, to: doc.first + doc.size}];
 var gutterW = display.sizer.style.marginLeft = display.gutters.offsetWidth + "px";
 display.scrollbarH.style.left = cm.options.fixedGutter ? gutterW : "0";

 // Used to determine which lines need their line numbers updated
 var positionsChangedFrom = Infinity;
 if (cm.options.lineNumbers)
 for (var i = 0; i < changes.length; ++i)
 if (changes[i].diff && changes[i].from < positionsChangedFrom) { positionsChangedFrom = changes[i].from; }

 var end = doc.first + doc.size;
 var from = Math.max(visible.from - cm.options.viewportMargin, doc.first);
 var to = Math.min(end, visible.to + cm.options.viewportMargin);
 if (display.showingFrom < from && from - display.showingFrom < 20) from = Math.max(doc.first, display.showingFrom);
 if (display.showingTo > to && display.showingTo - to < 20) to = Math.min(end, display.showingTo);
 if (sawCollapsedSpans) {
 from = lineNo(visualLine(doc, getLine(doc, from)));
 while (to < end && lineIsHidden(doc, getLine(doc, to))) ++to;
 }

 // Create a range of theoretically intact lines, and punch holes
 // in that using the change info.
 var intact = [{from: Math.max(display.showingFrom, doc.first),
 to: Math.min(display.showingTo, end)}];
 if (intact[0].from >= intact[0].to) intact = [];
 else intact = computeIntact(intact, changes);
 // When merged lines are present, we might have to reduce the
 // intact ranges because changes in continued fragments of the
 // intact lines do require the lines to be redrawn.
 if (sawCollapsedSpans)
 for (var i = 0; i < intact.length; ++i) {
 var range = intact[i], merged;
 while (merged = collapsedSpanAtEnd(getLine(doc, range.to - 1))) {
 var newTo = merged.find().from.line;
 if (newTo > range.from) range.to = newTo;
 else { intact.splice(i--, 1); break; }
 }
 }

 // Clip off the parts that won't be visible
 var intactLines = 0;
 for (var i = 0; i < intact.length; ++i) {
 var range = intact[i];
 if (range.from < from) range.from = from;
 if (range.to > to) range.to = to;
 if (range.from >= range.to) intact.splice(i--, 1);
 else intactLines += range.to - range.from;
 }
 if (!forced && intactLines == to - from && from == display.showingFrom && to == display.showingTo) {
 updateViewOffset(cm);
 return;
 }
 intact.sort(function(a, b) {return a.from - b.from;});

 // Avoid crashing on IE's "unspecified error" when in iframes
 try {
 var focused = document.activeElement;
 } catch(e) {}
 if (intactLines < (to - from) * .7) display.lineDiv.style.display = "none";
 patchDisplay(cm, from, to, intact, positionsChangedFrom);
 display.lineDiv.style.display = "";
 if (focused && document.activeElement != focused && focused.offsetHeight) focused.focus();

 var different = from != display.showingFrom || to != display.showingTo ||
 display.lastSizeC != display.wrapper.clientHeight;
 // This is just a bogus formula that detects when the editor is
 // resized or the font size changes.
 if (different) {
 display.lastSizeC = display.wrapper.clientHeight;
 startWorker(cm, 400);
 }
 display.showingFrom = from; display.showingTo = to;

 display.gutters.style.height = "";
 updateHeightsInViewport(cm);
 updateViewOffset(cm);

 return true;
 }

 function updateHeightsInViewport(cm) {
 var display = cm.display;
 var prevBottom = display.lineDiv.offsetTop;
 for (var node = display.lineDiv.firstChild, height; node; node = node.nextSibling) if (node.lineObj) {
 if (ie_lt8) {
 var bot = node.offsetTop + node.offsetHeight;
 height = bot - prevBottom;
 prevBottom = bot;
 } else {
 var box = getRect(node);
 height = box.bottom - box.top;
 }
 var diff = node.lineObj.height - height;
 if (height < 2) height = textHeight(display);
 if (diff > .001 || diff < -.001) {
 updateLineHeight(node.lineObj, height);
 var widgets = node.lineObj.widgets;
 if (widgets) for (var i = 0; i < widgets.length; ++i)
 widgets[i].height = widgets[i].node.offsetHeight;
 }
 }
 }

 function updateViewOffset(cm) {
 var off = cm.display.viewOffset = heightAtLine(cm, getLine(cm.doc, cm.display.showingFrom));
 // Position the mover div to align with the current virtual scroll position
 cm.display.mover.style.top = off + "px";
 }

 function computeIntact(intact, changes) {
 for (var i = 0, l = changes.length || 0; i < l; ++i) {
 var change = changes[i], intact2 = [], diff = change.diff || 0;
 for (var j = 0, l2 = intact.length; j < l2; ++j) {
 var range = intact[j];
 if (change.to <= range.from && change.diff) {
 intact2.push({from: range.from + diff, to: range.to + diff});
 } else if (change.to <= range.from || change.from >= range.to) {
 intact2.push(range);
 } else {
 if (change.from > range.from)
 intact2.push({from: range.from, to: change.from});
 if (change.to < range.to)
 intact2.push({from: change.to + diff, to: range.to + diff});
 }
 }
 intact = intact2;
 }
 return intact;
 }

 function getDimensions(cm) {
 var d = cm.display, left = {}, width = {};
 for (var n = d.gutters.firstChild, i = 0; n; n = n.nextSibling, ++i) {
 left[cm.options.gutters[i]] = n.offsetLeft;
 width[cm.options.gutters[i]] = n.offsetWidth;
 }
 return {fixedPos: compensateForHScroll(d),
 gutterTotalWidth: d.gutters.offsetWidth,
 gutterLeft: left,
 gutterWidth: width,
 wrapperWidth: d.wrapper.clientWidth};
 }

 function patchDisplay(cm, from, to, intact, updateNumbersFrom) {
 var dims = getDimensions(cm);
 var display = cm.display, lineNumbers = cm.options.lineNumbers;
 if (!intact.length && (!webkit || !cm.display.currentWheelTarget))
 removeChildren(display.lineDiv);
 var container = display.lineDiv, cur = container.firstChild;

 function rm(node) {
 var next = node.nextSibling;
 if (webkit && mac && cm.display.currentWheelTarget == node) {
 node.style.display = "none";
 node.lineObj = null;
 } else {
 node.parentNode.removeChild(node);
 }
 return next;
 }

 var nextIntact = intact.shift(), lineN = from;
 cm.doc.iter(from, to, function(line) {
 if (nextIntact && nextIntact.to == lineN) nextIntact = intact.shift();
 if (lineIsHidden(cm.doc, line)) {
 if (line.height != 0) updateLineHeight(line, 0);
 if (line.widgets && cur && cur.previousSibling) for (var i = 0; i < line.widgets.length; ++i) {
 var w = line.widgets[i];
 if (w.showIfHidden) {
 var prev = cur.previousSibling;
 if (/pre/i.test(prev.nodeName)) {
 var wrap = elt("div", null, null, "position: relative");
 prev.parentNode.replaceChild(wrap, prev);
 wrap.appendChild(prev);
 prev = wrap;
 }
 var wnode = prev.appendChild(elt("div", [w.node], "CodeMirror-linewidget"));
 if (!w.handleMouseEvents) wnode.ignoreEvents = true;
 positionLineWidget(w, wnode, prev, dims);
 }
 }
 } else if (nextIntact && nextIntact.from <= lineN && nextIntact.to > lineN) {
 // This line is intact. Skip to the actual node. Update its
 // line number if needed.
 while (cur.lineObj != line) cur = rm(cur);
 if (lineNumbers && updateNumbersFrom <= lineN && cur.lineNumber)
 setTextContent(cur.lineNumber, lineNumberFor(cm.options, lineN));
 cur = cur.nextSibling;
 } else {
 // For lines with widgets, make an attempt to find and reuse
 // the existing element, so that widgets aren't needlessly
 // removed and re-inserted into the dom
 if (line.widgets) for (var j = 0, search = cur, reuse; search && j < 20; ++j, search = search.nextSibling)
 if (search.lineObj == line && /div/i.test(search.nodeName)) { reuse = search; break; }
 // This line needs to be generated.
 var lineNode = buildLineElement(cm, line, lineN, dims, reuse);
 if (lineNode != reuse) {
 container.insertBefore(lineNode, cur);
 } else {
 while (cur != reuse) cur = rm(cur);
 cur = cur.nextSibling;
 }

 lineNode.lineObj = line;
 }
 ++lineN;
 });
 while (cur) cur = rm(cur);
 }

 function buildLineElement(cm, line, lineNo, dims, reuse) {
 var built = buildLineContent(cm, line), lineElement = built.pre;
 var markers = line.gutterMarkers, display = cm.display, wrap;

 var bgClass = built.bgClass ? built.bgClass + " " + (line.bgClass || "") : line.bgClass;
 if (!cm.options.lineNumbers && !markers && !bgClass && !line.wrapClass && !line.widgets)
 return lineElement;

 // Lines with gutter elements, widgets or a background class need
 // to be wrapped again, and have the extra elements added to the
 // wrapper div

 if (reuse) {
 reuse.alignable = null;
 var isOk = true, widgetsSeen = 0, insertBefore = null;
 for (var n = reuse.firstChild, next; n; n = next) {
 next = n.nextSibling;
 if (!/\bCodeMirror-linewidget\b/.test(n.className)) {
 reuse.removeChild(n);
 } else {
 for (var i = 0; i < line.widgets.length; ++i) {
 var widget = line.widgets[i];
 if (widget.node == n.firstChild) {
 if (!widget.above && !insertBefore) insertBefore = n;
 positionLineWidget(widget, n, reuse, dims);
 ++widgetsSeen;
 break;
 }
 }
 if (i == line.widgets.length) { isOk = false; break; }
 }
 }
 reuse.insertBefore(lineElement, insertBefore);
 if (isOk && widgetsSeen == line.widgets.length) {
 wrap = reuse;
 reuse.className = line.wrapClass || "";
 }
 }
 if (!wrap) {
 wrap = elt("div", null, line.wrapClass, "position: relative");
 wrap.appendChild(lineElement);
 }
 // Kludge to make sure the styled element lies behind the selection (by z-index)
 if (bgClass)
 wrap.insertBefore(elt("div", null, bgClass + " CodeMirror-linebackground"), wrap.firstChild);
 if (cm.options.lineNumbers || markers) {
 var gutterWrap = wrap.insertBefore(elt("div", null, "CodeMirror-gutter-wrapper", "position: absolute; left: " +
 (cm.options.fixedGutter ? dims.fixedPos : -dims.gutterTotalWidth) + "px"),
 lineElement);
 if (cm.options.fixedGutter) (wrap.alignable || (wrap.alignable = [])).push(gutterWrap);
 if (cm.options.lineNumbers && (!markers || !markers["CodeMirror-linenumbers"]))
 wrap.lineNumber = gutterWrap.appendChild(
 elt("div", lineNumberFor(cm.options, lineNo),
 "CodeMirror-linenumber CodeMirror-gutter-elt",
 "left: " + dims.gutterLeft["CodeMirror-linenumbers"] + "px; width: "
 + display.lineNumInnerWidth + "px"));
 if (markers)
 for (var k = 0; k < cm.options.gutters.length; ++k) {
 var id = cm.options.gutters[k], found = markers.hasOwnProperty(id) && markers[id];
 if (found)
 gutterWrap.appendChild(elt("div", [found], "CodeMirror-gutter-elt", "left: " +
 dims.gutterLeft[id] + "px; width: " + dims.gutterWidth[id] + "px"));
 }
 }
 if (ie_lt8) wrap.style.zIndex = 2;
 if (line.widgets && wrap != reuse) for (var i = 0, ws = line.widgets; i < ws.length; ++i) {
 var widget = ws[i], node = elt("div", [widget.node], "CodeMirror-linewidget");
 if (!widget.handleMouseEvents) node.ignoreEvents = true;
 positionLineWidget(widget, node, wrap, dims);
 if (widget.above)
 wrap.insertBefore(node, cm.options.lineNumbers && line.height != 0 ? gutterWrap : lineElement);
 else
 wrap.appendChild(node);
 signalLater(widget, "redraw");
 }
 return wrap;
 }

 function positionLineWidget(widget, node, wrap, dims) {
 if (widget.noHScroll) {
 (wrap.alignable || (wrap.alignable = [])).push(node);
 var width = dims.wrapperWidth;
 node.style.left = dims.fixedPos + "px";
 if (!widget.coverGutter) {
 width -= dims.gutterTotalWidth;
 node.style.paddingLeft = dims.gutterTotalWidth + "px";
 }
 node.style.width = width + "px";
 }
 if (widget.coverGutter) {
 node.style.zIndex = 5;
 node.style.position = "relative";
 if (!widget.noHScroll) node.style.marginLeft = -dims.gutterTotalWidth + "px";
 }
 }

 // SELECTION / CURSOR

 function updateSelection(cm) {
 var display = cm.display;
 var collapsed = posEq(cm.doc.sel.from, cm.doc.sel.to);
 if (collapsed || cm.options.showCursorWhenSelecting)
 updateSelectionCursor(cm);
 else
 display.cursor.style.display = display.otherCursor.style.display = "none";
 if (!collapsed)
 updateSelectionRange(cm);
 else
 display.selectionDiv.style.display = "none";

 // Move the hidden textarea near the cursor to prevent scrolling artifacts
 if (cm.options.moveInputWithCursor) {
 var headPos = cursorCoords(cm, cm.doc.sel.head, "div");
 var wrapOff = getRect(display.wrapper), lineOff = getRect(display.lineDiv);
 display.inputDiv.style.top = Math.max(0, Math.min(display.wrapper.clientHeight - 10,
 headPos.top + lineOff.top - wrapOff.top)) + "px";
 display.inputDiv.style.left = Math.max(0, Math.min(display.wrapper.clientWidth - 10,
 headPos.left + lineOff.left - wrapOff.left)) + "px";
 }
 }

 // No selection, plain cursor
 function updateSelectionCursor(cm) {
 var display = cm.display, pos = cursorCoords(cm, cm.doc.sel.head, "div");
 display.cursor.style.left = pos.left + "px";
 display.cursor.style.top = pos.top + "px";
 display.cursor.style.height = Math.max(0, pos.bottom - pos.top) * cm.options.cursorHeight + "px";
 display.cursor.style.display = "";

 if (pos.other) {
 display.otherCursor.style.display = "";
 display.otherCursor.style.left = pos.other.left + "px";
 display.otherCursor.style.top = pos.other.top + "px";
 display.otherCursor.style.height = (pos.other.bottom - pos.other.top) * .85 + "px";
 } else { display.otherCursor.style.display = "none"; }
 }

 // Highlight selection
 function updateSelectionRange(cm) {
 var display = cm.display, doc = cm.doc, sel = cm.doc.sel;
 var fragment = document.createDocumentFragment();
 var padding = paddingH(cm.display), leftSide = padding.left, rightSide = display.lineSpace.offsetWidth - padding.right;

 function add(left, top, width, bottom) {
 if (top < 0) top = 0;
 fragment.appendChild(elt("div", null, "CodeMirror-selected", "position: absolute; left: " + left +
 "px; top: " + top + "px; width: " + (width == null ? rightSide - left : width) +
 "px; height: " + (bottom - top) + "px"));
 }

 function drawForLine(line, fromArg, toArg) {
 var lineObj = getLine(doc, line);
 var lineLen = lineObj.text.length;
 var start, end;
 function coords(ch, bias) {
 return charCoords(cm, Pos(line, ch), "div", lineObj, bias);
 }

 iterateBidiSections(getOrder(lineObj), fromArg || 0, toArg == null ? lineLen : toArg, function(from, to, dir) {
 var leftPos = coords(from, "left"), rightPos, left, right;
 if (from == to) {
 rightPos = leftPos;
 left = right = leftPos.left;
 } else {
 rightPos = coords(to - 1, "right");
 if (dir == "rtl") { var tmp = leftPos; leftPos = rightPos; rightPos = tmp; }
 left = leftPos.left;
 right = rightPos.right;
 }
 if (fromArg == null && from == 0) left = leftSide;
 if (rightPos.top - leftPos.top > 3) { // Different lines, draw top part
 add(left, leftPos.top, null, leftPos.bottom);
 left = leftSide;
 if (leftPos.bottom < rightPos.top) add(left, leftPos.bottom, null, rightPos.top);
 }
 if (toArg == null && to == lineLen) right = rightSide;
 if (!start || leftPos.top < start.top || leftPos.top == start.top && leftPos.left < start.left)
 start = leftPos;
 if (!end || rightPos.bottom > end.bottom || rightPos.bottom == end.bottom && rightPos.right > end.right)
 end = rightPos;
 if (left < leftSide + 1) left = leftSide;
 add(left, rightPos.top, right - left, rightPos.bottom);
 });
 return {start: start, end: end};
 }

 if (sel.from.line == sel.to.line) {
 drawForLine(sel.from.line, sel.from.ch, sel.to.ch);
 } else {
 var fromLine = getLine(doc, sel.from.line), toLine = getLine(doc, sel.to.line);
 var singleVLine = visualLine(doc, fromLine) == visualLine(doc, toLine);
 var leftEnd = drawForLine(sel.from.line, sel.from.ch, singleVLine ? fromLine.text.length : null).end;
 var rightStart = drawForLine(sel.to.line, singleVLine ? 0 : null, sel.to.ch).start;
 if (singleVLine) {
 if (leftEnd.top < rightStart.top - 2) {
 add(leftEnd.right, leftEnd.top, null, leftEnd.bottom);
 add(leftSide, rightStart.top, rightStart.left, rightStart.bottom);
 } else {
 add(leftEnd.right, leftEnd.top, rightStart.left - leftEnd.right, leftEnd.bottom);
 }
 }
 if (leftEnd.bottom < rightStart.top)
 add(leftSide, leftEnd.bottom, null, rightStart.top);
 }

 removeChildrenAndAdd(display.selectionDiv, fragment);
 display.selectionDiv.style.display = "";
 }

 // Cursor-blinking
 function restartBlink(cm) {
 if (!cm.state.focused) return;
 var display = cm.display;
 clearInterval(display.blinker);
 var on = true;
 display.cursor.style.visibility = display.otherCursor.style.visibility = "";
 if (cm.options.cursorBlinkRate > 0)
 display.blinker = setInterval(function() {
 display.cursor.style.visibility = display.otherCursor.style.visibility = (on = !on) ? "" : "hidden";
 }, cm.options.cursorBlinkRate);
 }

 // HIGHLIGHT WORKER

 function startWorker(cm, time) {
 if (cm.doc.mode.startState && cm.doc.frontier < cm.display.showingTo)
 cm.state.highlight.set(time, bind(highlightWorker, cm));
 }

 function highlightWorker(cm) {
 var doc = cm.doc;
 if (doc.frontier < doc.first) doc.frontier = doc.first;
 if (doc.frontier >= cm.display.showingTo) return;
 var end = +new Date + cm.options.workTime;
 var state = copyState(doc.mode, getStateBefore(cm, doc.frontier));
 var changed = [], prevChange;
 doc.iter(doc.frontier, Math.min(doc.first + doc.size, cm.display.showingTo + 500), function(line) {
 if (doc.frontier >= cm.display.showingFrom) { // Visible
 var oldStyles = line.styles;
 line.styles = highlightLine(cm, line, state, true);
 var ischange = !oldStyles || oldStyles.length != line.styles.length;
 for (var i = 0; !ischange && i < oldStyles.length; ++i) ischange = oldStyles[i] != line.styles[i];
 if (ischange) {
 if (prevChange && prevChange.end == doc.frontier) prevChange.end++;
 else changed.push(prevChange = {start: doc.frontier, end: doc.frontier + 1});
 }
 line.stateAfter = copyState(doc.mode, state);
 } else {
 processLine(cm, line.text, state);
 line.stateAfter = doc.frontier % 5 == 0 ? copyState(doc.mode, state) : null;
 }
 ++doc.frontier;
 if (+new Date > end) {
 startWorker(cm, cm.options.workDelay);
 return true;
 }
 });
 if (changed.length)
 operation(cm, function() {
 for (var i = 0; i < changed.length; ++i)
 regChange(this, changed[i].start, changed[i].end);
 })();
 }

 // Finds the line to start with when starting a parse. Tries to
 // find a line with a stateAfter, so that it can start with a
 // valid state. If that fails, it returns the line with the
 // smallest indentation, which tends to need the least context to
 // parse correctly.
 function findStartLine(cm, n, precise) {
 var minindent, minline, doc = cm.doc;
 var lim = precise ? -1 : n - (cm.doc.mode.innerMode ? 1000 : 100);
 for (var search = n; search > lim; --search) {
 if (search <= doc.first) return doc.first;
 var line = getLine(doc, search - 1);
 if (line.stateAfter && (!precise || search <= doc.frontier)) return search;
 var indented = countColumn(line.text, null, cm.options.tabSize);
 if (minline == null || minindent > indented) {
 minline = search - 1;
 minindent = indented;
 }
 }
 return minline;
 }

 function getStateBefore(cm, n, precise) {
 var doc = cm.doc, display = cm.display;
 if (!doc.mode.startState) return true;
 var pos = findStartLine(cm, n, precise), state = pos > doc.first && getLine(doc, pos-1).stateAfter;
 if (!state) state = startState(doc.mode);
 else state = copyState(doc.mode, state);
 doc.iter(pos, n, function(line) {
 processLine(cm, line.text, state);
 var save = pos == n - 1 || pos % 5 == 0 || pos >= display.showingFrom && pos < display.showingTo;
 line.stateAfter = save ? copyState(doc.mode, state) : null;
 ++pos;
 });
 if (precise) doc.frontier = pos;
 return state;
 }

 // POSITION MEASUREMENT

 function paddingTop(display) {return display.lineSpace.offsetTop;}
 function paddingVert(display) {return display.mover.offsetHeight - display.lineSpace.offsetHeight;}
 function paddingH(display) {
 if (display.cachedPaddingH) return display.cachedPaddingH;
 var e = removeChildrenAndAdd(display.measure, elt("pre", "x"));
 var style = window.getComputedStyle ? window.getComputedStyle(e) : e.currentStyle;
 return display.cachedPaddingH = {left: parseInt(style.paddingLeft),
 right: parseInt(style.paddingRight)};
 }

 function measureChar(cm, line, ch, data, bias) {
 var dir = -1;
 data = data || measureLine(cm, line);
 if (data.crude) {
 var left = data.left + ch * data.width;
 return {left: left, right: left + data.width, top: data.top, bottom: data.bottom};
 }

 for (var pos = ch;; pos += dir) {
 var r = data[pos];
 if (r) break;
 if (dir < 0 && pos == 0) dir = 1;
 }
 bias = pos > ch ? "left" : pos < ch ? "right" : bias;
 if (bias == "left" && r.leftSide) r = r.leftSide;
 else if (bias == "right" && r.rightSide) r = r.rightSide;
 return {left: pos < ch ? r.right : r.left,
 right: pos > ch ? r.left : r.right,
 top: r.top,
 bottom: r.bottom};
 }

 function findCachedMeasurement(cm, line) {
 var cache = cm.display.measureLineCache;
 for (var i = 0; i < cache.length; ++i) {
 var memo = cache[i];
 if (memo.text == line.text && memo.markedSpans == line.markedSpans &&
 cm.display.scroller.clientWidth == memo.width &&
 memo.classes == line.textClass + "|" + line.wrapClass)
 return memo;
 }
 }

 function clearCachedMeasurement(cm, line) {
 var exists = findCachedMeasurement(cm, line);
 if (exists) exists.text = exists.measure = exists.markedSpans = null;
 }

 function measureLine(cm, line) {
 // First look in the cache
 var cached = findCachedMeasurement(cm, line);
 if (cached) return cached.measure;

 // Failing that, recompute and store result in cache
 var measure = measureLineInner(cm, line);
 var cache = cm.display.measureLineCache;
 var memo = {text: line.text, width: cm.display.scroller.clientWidth,
 markedSpans: line.markedSpans, measure: measure,
 classes: line.textClass + "|" + line.wrapClass};
 if (cache.length == 16) cache[++cm.display.measureLineCachePos % 16] = memo;
 else cache.push(memo);
 return measure;
 }

 function measureLineInner(cm, line) {
 if (!cm.options.lineWrapping && line.text.length >= cm.options.crudeMeasuringFrom)
 return crudelyMeasureLine(cm, line);

 var display = cm.display, measure = emptyArray(line.text.length);
 var pre = buildLineContent(cm, line, measure, true).pre;

 // IE does not cache element positions of inline elements between
 // calls to getBoundingClientRect. This makes the loop below,
 // which gathers the positions of all the characters on the line,
 // do an amount of layout work quadratic to the number of
 // characters. When line wrapping is off, we try to improve things
 // by first subdividing the line into a bunch of inline blocks, so
 // that IE can reuse most of the layout information from caches
 // for those blocks. This does interfere with line wrapping, so it
 // doesn't work when wrapping is on, but in that case the
 // situation is slightly better, since IE does cache line-wrapping
 // information and only recomputes per-line.
 if (old_ie && !ie_lt8 && !cm.options.lineWrapping && pre.childNodes.length > 100) {
 var fragment = document.createDocumentFragment();
 var chunk = 10, n = pre.childNodes.length;
 for (var i = 0, chunks = Math.ceil(n / chunk); i < chunks; ++i) {
 var wrap = elt("div", null, null, "display: inline-block");
 for (var j = 0; j < chunk && n; ++j) {
 wrap.appendChild(pre.firstChild);
 --n;
 }
 fragment.appendChild(wrap);
 }
 pre.appendChild(fragment);
 }

 removeChildrenAndAdd(display.measure, pre);

 var outer = getRect(display.lineDiv);
 var vranges = [], data = emptyArray(line.text.length), maxBot = pre.offsetHeight;
 // Work around an IE7/8 bug where it will sometimes have randomly
 // replaced our pre with a clone at this point.
 if (ie_lt9 && display.measure.first != pre)
 removeChildrenAndAdd(display.measure, pre);

 function measureRect(rect) {
 var top = rect.top - outer.top, bot = rect.bottom - outer.top;
 if (bot > maxBot) bot = maxBot;
 if (top < 0) top = 0;
 for (var i = vranges.length - 2; i >= 0; i -= 2) {
 var rtop = vranges[i], rbot = vranges[i+1];
 if (rtop > bot || rbot < top) continue;
 if (rtop <= top && rbot >= bot ||
 top <= rtop && bot >= rbot ||
 Math.min(bot, rbot) - Math.max(top, rtop) >= (bot - top) >> 1) {
 vranges[i] = Math.min(top, rtop);
 vranges[i+1] = Math.max(bot, rbot);
 break;
 }
 }
 if (i < 0) { i = vranges.length; vranges.push(top, bot); }
 return {left: rect.left - outer.left,
 right: rect.right - outer.left,
 top: i, bottom: null};
 }
 function finishRect(rect) {
 rect.bottom = vranges[rect.top+1];
 rect.top = vranges[rect.top];
 }

 for (var i = 0, cur; i < measure.length; ++i) if (cur = measure[i]) {
 var node = cur, rect = null;
 // A widget might wrap, needs special care
 if (/\bCodeMirror-widget\b/.test(cur.className) && cur.getClientRects) {
 if (cur.firstChild.nodeType == 1) node = cur.firstChild;
 var rects = node.getClientRects();
 if (rects.length > 1) {
 rect = data[i] = measureRect(rects[0]);
 rect.rightSide = measureRect(rects[rects.length - 1]);
 }
 }
 if (!rect) rect = data[i] = measureRect(getRect(node));
 if (cur.measureRight) rect.right = getRect(cur.measureRight).left - outer.left;
 if (cur.leftSide) rect.leftSide = measureRect(getRect(cur.leftSide));
 }
 removeChildren(cm.display.measure);
 for (var i = 0, cur; i < data.length; ++i) if (cur = data[i]) {
 finishRect(cur);
 if (cur.leftSide) finishRect(cur.leftSide);
 if (cur.rightSide) finishRect(cur.rightSide);
 }
 return data;
 }

 function crudelyMeasureLine(cm, line) {
 var copy = new Line(line.text.slice(0, 100), null);
 if (line.textClass) copy.textClass = line.textClass;
 var measure = measureLineInner(cm, copy);
 var left = measureChar(cm, copy, 0, measure, "left");
 var right = measureChar(cm, copy, 99, measure, "right");
 return {crude: true, top: left.top, left: left.left, bottom: left.bottom, width: (right.right - left.left) / 100};
 }

 function measureLineWidth(cm, line) {
 var hasBadSpan = false;
 if (line.markedSpans) for (var i = 0; i < line.markedSpans; ++i) {
 var sp = line.markedSpans[i];
 if (sp.collapsed && (sp.to == null || sp.to == line.text.length)) hasBadSpan = true;
 }
 var cached = !hasBadSpan && findCachedMeasurement(cm, line);
 if (cached || line.text.length >= cm.options.crudeMeasuringFrom)
 return measureChar(cm, line, line.text.length, cached && cached.measure, "right").right;

 var pre = buildLineContent(cm, line, null, true).pre;
 var end = pre.appendChild(zeroWidthElement(cm.display.measure));
 removeChildrenAndAdd(cm.display.measure, pre);
 return getRect(end).right - getRect(cm.display.lineDiv).left;
 }

 function clearCaches(cm) {
 cm.display.measureLineCache.length = cm.display.measureLineCachePos = 0;
 cm.display.cachedCharWidth = cm.display.cachedTextHeight = cm.display.cachedPaddingH = null;
 if (!cm.options.lineWrapping) cm.display.maxLineChanged = true;
 cm.display.lineNumChars = null;
 }

 function pageScrollX() { return window.pageXOffset || (document.documentElement || document.body).scrollLeft; }
 function pageScrollY() { return window.pageYOffset || (document.documentElement || document.body).scrollTop; }

 // Context is one of "line", "div" (display.lineDiv), "local"/null (editor), or "page"
 function intoCoordSystem(cm, lineObj, rect, context) {
 if (lineObj.widgets) for (var i = 0; i < lineObj.widgets.length; ++i) if (lineObj.widgets[i].above) {
 var size = widgetHeight(lineObj.widgets[i]);
 rect.top += size; rect.bottom += size;
 }
 if (context == "line") return rect;
 if (!context) context = "local";
 var yOff = heightAtLine(cm, lineObj);
 if (context == "local") yOff += paddingTop(cm.display);
 else yOff -= cm.display.viewOffset;
 if (context == "page" || context == "window") {
 var lOff = getRect(cm.display.lineSpace);
 yOff += lOff.top + (context == "window" ? 0 : pageScrollY());
 var xOff = lOff.left + (context == "window" ? 0 : pageScrollX());
 rect.left += xOff; rect.right += xOff;
 }
 rect.top += yOff; rect.bottom += yOff;
 return rect;
 }

 // Context may be "window", "page", "div", or "local"/null
 // Result is in "div" coords
 function fromCoordSystem(cm, coords, context) {
 if (context == "div") return coords;
 var left = coords.left, top = coords.top;
 // First move into "page" coordinate system
 if (context == "page") {
 left -= pageScrollX();
 top -= pageScrollY();
 } else if (context == "local" || !context) {
 var localBox = getRect(cm.display.sizer);
 left += localBox.left;
 top += localBox.top;
 }

 var lineSpaceBox = getRect(cm.display.lineSpace);
 return {left: left - lineSpaceBox.left, top: top - lineSpaceBox.top};
 }

 function charCoords(cm, pos, context, lineObj, bias) {
 if (!lineObj) lineObj = getLine(cm.doc, pos.line);
 return intoCoordSystem(cm, lineObj, measureChar(cm, lineObj, pos.ch, null, bias), context);
 }

 function cursorCoords(cm, pos, context, lineObj, measurement) {
 lineObj = lineObj || getLine(cm.doc, pos.line);
 if (!measurement) measurement = measureLine(cm, lineObj);
 function get(ch, right) {
 var m = measureChar(cm, lineObj, ch, measurement, right ? "right" : "left");
 if (right) m.left = m.right; else m.right = m.left;
 return intoCoordSystem(cm, lineObj, m, context);
 }
 function getBidi(ch, partPos) {
 var part = order[partPos], right = part.level % 2;
 if (ch == bidiLeft(part) && partPos && part.level < order[partPos - 1].level) {
 part = order[--partPos];
 ch = bidiRight(part) - (part.level % 2 ? 0 : 1);
 right = true;
 } else if (ch == bidiRight(part) && partPos < order.length - 1 && part.level < order[partPos + 1].level) {
 part = order[++partPos];
 ch = bidiLeft(part) - part.level % 2;
 right = false;
 }
 if (right && ch == part.to && ch > part.from) return get(ch - 1);
 return get(ch, right);
 }
 var order = getOrder(lineObj), ch = pos.ch;
 if (!order) return get(ch);
 var partPos = getBidiPartAt(order, ch);
 var val = getBidi(ch, partPos);
 if (bidiOther != null) val.other = getBidi(ch, bidiOther);
 return val;
 }

 function PosWithInfo(line, ch, outside, xRel) {
 var pos = new Pos(line, ch);
 pos.xRel = xRel;
 if (outside) pos.outside = true;
 return pos;
 }

 // Coords must be lineSpace-local
 function coordsChar(cm, x, y) {
 var doc = cm.doc;
 y += cm.display.viewOffset;
 if (y < 0) return PosWithInfo(doc.first, 0, true, -1);
 var lineNo = lineAtHeight(doc, y), last = doc.first + doc.size - 1;
 if (lineNo > last)
 return PosWithInfo(doc.first + doc.size - 1, getLine(doc, last).text.length, true, 1);
 if (x < 0) x = 0;

 for (;;) {
 var lineObj = getLine(doc, lineNo);
 var found = coordsCharInner(cm, lineObj, lineNo, x, y);
 var merged = collapsedSpanAtEnd(lineObj);
 var mergedPos = merged && merged.find();
 if (merged && (found.ch > mergedPos.from.ch || found.ch == mergedPos.from.ch && found.xRel > 0))
 lineNo = mergedPos.to.line;
 else
 return found;
 }
 }

 function coordsCharInner(cm, lineObj, lineNo, x, y) {
 var innerOff = y - heightAtLine(cm, lineObj);
 var wrongLine = false, adjust = 2 * cm.display.wrapper.clientWidth;
 var measurement = measureLine(cm, lineObj);

 function getX(ch) {
 var sp = cursorCoords(cm, Pos(lineNo, ch), "line",
 lineObj, measurement);
 wrongLine = true;
 if (innerOff > sp.bottom) return sp.left - adjust;
 else if (innerOff < sp.top) return sp.left + adjust;
 else wrongLine = false;
 return sp.left;
 }

 var bidi = getOrder(lineObj), dist = lineObj.text.length;
 var from = lineLeft(lineObj), to = lineRight(lineObj);
 var fromX = getX(from), fromOutside = wrongLine, toX = getX(to), toOutside = wrongLine;

 if (x > toX) return PosWithInfo(lineNo, to, toOutside, 1);
 // Do a binary search between these bounds.
 for (;;) {
 if (bidi ? to == from || to == moveVisually(lineObj, from, 1) : to - from <= 1) {
 var ch = x < fromX || x - fromX <= toX - x ? from : to;
 var xDiff = x - (ch == from ? fromX : toX);
 while (isExtendingChar(lineObj.text.charAt(ch))) ++ch;
 var pos = PosWithInfo(lineNo, ch, ch == from ? fromOutside : toOutside,
 xDiff < 0 ? -1 : xDiff ? 1 : 0);
 return pos;
 }
 var step = Math.ceil(dist / 2), middle = from + step;
 if (bidi) {
 middle = from;
 for (var i = 0; i < step; ++i) middle = moveVisually(lineObj, middle, 1);
 }
 var middleX = getX(middle);
 if (middleX > x) {to = middle; toX = middleX; if (toOutside = wrongLine) toX += 1000; dist = step;}
 else {from = middle; fromX = middleX; fromOutside = wrongLine; dist -= step;}
 }
 }

 var measureText;
 function textHeight(display) {
 if (display.cachedTextHeight != null) return display.cachedTextHeight;
 if (measureText == null) {
 measureText = elt("pre");
 // Measure a bunch of lines, for browsers that compute
 // fractional heights.
 for (var i = 0; i < 49; ++i) {
 measureText.appendChild(document.createTextNode("x"));
 measureText.appendChild(elt("br"));
 }
 measureText.appendChild(document.createTextNode("x"));
 }
 removeChildrenAndAdd(display.measure, measureText);
 var height = measureText.offsetHeight / 50;
 if (height > 3) display.cachedTextHeight = height;
 removeChildren(display.measure);
 return height || 1;
 }

 function charWidth(display) {
 if (display.cachedCharWidth != null) return display.cachedCharWidth;
 var anchor = elt("span", "x");
 var pre = elt("pre", [anchor]);
 removeChildrenAndAdd(display.measure, pre);
 var width = anchor.offsetWidth;
 if (width > 2) display.cachedCharWidth = width;
 return width || 10;
 }

 // OPERATIONS

 // Operations are used to wrap changes in such a way that each
 // change won't have to update the cursor and display (which would
 // be awkward, slow, and error-prone), but instead updates are
 // batched and then all combined and executed at once.

 var nextOpId = 0;
 function startOperation(cm) {
 cm.curOp = {
 // An array of ranges of lines that have to be updated. See
 // updateDisplay.
 changes: [],
 forceUpdate: false,
 updateInput: null,
 userSelChange: null,
 textChanged: null,
 selectionChanged: false,
 cursorActivity: false,
 updateMaxLine: false,
 updateScrollPos: false,
 id: ++nextOpId
 };
 if (!delayedCallbackDepth++) delayedCallbacks = [];
 }

 function endOperation(cm) {
 var op = cm.curOp, doc = cm.doc, display = cm.display;
 cm.curOp = null;

 if (op.updateMaxLine) computeMaxLength(cm);
 if (display.maxLineChanged && !cm.options.lineWrapping && display.maxLine) {
 var width = measureLineWidth(cm, display.maxLine);
 display.sizer.style.minWidth = Math.max(0, width + 3) + "px";
 display.maxLineChanged = false;
 var maxScrollLeft = Math.max(0, display.sizer.offsetLeft + display.sizer.offsetWidth - display.scroller.clientWidth);
 if (maxScrollLeft < doc.scrollLeft && !op.updateScrollPos)
 setScrollLeft(cm, Math.min(display.scroller.scrollLeft, maxScrollLeft), true);
 }
 var newScrollPos, updated;
 if (op.updateScrollPos) {
 newScrollPos = op.updateScrollPos;
 } else if (op.selectionChanged && display.scroller.clientHeight) { // don't rescroll if not visible
 var coords = cursorCoords(cm, doc.sel.head);
 newScrollPos = calculateScrollPos(cm, coords.left, coords.top, coords.left, coords.bottom);
 }
 if (op.changes.length || op.forceUpdate || newScrollPos && newScrollPos.scrollTop != null) {
 updated = updateDisplay(cm, op.changes, newScrollPos && newScrollPos.scrollTop, op.forceUpdate);
 if (cm.display.scroller.offsetHeight) cm.doc.scrollTop = cm.display.scroller.scrollTop;
 }
 if (!updated && op.selectionChanged) updateSelection(cm);
 if (op.updateScrollPos) {
 var top = Math.max(0, Math.min(display.scroller.scrollHeight - display.scroller.clientHeight, newScrollPos.scrollTop));
 var left = Math.max(0, Math.min(display.scroller.scrollWidth - display.scroller.clientWidth, newScrollPos.scrollLeft));
 display.scroller.scrollTop = display.scrollbarV.scrollTop = doc.scrollTop = top;
 display.scroller.scrollLeft = display.scrollbarH.scrollLeft = doc.scrollLeft = left;
 alignHorizontally(cm);
 if (op.scrollToPos)
 scrollPosIntoView(cm, clipPos(cm.doc, op.scrollToPos.from),
 clipPos(cm.doc, op.scrollToPos.to), op.scrollToPos.margin);
 } else if (newScrollPos) {
 scrollCursorIntoView(cm);
 }
 if (op.selectionChanged) restartBlink(cm);

 if (cm.state.focused && op.updateInput)
 resetInput(cm, op.userSelChange);

 var hidden = op.maybeHiddenMarkers, unhidden = op.maybeUnhiddenMarkers;
 if (hidden) for (var i = 0; i < hidden.length; ++i)
 if (!hidden[i].lines.length) signal(hidden[i], "hide");
 if (unhidden) for (var i = 0; i < unhidden.length; ++i)
 if (unhidden[i].lines.length) signal(unhidden[i], "unhide");

 var delayed;
 if (!--delayedCallbackDepth) {
 delayed = delayedCallbacks;
 delayedCallbacks = null;
 }
 if (op.textChanged)
 signal(cm, "change", cm, op.textChanged);
 if (op.cursorActivity) signal(cm, "cursorActivity", cm);
 if (delayed) for (var i = 0; i < delayed.length; ++i) delayed[i]();
 }

 // Wraps a function in an operation. Returns the wrapped function.
 function operation(cm1, f) {
 return function() {
 var cm = cm1 || this, withOp = !cm.curOp;
 if (withOp) startOperation(cm);
 try { var result = f.apply(cm, arguments); }
 finally { if (withOp) endOperation(cm); }
 return result;
 };
 }
 function docOperation(f) {
 return function() {
 var withOp = this.cm && !this.cm.curOp, result;
 if (withOp) startOperation(this.cm);
 try { result = f.apply(this, arguments); }
 finally { if (withOp) endOperation(this.cm); }
 return result;
 };
 }
 function runInOp(cm, f) {
 var withOp = !cm.curOp, result;
 if (withOp) startOperation(cm);
 try { result = f(); }
 finally { if (withOp) endOperation(cm); }
 return result;
 }

 function regChange(cm, from, to, lendiff) {
 if (from == null) from = cm.doc.first;
 if (to == null) to = cm.doc.first + cm.doc.size;
 cm.curOp.changes.push({from: from, to: to, diff: lendiff});
 }

 // INPUT HANDLING

 function slowPoll(cm) {
 if (cm.display.pollingFast) return;
 cm.display.poll.set(cm.options.pollInterval, function() {
 readInput(cm);
 if (cm.state.focused) slowPoll(cm);
 });
 }

 function fastPoll(cm) {
 var missed = false;
 cm.display.pollingFast = true;
 function p() {
 var changed = readInput(cm);
 if (!changed && !missed) {missed = true; cm.display.poll.set(60, p);}
 else {cm.display.pollingFast = false; slowPoll(cm);}
 }
 cm.display.poll.set(20, p);
 }

 // prevInput is a hack to work with IME. If we reset the textarea
 // on every change, that breaks IME. So we look for changes
 // compared to the previous content instead. (Modern browsers have
 // events that indicate IME taking place, but these are not widely
 // supported or compatible enough yet to rely on.)
 function readInput(cm) {
 var input = cm.display.input, prevInput = cm.display.prevInput, doc = cm.doc, sel = doc.sel;
 if (!cm.state.focused || hasSelection(input) || isReadOnly(cm) || cm.options.disableInput) return false;
 if (cm.state.pasteIncoming && cm.state.fakedLastChar) {
 input.value = input.value.substring(0, input.value.length - 1);
 cm.state.fakedLastChar = false;
 }
 var text = input.value;
 if (text == prevInput && posEq(sel.from, sel.to)) return false;
 if (ie && !ie_lt9 && cm.display.inputHasSelection === text) {
 resetInput(cm, true);
 return false;
 }

 var withOp = !cm.curOp;
 if (withOp) startOperation(cm);
 sel.shift = false;
 var same = 0, l = Math.min(prevInput.length, text.length);
 while (same < l && prevInput.charCodeAt(same) == text.charCodeAt(same)) ++same;
 var from = sel.from, to = sel.to;
 var inserted = text.slice(same);
 if (same < prevInput.length)
 from = Pos(from.line, from.ch - (prevInput.length - same));
 else if (cm.state.overwrite && posEq(from, to) && !cm.state.pasteIncoming)
 to = Pos(to.line, Math.min(getLine(doc, to.line).text.length, to.ch + inserted.length));

 var updateInput = cm.curOp.updateInput;
 var changeEvent = {from: from, to: to, text: splitLines(inserted),
 origin: cm.state.pasteIncoming ? "paste" : cm.state.cutIncoming ? "cut" : "+input"};
 makeChange(cm.doc, changeEvent, "end");
 cm.curOp.updateInput = updateInput;
 signalLater(cm, "inputRead", cm, changeEvent);
 if (inserted && !cm.state.pasteIncoming && cm.options.electricChars &&
 cm.options.smartIndent && sel.head.ch < 100) {
 var electric = cm.getModeAt(sel.head).electricChars;
 if (electric) for (var i = 0; i < electric.length; i++)
 if (inserted.indexOf(electric.charAt(i)) > -1) {
 indentLine(cm, sel.head.line, "smart");
 break;
 }
 }

 if (text.length > 1000 || text.indexOf("\n") > -1) input.value = cm.display.prevInput = "";
 else cm.display.prevInput = text;
 if (withOp) endOperation(cm);
 cm.state.pasteIncoming = cm.state.cutIncoming = false;
 return true;
 }

 function resetInput(cm, user) {
 var minimal, selected, doc = cm.doc;
 if (!posEq(doc.sel.from, doc.sel.to)) {
 cm.display.prevInput = "";
 minimal = hasCopyEvent &&
 (doc.sel.to.line - doc.sel.from.line > 100 || (selected = cm.getSelection()).length > 1000);
 var content = minimal ? "-" : selected || cm.getSelection();
 cm.display.input.value = content;
 if (cm.state.focused) selectInput(cm.display.input);
 if (ie && !ie_lt9) cm.display.inputHasSelection = content;
 } else if (user) {
 cm.display.prevInput = cm.display.input.value = "";
 if (ie && !ie_lt9) cm.display.inputHasSelection = null;
 }
 cm.display.inaccurateSelection = minimal;
 }

 function focusInput(cm) {
 if (cm.options.readOnly != "nocursor" && (!mobile || document.activeElement != cm.display.input))
 cm.display.input.focus();
 }

 function ensureFocus(cm) {
 if (!cm.state.focused) { focusInput(cm); onFocus(cm); }
 }

 function isReadOnly(cm) {
 return cm.options.readOnly || cm.doc.cantEdit;
 }

 // EVENT HANDLERS

 function registerEventHandlers(cm) {
 var d = cm.display;
 on(d.scroller, "mousedown", operation(cm, onMouseDown));
 if (old_ie)
 on(d.scroller, "dblclick", operation(cm, function(e) {
 if (signalDOMEvent(cm, e)) return;
 var pos = posFromMouse(cm, e);
 if (!pos || clickInGutter(cm, e) || eventInWidget(cm.display, e)) return;
 e_preventDefault(e);
 var word = findWordAt(getLine(cm.doc, pos.line).text, pos);
 extendSelection(cm.doc, word.from, word.to);
 }));
 else
 on(d.scroller, "dblclick", function(e) { signalDOMEvent(cm, e) || e_preventDefault(e); });
 on(d.lineSpace, "selectstart", function(e) {
 if (!eventInWidget(d, e)) e_preventDefault(e);
 });
 // Gecko browsers fire contextmenu *after* opening the menu, at
 // which point we can't mess with it anymore. Context menu is
 // handled in onMouseDown for Gecko.
 if (!captureMiddleClick) on(d.scroller, "contextmenu", function(e) {onContextMenu(cm, e);});

 on(d.scroller, "scroll", function() {
 if (d.scroller.clientHeight) {
 setScrollTop(cm, d.scroller.scrollTop);
 setScrollLeft(cm, d.scroller.scrollLeft, true);
 signal(cm, "scroll", cm);
 }
 });
 on(d.scrollbarV, "scroll", function() {
 if (d.scroller.clientHeight) setScrollTop(cm, d.scrollbarV.scrollTop);
 });
 on(d.scrollbarH, "scroll", function() {
 if (d.scroller.clientHeight) setScrollLeft(cm, d.scrollbarH.scrollLeft);
 });

 on(d.scroller, "mousewheel", function(e){onScrollWheel(cm, e);});
 on(d.scroller, "DOMMouseScroll", function(e){onScrollWheel(cm, e);});

 function reFocus() { if (cm.state.focused) setTimeout(bind(focusInput, cm), 0); }
 on(d.scrollbarH, "mousedown", reFocus);
 on(d.scrollbarV, "mousedown", reFocus);
 // Prevent wrapper from ever scrolling
 on(d.wrapper, "scroll", function() { d.wrapper.scrollTop = d.wrapper.scrollLeft = 0; });

 var resizeTimer;
 function onResize() {
 if (resizeTimer == null) resizeTimer = setTimeout(function() {
 resizeTimer = null;
 // Might be a text scaling operation, clear size caches.
 d.cachedCharWidth = d.cachedTextHeight = d.cachedPaddingH = knownScrollbarWidth = null;
 clearCaches(cm);
 runInOp(cm, bind(regChange, cm));
 }, 100);
 }
 on(window, "resize", onResize);
 // Above handler holds on to the editor and its data structures.
 // Here we poll to unregister it when the editor is no longer in
 // the document, so that it can be garbage-collected.
 function unregister() {
 for (var p = d.wrapper.parentNode; p && p != document.body; p = p.parentNode) {}
 if (p) setTimeout(unregister, 5000);
 else off(window, "resize", onResize);
 }
 setTimeout(unregister, 5000);

 on(d.input, "keyup", operation(cm, onKeyUp));
 on(d.input, "input", function() {
 if (ie && !ie_lt9 && cm.display.inputHasSelection) cm.display.inputHasSelection = null;
 fastPoll(cm);
 });
 on(d.input, "keydown", operation(cm, onKeyDown));
 on(d.input, "keypress", operation(cm, onKeyPress));
 on(d.input, "focus", bind(onFocus, cm));
 on(d.input, "blur", bind(onBlur, cm));

 function drag_(e) {
 if (signalDOMEvent(cm, e) || cm.options.onDragEvent && cm.options.onDragEvent(cm, addStop(e))) return;
 e_stop(e);
 }
 if (cm.options.dragDrop) {
 on(d.scroller, "dragstart", function(e){onDragStart(cm, e);});
 on(d.scroller, "dragenter", drag_);
 on(d.scroller, "dragover", drag_);
 on(d.scroller, "drop", operation(cm, onDrop));
 }
 on(d.scroller, "paste", function(e) {
 if (eventInWidget(d, e)) return;
 focusInput(cm);
 fastPoll(cm);
 });
 on(d.input, "paste", function() {
 // Workaround for webkit bug https://bugs.webkit.org/show_bug.cgi?id=90206
 // Add a char to the end of textarea before paste occur so that
 // selection doesn't span to the end of textarea.
 if (webkit && !cm.state.fakedLastChar && !(new Date - cm.state.lastMiddleDown < 200)) {
 var start = d.input.selectionStart, end = d.input.selectionEnd;
 d.input.value += "$";
 d.input.selectionStart = start;
 d.input.selectionEnd = end;
 cm.state.fakedLastChar = true;
 }
 cm.state.pasteIncoming = true;
 fastPoll(cm);
 });

 function prepareCopy(e) {
 if (d.inaccurateSelection) {
 d.prevInput = "";
 d.inaccurateSelection = false;
 d.input.value = cm.getSelection();
 selectInput(d.input);
 }
 if (e.type == "cut") cm.state.cutIncoming = true;
 }
 on(d.input, "cut", prepareCopy);
 on(d.input, "copy", prepareCopy);

 // Needed to handle Tab key in KHTML
 if (khtml) on(d.sizer, "mouseup", function() {
 if (document.activeElement == d.input) d.input.blur();
 focusInput(cm);
 });
 }

 function eventInWidget(display, e) {
 for (var n = e_target(e); n != display.wrapper; n = n.parentNode) {
 if (!n || n.ignoreEvents || n.parentNode == display.sizer && n != display.mover) return true;
 }
 }

 function posFromMouse(cm, e, liberal) {
 var display = cm.display;
 if (!liberal) {
 var target = e_target(e);
 if (target == display.scrollbarH || target == display.scrollbarH.firstChild ||
 target == display.scrollbarV || target == display.scrollbarV.firstChild ||
 target == display.scrollbarFiller || target == display.gutterFiller) return null;
 }
 var x, y, space = getRect(display.lineSpace);
 // Fails unpredictably on IE[67] when mouse is dragged around quickly.
 try { x = e.clientX; y = e.clientY; } catch (e) { return null; }
 return coordsChar(cm, x - space.left, y - space.top);
 }

 var lastClick, lastDoubleClick;
 function onMouseDown(e) {
 if (signalDOMEvent(this, e)) return;
 var cm = this, display = cm.display, doc = cm.doc, sel = doc.sel;
 sel.shift = e.shiftKey;

 if (eventInWidget(display, e)) {
 if (!webkit) {
 display.scroller.draggable = false;
 setTimeout(function(){display.scroller.draggable = true;}, 100);
 }
 return;
 }
 if (clickInGutter(cm, e)) return;
 var start = posFromMouse(cm, e);
 window.focus();

 switch (e_button(e)) {
 case 3:
 if (captureMiddleClick) onContextMenu.call(cm, cm, e);
 return;
 case 2:
 if (webkit) cm.state.lastMiddleDown = +new Date;
 if (start) extendSelection(cm.doc, start);
 setTimeout(bind(focusInput, cm), 20);
 e_preventDefault(e);
 return;
 }
 // For button 1, if it was clicked inside the editor
 // (posFromMouse returning non-null), we have to adjust the
 // selection.
 if (!start) {if (e_target(e) == display.scroller) e_preventDefault(e); return;}

 setTimeout(bind(ensureFocus, cm), 0);

 var now = +new Date, type = "single";
 if (lastDoubleClick && lastDoubleClick.time > now - 400 && posEq(lastDoubleClick.pos, start)) {
 type = "triple";
 e_preventDefault(e);
 setTimeout(bind(focusInput, cm), 20);
 selectLine(cm, start.line);
 } else if (lastClick && lastClick.time > now - 400 && posEq(lastClick.pos, start)) {
 type = "double";
 lastDoubleClick = {time: now, pos: start};
 e_preventDefault(e);
 var word = findWordAt(getLine(doc, start.line).text, start);
 extendSelection(cm.doc, word.from, word.to);
 } else { lastClick = {time: now, pos: start}; }

 var last = start;
 if (cm.options.dragDrop && dragAndDrop && !isReadOnly(cm) && !posEq(sel.from, sel.to) &&
 !posLess(start, sel.from) && !posLess(sel.to, start) && type == "single") {
 var dragEnd = operation(cm, function(e2) {
 if (webkit) display.scroller.draggable = false;
 cm.state.draggingText = false;
 off(document, "mouseup", dragEnd);
 off(display.scroller, "drop", dragEnd);
 if (Math.abs(e.clientX - e2.clientX) + Math.abs(e.clientY - e2.clientY) < 10) {
 e_preventDefault(e2);
 extendSelection(cm.doc, start);
 focusInput(cm);
 // Work around unexplainable focus problem in IE9 (#2127)
 if (old_ie && !ie_lt9)
 setTimeout(function() {document.body.focus(); focusInput(cm);}, 20);
 }
 });
 // Let the drag handler handle this.
 if (webkit) display.scroller.draggable = true;
 cm.state.draggingText = dragEnd;
 // IE's approach to draggable
 if (display.scroller.dragDrop) display.scroller.dragDrop();
 on(document, "mouseup", dragEnd);
 on(display.scroller, "drop", dragEnd);
 return;
 }
 e_preventDefault(e);
 if (type == "single") extendSelection(cm.doc, clipPos(doc, start));

 var startstart = sel.from, startend = sel.to, lastPos = start;

 function doSelect(cur) {
 if (posEq(lastPos, cur)) return;
 lastPos = cur;

 if (type == "single") {
 extendSelection(cm.doc, clipPos(doc, start), cur);
 return;
 }

 startstart = clipPos(doc, startstart);
 startend = clipPos(doc, startend);
 if (type == "double") {
 var word = findWordAt(getLine(doc, cur.line).text, cur);
 if (posLess(cur, startstart)) extendSelection(cm.doc, word.from, startend);
 else extendSelection(cm.doc, startstart, word.to);
 } else if (type == "triple") {
 if (posLess(cur, startstart)) extendSelection(cm.doc, startend, clipPos(doc, Pos(cur.line, 0)));
 else extendSelection(cm.doc, startstart, clipPos(doc, Pos(cur.line + 1, 0)));
 }
 }

 var editorSize = getRect(display.wrapper);
 // Used to ensure timeout re-tries don't fire when another extend
 // happened in the meantime (clearTimeout isn't reliable -- at
 // least on Chrome, the timeouts still happen even when cleared,
 // if the clear happens after their scheduled firing time).
 var counter = 0;

 function extend(e) {
 var curCount = ++counter;
 var cur = posFromMouse(cm, e, true);
 if (!cur) return;
 if (!posEq(cur, last)) {
 ensureFocus(cm);
 last = cur;
 doSelect(cur);
 var visible = visibleLines(display, doc);
 if (cur.line >= visible.to || cur.line < visible.from)
 setTimeout(operation(cm, function(){if (counter == curCount) extend(e);}), 150);
 } else {
 var outside = e.clientY < editorSize.top ? -20 : e.clientY > editorSize.bottom ? 20 : 0;
 if (outside) setTimeout(operation(cm, function() {
 if (counter != curCount) return;
 display.scroller.scrollTop += outside;
 extend(e);
 }), 50);
 }
 }

 function done(e) {
 counter = Infinity;
 e_preventDefault(e);
 focusInput(cm);
 off(document, "mousemove", move);
 off(document, "mouseup", up);
 }

 var move = operation(cm, function(e) {
 if ((ie && !ie_lt10) ? !e.buttons : !e_button(e)) done(e);
 else extend(e);
 });
 var up = operation(cm, done);
 on(document, "mousemove", move);
 on(document, "mouseup", up);
 }

 function gutterEvent(cm, e, type, prevent, signalfn) {
 try { var mX = e.clientX, mY = e.clientY; }
 catch(e) { return false; }
 if (mX >= Math.floor(getRect(cm.display.gutters).right)) return false;
 if (prevent) e_preventDefault(e);

 var display = cm.display;
 var lineBox = getRect(display.lineDiv);

 if (mY > lineBox.bottom || !hasHandler(cm, type)) return e_defaultPrevented(e);
 mY -= lineBox.top - display.viewOffset;

 for (var i = 0; i < cm.options.gutters.length; ++i) {
 var g = display.gutters.childNodes[i];
 if (g && getRect(g).right >= mX) {
 var line = lineAtHeight(cm.doc, mY);
 var gutter = cm.options.gutters[i];
 signalfn(cm, type, cm, line, gutter, e);
 return e_defaultPrevented(e);
 }
 }
 }

 function contextMenuInGutter(cm, e) {
 if (!hasHandler(cm, "gutterContextMenu")) return false;
 return gutterEvent(cm, e, "gutterContextMenu", false, signal);
 }

 function clickInGutter(cm, e) {
 return gutterEvent(cm, e, "gutterClick", true, signalLater);
 }

 // Kludge to work around strange IE behavior where it'll sometimes
 // re-fire a series of drag-related events right after the drop (#1551)
 var lastDrop = 0;

 function onDrop(e) {
 var cm = this;
 if (signalDOMEvent(cm, e) || eventInWidget(cm.display, e) || (cm.options.onDragEvent && cm.options.onDragEvent(cm, addStop(e))))
 return;
 e_preventDefault(e);
 if (ie) lastDrop = +new Date;
 var pos = posFromMouse(cm, e, true), files = e.dataTransfer.files;
 if (!pos || isReadOnly(cm)) return;
 if (files && files.length && window.FileReader && window.File) {
 var n = files.length, text = Array(n), read = 0;
 var loadFile = function(file, i) {
 var reader = new FileReader;
 reader.onload = function() {
 text[i] = reader.result;
 if (++read == n) {
 pos = clipPos(cm.doc, pos);
 makeChange(cm.doc, {from: pos, to: pos, text: splitLines(text.join("\n")), origin: "paste"}, "around");
 }
 };
 reader.readAsText(file);
 };
 for (var i = 0; i < n; ++i) loadFile(files[i], i);
 } else {
 // Don't do a replace if the drop happened inside of the selected text.
 if (cm.state.draggingText && !(posLess(pos, cm.doc.sel.from) || posLess(cm.doc.sel.to, pos))) {
 cm.state.draggingText(e);
 // Ensure the editor is re-focused
 setTimeout(bind(focusInput, cm), 20);
 return;
 }
 try {
 var text = e.dataTransfer.getData("Text");
 if (text) {
 var curFrom = cm.doc.sel.from, curTo = cm.doc.sel.to;
 setSelection(cm.doc, pos, pos);
 if (cm.state.draggingText) replaceRange(cm.doc, "", curFrom, curTo, "paste");
 cm.replaceSelection(text, null, "paste");
 focusInput(cm);
 }
 }
 catch(e){}
 }
 }

 function onDragStart(cm, e) {
 if (ie && (!cm.state.draggingText || +new Date - lastDrop < 100)) { e_stop(e); return; }
 if (signalDOMEvent(cm, e) || eventInWidget(cm.display, e)) return;

 var txt = cm.getSelection();
 e.dataTransfer.setData("Text", txt);

 // Use dummy image instead of default browsers image.
 // Recent Safari (~6.0.2) have a tendency to segfault when this happens, so we don't do it there.
 if (e.dataTransfer.setDragImage && !safari) {
 var img = elt("img", null, null, "position: fixed; left: 0; top: 0;");
 img.src = "data:image/gif;base64,R0lGODlhAQABAAAAACH5BAEKAAEALAAAAAABAAEAAAICTAEAOw==";
 if (opera) {
 img.width = img.height = 1;
 cm.display.wrapper.appendChild(img);
 // Force a relayout, or Opera won't use our image for some obscure reason
 img._top = img.offsetTop;
 }
 e.dataTransfer.setDragImage(img, 0, 0);
 if (opera) img.parentNode.removeChild(img);
 }
 }

 function setScrollTop(cm, val) {
 if (Math.abs(cm.doc.scrollTop - val) < 2) return;
 cm.doc.scrollTop = val;
 if (!gecko) updateDisplay(cm, [], val);
 if (cm.display.scroller.scrollTop != val) cm.display.scroller.scrollTop = val;
 if (cm.display.scrollbarV.scrollTop != val) cm.display.scrollbarV.scrollTop = val;
 if (gecko) updateDisplay(cm, []);
 startWorker(cm, 100);
 }
 function setScrollLeft(cm, val, isScroller) {
 if (isScroller ? val == cm.doc.scrollLeft : Math.abs(cm.doc.scrollLeft - val) < 2) return;
 val = Math.min(val, cm.display.scroller.scrollWidth - cm.display.scroller.clientWidth);
 cm.doc.scrollLeft = val;
 alignHorizontally(cm);
 if (cm.display.scroller.scrollLeft != val) cm.display.scroller.scrollLeft = val;
 if (cm.display.scrollbarH.scrollLeft != val) cm.display.scrollbarH.scrollLeft = val;
 }

 // Since the delta values reported on mouse wheel events are
 // unstandardized between browsers and even browser versions, and
 // generally horribly unpredictable, this code starts by measuring
 // the scroll effect that the first few mouse wheel events have,
 // and, from that, detects the way it can convert deltas to pixel
 // offsets afterwards.
 //
 // The reason we want to know the amount a wheel event will scroll
 // is that it gives us a chance to update the display before the
 // actual scrolling happens, reducing flickering.

 var wheelSamples = 0, wheelPixelsPerUnit = null;
 // Fill in a browser-detected starting value on browsers where we
 // know one. These don't have to be accurate -- the result of them
 // being wrong would just be a slight flicker on the first wheel
 // scroll (if it is large enough).
 if (ie) wheelPixelsPerUnit = -.53;
 else if (gecko) wheelPixelsPerUnit = 15;
 else if (chrome) wheelPixelsPerUnit = -.7;
 else if (safari) wheelPixelsPerUnit = -1/3;

 function onScrollWheel(cm, e) {
 var dx = e.wheelDeltaX, dy = e.wheelDeltaY;
 if (dx == null && e.detail && e.axis == e.HORIZONTAL_AXIS) dx = e.detail;
 if (dy == null && e.detail && e.axis == e.VERTICAL_AXIS) dy = e.detail;
 else if (dy == null) dy = e.wheelDelta;

 var display = cm.display, scroll = display.scroller;
 // Quit if there's nothing to scroll here
 if (!(dx && scroll.scrollWidth > scroll.clientWidth ||
 dy && scroll.scrollHeight > scroll.clientHeight)) return;

 // Webkit browsers on OS X abort momentum scrolls when the target
 // of the scroll event is removed from the scrollable element.
 // This hack (see related code in patchDisplay) makes sure the
 // element is kept around.
 if (dy && mac && webkit) {
 for (var cur = e.target; cur != scroll; cur = cur.parentNode) {
 if (cur.lineObj) {
 cm.display.currentWheelTarget = cur;
 break;
 }
 }
 }

 // On some browsers, horizontal scrolling will cause redraws to
 // happen before the gutter has been realigned, causing it to
 // wriggle around in a most unseemly way. When we have an
 // estimated pixels/delta value, we just handle horizontal
 // scrolling entirely here. It'll be slightly off from native, but
 // better than glitching out.
 if (dx && !gecko && !opera && wheelPixelsPerUnit != null) {
 if (dy)
 setScrollTop(cm, Math.max(0, Math.min(scroll.scrollTop + dy * wheelPixelsPerUnit, scroll.scrollHeight - scroll.clientHeight)));
 setScrollLeft(cm, Math.max(0, Math.min(scroll.scrollLeft + dx * wheelPixelsPerUnit, scroll.scrollWidth - scroll.clientWidth)));
 e_preventDefault(e);
 display.wheelStartX = null; // Abort measurement, if in progress
 return;
 }

 if (dy && wheelPixelsPerUnit != null) {
 var pixels = dy * wheelPixelsPerUnit;
 var top = cm.doc.scrollTop, bot = top + display.wrapper.clientHeight;
 if (pixels < 0) top = Math.max(0, top + pixels - 50);
 else bot = Math.min(cm.doc.height, bot + pixels + 50);
 updateDisplay(cm, [], {top: top, bottom: bot});
 }

 if (wheelSamples < 20) {
 if (display.wheelStartX == null) {
 display.wheelStartX = scroll.scrollLeft; display.wheelStartY = scroll.scrollTop;
 display.wheelDX = dx; display.wheelDY = dy;
 setTimeout(function() {
 if (display.wheelStartX == null) return;
 var movedX = scroll.scrollLeft - display.wheelStartX;
 var movedY = scroll.scrollTop - display.wheelStartY;
 var sample = (movedY && display.wheelDY && movedY / display.wheelDY) ||
 (movedX && display.wheelDX && movedX / display.wheelDX);
 display.wheelStartX = display.wheelStartY = null;
 if (!sample) return;
 wheelPixelsPerUnit = (wheelPixelsPerUnit * wheelSamples + sample) / (wheelSamples + 1);
 ++wheelSamples;
 }, 200);
 } else {
 display.wheelDX += dx; display.wheelDY += dy;
 }
 }
 }

 function doHandleBinding(cm, bound, dropShift) {
 if (typeof bound == "string") {
 bound = commands[bound];
 if (!bound) return false;
 }
 // Ensure previous input has been read, so that the handler sees a
 // consistent view of the document
 if (cm.display.pollingFast && readInput(cm)) cm.display.pollingFast = false;
 var doc = cm.doc, prevShift = doc.sel.shift, done = false;
 try {
 if (isReadOnly(cm)) cm.state.suppressEdits = true;
 if (dropShift) doc.sel.shift = false;
 done = bound(cm) != Pass;
 } finally {
 doc.sel.shift = prevShift;
 cm.state.suppressEdits = false;
 }
 return done;
 }

 function allKeyMaps(cm) {
 var maps = cm.state.keyMaps.slice(0);
 if (cm.options.extraKeys) maps.push(cm.options.extraKeys);
 maps.push(cm.options.keyMap);
 return maps;
 }

 var maybeTransition;
 function handleKeyBinding(cm, e) {
 // Handle auto keymap transitions
 var startMap = getKeyMap(cm.options.keyMap), next = startMap.auto;
 clearTimeout(maybeTransition);
 if (next && !isModifierKey(e)) maybeTransition = setTimeout(function() {
 if (getKeyMap(cm.options.keyMap) == startMap) {
 cm.options.keyMap = (next.call ? next.call(null, cm) : next);
 keyMapChanged(cm);
 }
 }, 50);

 var name = keyName(e, true), handled = false;
 if (!name) return false;
 var keymaps = allKeyMaps(cm);

 if (e.shiftKey) {
 // First try to resolve full name (including 'Shift-'). Failing
 // that, see if there is a cursor-motion command (starting with
 // 'go') bound to the keyname without 'Shift-'.
 handled = lookupKey("Shift-" + name, keymaps, function(b) {return doHandleBinding(cm, b, true);})
 || lookupKey(name, keymaps, function(b) {
 if (typeof b == "string" ? /^go[A-Z]/.test(b) : b.motion)
 return doHandleBinding(cm, b);
 });
 } else {
 handled = lookupKey(name, keymaps, function(b) { return doHandleBinding(cm, b); });
 }

 if (handled) {
 e_preventDefault(e);
 restartBlink(cm);
 if (ie_lt9) { e.oldKeyCode = e.keyCode; e.keyCode = 0; }
 signalLater(cm, "keyHandled", cm, name, e);
 }
 return handled;
 }

 function handleCharBinding(cm, e, ch) {
 var handled = lookupKey("'" + ch + "'", allKeyMaps(cm),
 function(b) { return doHandleBinding(cm, b, true); });
 if (handled) {
 e_preventDefault(e);
 restartBlink(cm);
 signalLater(cm, "keyHandled", cm, "'" + ch + "'", e);
 }
 return handled;
 }

 function onKeyUp(e) {
 var cm = this;
 if (signalDOMEvent(cm, e) || cm.options.onKeyEvent && cm.options.onKeyEvent(cm, addStop(e))) return;
 if (e.keyCode == 16) cm.doc.sel.shift = false;
 }

 var lastStoppedKey = null;
 function onKeyDown(e) {
 var cm = this;
 ensureFocus(cm);
 if (signalDOMEvent(cm, e) || cm.options.onKeyEvent && cm.options.onKeyEvent(cm, addStop(e))) return;
 if (old_ie && e.keyCode == 27) e.returnValue = false;
 var code = e.keyCode;
 // IE does strange things with escape.
 cm.doc.sel.shift = code == 16 || e.shiftKey;
 // First give onKeyEvent option a chance to handle this.
 var handled = handleKeyBinding(cm, e);
 if (opera) {
 lastStoppedKey = handled ? code : null;
 // Opera has no cut event... we try to at least catch the key combo
 if (!handled && code == 88 && !hasCopyEvent && (mac ? e.metaKey : e.ctrlKey))
 cm.replaceSelection("");
 }
 }

 function onKeyPress(e) {
 var cm = this;
 if (signalDOMEvent(cm, e) || cm.options.onKeyEvent && cm.options.onKeyEvent(cm, addStop(e))) return;
 var keyCode = e.keyCode, charCode = e.charCode;
 if (opera && keyCode == lastStoppedKey) {lastStoppedKey = null; e_preventDefault(e); return;}
 if (((opera && (!e.which || e.which < 10)) || khtml) && handleKeyBinding(cm, e)) return;
 var ch = String.fromCharCode(charCode == null ? keyCode : charCode);
 if (handleCharBinding(cm, e, ch)) return;
 if (ie && !ie_lt9) cm.display.inputHasSelection = null;
 fastPoll(cm);
 }

 function onFocus(cm) {
 if (cm.options.readOnly == "nocursor") return;
 if (!cm.state.focused) {
 signal(cm, "focus", cm);
 cm.state.focused = true;
 if (cm.display.wrapper.className.search(/\bCodeMirror-focused\b/) == -1)
 cm.display.wrapper.className += " CodeMirror-focused";
 if (!cm.curOp) {
 resetInput(cm, true);
 if (webkit) setTimeout(bind(resetInput, cm, true), 0); // Issue #1730
 }
 }
 slowPoll(cm);
 restartBlink(cm);
 }
 function onBlur(cm) {
 if (cm.state.focused) {
 signal(cm, "blur", cm);
 cm.state.focused = false;
 cm.display.wrapper.className = cm.display.wrapper.className.replace(" CodeMirror-focused", "");
 }
 clearInterval(cm.display.blinker);
 setTimeout(function() {if (!cm.state.focused) cm.doc.sel.shift = false;}, 150);
 }

 var detectingSelectAll;
 function onContextMenu(cm, e) {
 if (signalDOMEvent(cm, e, "contextmenu")) return;
 var display = cm.display, sel = cm.doc.sel;
 if (eventInWidget(display, e) || contextMenuInGutter(cm, e)) return;

 var pos = posFromMouse(cm, e), scrollPos = display.scroller.scrollTop;
 if (!pos || opera) return; // Opera is difficult.

 // Reset the current text selection only if the click is done outside of the selection
 // and 'resetSelectionOnContextMenu' option is true.
 var reset = cm.options.resetSelectionOnContextMenu;
 if (reset && (posEq(sel.from, sel.to) || posLess(pos, sel.from) || !posLess(pos, sel.to)))
 operation(cm, setSelection)(cm.doc, pos, pos);

 var oldCSS = display.input.style.cssText;
 display.inputDiv.style.position = "absolute";
 display.input.style.cssText = "position: fixed; width: 30px; height: 30px; top: " + (e.clientY - 5) +
 "px; left: " + (e.clientX - 5) + "px; z-index: 1000; background: transparent; outline: none;" +
 "border-width: 0; outline: none; overflow: hidden; opacity: .05; -ms-opacity: .05; filter: alpha(opacity=5);";
 focusInput(cm);
 resetInput(cm, true);
 // Adds "Select all" to context menu in FF
 if (posEq(sel.from, sel.to)) display.input.value = display.prevInput = " ";

 function prepareSelectAllHack() {
 if (display.input.selectionStart != null) {
 var extval = display.input.value = "\u200b" + (posEq(sel.from, sel.to) ? "" : display.input.value);
 display.prevInput = "\u200b";
 display.input.selectionStart = 1; display.input.selectionEnd = extval.length;
 }
 }
 function rehide() {
 display.inputDiv.style.position = "relative";
 display.input.style.cssText = oldCSS;
 if (ie_lt9) display.scrollbarV.scrollTop = display.scroller.scrollTop = scrollPos;
 slowPoll(cm);

 // Try to detect the user choosing select-all
 if (display.input.selectionStart != null) {
 if (!ie || ie_lt9) prepareSelectAllHack();
 clearTimeout(detectingSelectAll);
 var i = 0, poll = function(){
 if (display.prevInput == "\u200b" && display.input.selectionStart == 0)
 operation(cm, commands.selectAll)(cm);
 else if (i++ < 10) detectingSelectAll = setTimeout(poll, 500);
 else resetInput(cm);
 };
 detectingSelectAll = setTimeout(poll, 200);
 }
 }

 if (ie && !ie_lt9) prepareSelectAllHack();
 if (captureMiddleClick) {
 e_stop(e);
 var mouseup = function() {
 off(window, "mouseup", mouseup);
 setTimeout(rehide, 20);
 };
 on(window, "mouseup", mouseup);
 } else {
 setTimeout(rehide, 50);
 }
 }

 // UPDATING

 var changeEnd = CodeMirror.changeEnd = function(change) {
 if (!change.text) return change.to;
 return Pos(change.from.line + change.text.length - 1,
 lst(change.text).length + (change.text.length == 1 ? change.from.ch : 0));
 };

 // Make sure a position will be valid after the given change.
 function clipPostChange(doc, change, pos) {
 if (!posLess(change.from, pos)) return clipPos(doc, pos);
 var diff = (change.text.length - 1) - (change.to.line - change.from.line);
 if (pos.line > change.to.line + diff) {
 var preLine = pos.line - diff, lastLine = doc.first + doc.size - 1;
 if (preLine > lastLine) return Pos(lastLine, getLine(doc, lastLine).text.length);
 return clipToLen(pos, getLine(doc, preLine).text.length);
 }
 if (pos.line == change.to.line + diff)
 return clipToLen(pos, lst(change.text).length + (change.text.length == 1 ? change.from.ch : 0) +
 getLine(doc, change.to.line).text.length - change.to.ch);
 var inside = pos.line - change.from.line;
 return clipToLen(pos, change.text[inside].length + (inside ? 0 : change.from.ch));
 }

 // Hint can be null|"end"|"start"|"around"|{anchor,head}
 function computeSelAfterChange(doc, change, hint) {
 if (hint && typeof hint == "object") // Assumed to be {anchor, head} object
 return {anchor: clipPostChange(doc, change, hint.anchor),
 head: clipPostChange(doc, change, hint.head)};

 if (hint == "start") return {anchor: change.from, head: change.from};

 var end = changeEnd(change);
 if (hint == "around") return {anchor: change.from, head: end};
 if (hint == "end") return {anchor: end, head: end};

 // hint is null, leave the selection alone as much as possible
 var adjustPos = function(pos) {
 if (posLess(pos, change.from)) return pos;
 if (!posLess(change.to, pos)) return end;

 var line = pos.line + change.text.length - (change.to.line - change.from.line) - 1, ch = pos.ch;
 if (pos.line == change.to.line) ch += end.ch - change.to.ch;
 return Pos(line, ch);
 };
 return {anchor: adjustPos(doc.sel.anchor), head: adjustPos(doc.sel.head)};
 }

 function filterChange(doc, change, update) {
 var obj = {
 canceled: false,
 from: change.from,
 to: change.to,
 text: change.text,
 origin: change.origin,
 cancel: function() { this.canceled = true; }
 };
 if (update) obj.update = function(from, to, text, origin) {
 if (from) this.from = clipPos(doc, from);
 if (to) this.to = clipPos(doc, to);
 if (text) this.text = text;
 if (origin !== undefined) this.origin = origin;
 };
 signal(doc, "beforeChange", doc, obj);
 if (doc.cm) signal(doc.cm, "beforeChange", doc.cm, obj);

 if (obj.canceled) return null;
 return {from: obj.from, to: obj.to, text: obj.text, origin: obj.origin};
 }

 // Replace the range from from to to by the strings in replacement.
 // change is a {from, to, text [, origin]} object
 function makeChange(doc, change, selUpdate, ignoreReadOnly) {
 if (doc.cm) {
 if (!doc.cm.curOp) return operation(doc.cm, makeChange)(doc, change, selUpdate, ignoreReadOnly);
 if (doc.cm.state.suppressEdits) return;
 }

 if (hasHandler(doc, "beforeChange") || doc.cm && hasHandler(doc.cm, "beforeChange")) {
 change = filterChange(doc, change, true);
 if (!change) return;
 }

 // Possibly split or suppress the update based on the presence
 // of read-only spans in its range.
 var split = sawReadOnlySpans && !ignoreReadOnly && removeReadOnlyRanges(doc, change.from, change.to);
 if (split) {
 for (var i = split.length - 1; i >= 1; --i)
 makeChangeNoReadonly(doc, {from: split[i].from, to: split[i].to, text: [""]});
 if (split.length)
 makeChangeNoReadonly(doc, {from: split[0].from, to: split[0].to, text: change.text}, selUpdate);
 } else {
 makeChangeNoReadonly(doc, change, selUpdate);
 }
 }

 function makeChangeNoReadonly(doc, change, selUpdate) {
 if (change.text.length == 1 && change.text[0] == "" && posEq(change.from, change.to)) return;
 var selAfter = computeSelAfterChange(doc, change, selUpdate);
 addToHistory(doc, change, selAfter, doc.cm ? doc.cm.curOp.id : NaN);

 makeChangeSingleDoc(doc, change, selAfter, stretchSpansOverChange(doc, change));
 var rebased = [];

 linkedDocs(doc, function(doc, sharedHist) {
 if (!sharedHist && indexOf(rebased, doc.history) == -1) {
 rebaseHist(doc.history, change);
 rebased.push(doc.history);
 }
 makeChangeSingleDoc(doc, change, null, stretchSpansOverChange(doc, change));
 });
 }

 function makeChangeFromHistory(doc, type) {
 if (doc.cm && doc.cm.state.suppressEdits) return;

 var hist = doc.history;
 var event = (type == "undo" ? hist.done : hist.undone).pop();
 if (!event) return;

 var anti = {changes: [], anchorBefore: event.anchorAfter, headBefore: event.headAfter,
 anchorAfter: event.anchorBefore, headAfter: event.headBefore,
 generation: hist.generation};
 (type == "undo" ? hist.undone : hist.done).push(anti);
 hist.generation = event.generation || ++hist.maxGeneration;

 var filter = hasHandler(doc, "beforeChange") || doc.cm && hasHandler(doc.cm, "beforeChange");

 for (var i = event.changes.length - 1; i >= 0; --i) {
 var change = event.changes[i];
 change.origin = type;
 if (filter && !filterChange(doc, change, false)) {
 (type == "undo" ? hist.done : hist.undone).length = 0;
 return;
 }

 anti.changes.push(historyChangeFromChange(doc, change));

 var after = i ? computeSelAfterChange(doc, change, null)
 : {anchor: event.anchorBefore, head: event.headBefore};
 makeChangeSingleDoc(doc, change, after, mergeOldSpans(doc, change));
 var rebased = [];

 linkedDocs(doc, function(doc, sharedHist) {
 if (!sharedHist && indexOf(rebased, doc.history) == -1) {
 rebaseHist(doc.history, change);
 rebased.push(doc.history);
 }
 makeChangeSingleDoc(doc, change, null, mergeOldSpans(doc, change));
 });
 }
 }

 function shiftDoc(doc, distance) {
 function shiftPos(pos) {return Pos(pos.line + distance, pos.ch);}
 doc.first += distance;
 if (doc.cm) regChange(doc.cm, doc.first, doc.first, distance);
 doc.sel.head = shiftPos(doc.sel.head); doc.sel.anchor = shiftPos(doc.sel.anchor);
 doc.sel.from = shiftPos(doc.sel.from); doc.sel.to = shiftPos(doc.sel.to);
 }

 function makeChangeSingleDoc(doc, change, selAfter, spans) {
 if (doc.cm && !doc.cm.curOp)
 return operation(doc.cm, makeChangeSingleDoc)(doc, change, selAfter, spans);

 if (change.to.line < doc.first) {
 shiftDoc(doc, change.text.length - 1 - (change.to.line - change.from.line));
 return;
 }
 if (change.from.line > doc.lastLine()) return;

 // Clip the change to the size of this doc
 if (change.from.line < doc.first) {
 var shift = change.text.length - 1 - (doc.first - change.from.line);
 shiftDoc(doc, shift);
 change = {from: Pos(doc.first, 0), to: Pos(change.to.line + shift, change.to.ch),
 text: [lst(change.text)], origin: change.origin};
 }
 var last = doc.lastLine();
 if (change.to.line > last) {
 change = {from: change.from, to: Pos(last, getLine(doc, last).text.length),
 text: [change.text[0]], origin: change.origin};
 }

 change.removed = getBetween(doc, change.from, change.to);

 if (!selAfter) selAfter = computeSelAfterChange(doc, change, null);
 if (doc.cm) makeChangeSingleDocInEditor(doc.cm, change, spans, selAfter);
 else updateDoc(doc, change, spans, selAfter);
 }

 function makeChangeSingleDocInEditor(cm, change, spans, selAfter) {
 var doc = cm.doc, display = cm.display, from = change.from, to = change.to;

 var recomputeMaxLength = false, checkWidthStart = from.line;
 if (!cm.options.lineWrapping) {
 checkWidthStart = lineNo(visualLine(doc, getLine(doc, from.line)));
 doc.iter(checkWidthStart, to.line + 1, function(line) {
 if (line == display.maxLine) {
 recomputeMaxLength = true;
 return true;
 }
 });
 }

 if (!posLess(doc.sel.head, change.from) && !posLess(change.to, doc.sel.head))
 cm.curOp.cursorActivity = true;

 updateDoc(doc, change, spans, selAfter, estimateHeight(cm));

 if (!cm.options.lineWrapping) {
 doc.iter(checkWidthStart, from.line + change.text.length, function(line) {
 var len = lineLength(doc, line);
 if (len > display.maxLineLength) {
 display.maxLine = line;
 display.maxLineLength = len;
 display.maxLineChanged = true;
 recomputeMaxLength = false;
 }
 });
 if (recomputeMaxLength) cm.curOp.updateMaxLine = true;
 }

 // Adjust frontier, schedule worker
 doc.frontier = Math.min(doc.frontier, from.line);
 startWorker(cm, 400);

 var lendiff = change.text.length - (to.line - from.line) - 1;
 // Remember that these lines changed, for updating the display
 regChange(cm, from.line, to.line + 1, lendiff);

 if (hasHandler(cm, "change")) {
 var changeObj = {from: from, to: to,
 text: change.text,
 removed: change.removed,
 origin: change.origin};
 if (cm.curOp.textChanged) {
 for (var cur = cm.curOp.textChanged; cur.next; cur = cur.next) {}
 cur.next = changeObj;
 } else cm.curOp.textChanged = changeObj;
 }
 }

 function replaceRange(doc, code, from, to, origin) {
 if (!to) to = from;
 if (posLess(to, from)) { var tmp = to; to = from; from = tmp; }
 if (typeof code == "string") code = splitLines(code);
 makeChange(doc, {from: from, to: to, text: code, origin: origin}, null);
 }

 // POSITION OBJECT

 function Pos(line, ch) {
 if (!(this instanceof Pos)) return new Pos(line, ch);
 this.line = line; this.ch = ch;
 }
 CodeMirror.Pos = Pos;

 function posEq(a, b) {return a.line == b.line && a.ch == b.ch;}
 function posLess(a, b) {return a.line < b.line || (a.line == b.line && a.ch < b.ch);}
 function cmp(a, b) {return a.line - b.line || a.ch - b.ch;}
 function copyPos(x) {return Pos(x.line, x.ch);}

 // SELECTION

 function clipLine(doc, n) {return Math.max(doc.first, Math.min(n, doc.first + doc.size - 1));}
 function clipPos(doc, pos) {
 if (pos.line < doc.first) return Pos(doc.first, 0);
 var last = doc.first + doc.size - 1;
 if (pos.line > last) return Pos(last, getLine(doc, last).text.length);
 return clipToLen(pos, getLine(doc, pos.line).text.length);
 }
 function clipToLen(pos, linelen) {
 var ch = pos.ch;
 if (ch == null || ch > linelen) return Pos(pos.line, linelen);
 else if (ch < 0) return Pos(pos.line, 0);
 else return pos;
 }
 function isLine(doc, l) {return l >= doc.first && l < doc.first + doc.size;}

 // If shift is held, this will move the selection anchor. Otherwise,
 // it'll set the whole selection.
 function extendSelection(doc, pos, other, bias) {
 if (doc.sel.shift || doc.sel.extend) {
 var anchor = doc.sel.anchor;
 if (other) {
 var posBefore = posLess(pos, anchor);
 if (posBefore != posLess(other, anchor)) {
 anchor = pos;
 pos = other;
 } else if (posBefore != posLess(pos, other)) {
 pos = other;
 }
 }
 setSelection(doc, anchor, pos, bias);
 } else {
 setSelection(doc, pos, other || pos, bias);
 }
 if (doc.cm) doc.cm.curOp.userSelChange = true;
 }

 function filterSelectionChange(doc, anchor, head) {
 var obj = {anchor: anchor, head: head};
 signal(doc, "beforeSelectionChange", doc, obj);
 if (doc.cm) signal(doc.cm, "beforeSelectionChange", doc.cm, obj);
 obj.anchor = clipPos(doc, obj.anchor); obj.head = clipPos(doc, obj.head);
 return obj;
 }

 // Update the selection. Last two args are only used by
 // updateDoc, since they have to be expressed in the line
 // numbers before the update.
 function setSelection(doc, anchor, head, bias, checkAtomic) {
 if (!checkAtomic && hasHandler(doc, "beforeSelectionChange") || doc.cm && hasHandler(doc.cm, "beforeSelectionChange")) {
 var filtered = filterSelectionChange(doc, anchor, head);
 head = filtered.head;
 anchor = filtered.anchor;
 }

 var sel = doc.sel;
 sel.goalColumn = null;
 if (bias == null) bias = posLess(head, sel.head) ? -1 : 1;
 // Skip over atomic spans.
 if (checkAtomic || !posEq(anchor, sel.anchor))
 anchor = skipAtomic(doc, anchor, bias, checkAtomic != "push");
 if (checkAtomic || !posEq(head, sel.head))
 head = skipAtomic(doc, head, bias, checkAtomic != "push");

 if (posEq(sel.anchor, anchor) && posEq(sel.head, head)) return;

 sel.anchor = anchor; sel.head = head;
 var inv = posLess(head, anchor);
 sel.from = inv ? head : anchor;
 sel.to = inv ? anchor : head;

 if (doc.cm)
 doc.cm.curOp.updateInput = doc.cm.curOp.selectionChanged =
 doc.cm.curOp.cursorActivity = true;

 signalLater(doc, "cursorActivity", doc);
 }

 function reCheckSelection(cm) {
 setSelection(cm.doc, cm.doc.sel.from, cm.doc.sel.to, null, "push");
 }

 function skipAtomic(doc, pos, bias, mayClear) {
 var flipped = false, curPos = pos;
 var dir = bias || 1;
 doc.cantEdit = false;
 search: for (;;) {
 var line = getLine(doc, curPos.line);
 if (line.markedSpans) {
 for (var i = 0; i < line.markedSpans.length; ++i) {
 var sp = line.markedSpans[i], m = sp.marker;
 if ((sp.from == null || (m.inclusiveLeft ? sp.from <= curPos.ch : sp.from < curPos.ch)) &&
 (sp.to == null || (m.inclusiveRight ? sp.to >= curPos.ch : sp.to > curPos.ch))) {
 if (mayClear) {
 signal(m, "beforeCursorEnter");
 if (m.explicitlyCleared) {
 if (!line.markedSpans) break;
 else {--i; continue;}
 }
 }
 if (!m.atomic) continue;
 var newPos = m.find()[dir < 0 ? "from" : "to"];
 if (posEq(newPos, curPos)) {
 newPos.ch += dir;
 if (newPos.ch < 0) {
 if (newPos.line > doc.first) newPos = clipPos(doc, Pos(newPos.line - 1));
 else newPos = null;
 } else if (newPos.ch > line.text.length) {
 if (newPos.line < doc.first + doc.size - 1) newPos = Pos(newPos.line + 1, 0);
 else newPos = null;
 }
 if (!newPos) {
 if (flipped) {
 // Driven in a corner -- no valid cursor position found at all
 // -- try again *with* clearing, if we didn't already
 if (!mayClear) return skipAtomic(doc, pos, bias, true);
 // Otherwise, turn off editing until further notice, and return the start of the doc
 doc.cantEdit = true;
 return Pos(doc.first, 0);
 }
 flipped = true; newPos = pos; dir = -dir;
 }
 }
 curPos = newPos;
 continue search;
 }
 }
 }
 return curPos;
 }
 }

 // SCROLLING

 function scrollCursorIntoView(cm) {
 var coords = scrollPosIntoView(cm, cm.doc.sel.head, null, cm.options.cursorScrollMargin);
 if (!cm.state.focused) return;
 var display = cm.display, box = getRect(display.sizer), doScroll = null;
 if (coords.top + box.top < 0) doScroll = true;
 else if (coords.bottom + box.top > (window.innerHeight || document.documentElement.clientHeight)) doScroll = false;
 if (doScroll != null && !phantom) {
 var scrollNode = elt("div", "\u200b", null, "position: absolute; top: " +
 (coords.top - display.viewOffset) + "px; height: " +
 (coords.bottom - coords.top + scrollerCutOff) + "px; left: " +
 coords.left + "px; width: 2px;");
 cm.display.lineSpace.appendChild(scrollNode);
 scrollNode.scrollIntoView(doScroll);
 cm.display.lineSpace.removeChild(scrollNode);
 }
 }

 function scrollPosIntoView(cm, pos, end, margin) {
 if (margin == null) margin = 0;
 for (;;) {
 var changed = false, coords = cursorCoords(cm, pos);
 var endCoords = !end || end == pos ? coords : cursorCoords(cm, end);
 var scrollPos = calculateScrollPos(cm, Math.min(coords.left, endCoords.left),
 Math.min(coords.top, endCoords.top) - margin,
 Math.max(coords.left, endCoords.left),
 Math.max(coords.bottom, endCoords.bottom) + margin);
 var startTop = cm.doc.scrollTop, startLeft = cm.doc.scrollLeft;
 if (scrollPos.scrollTop != null) {
 setScrollTop(cm, scrollPos.scrollTop);
 if (Math.abs(cm.doc.scrollTop - startTop) > 1) changed = true;
 }
 if (scrollPos.scrollLeft != null) {
 setScrollLeft(cm, scrollPos.scrollLeft);
 if (Math.abs(cm.doc.scrollLeft - startLeft) > 1) changed = true;
 }
 if (!changed) return coords;
 }
 }

 function scrollIntoView(cm, x1, y1, x2, y2) {
 var scrollPos = calculateScrollPos(cm, x1, y1, x2, y2);
 if (scrollPos.scrollTop != null) setScrollTop(cm, scrollPos.scrollTop);
 if (scrollPos.scrollLeft != null) setScrollLeft(cm, scrollPos.scrollLeft);
 }

 function calculateScrollPos(cm, x1, y1, x2, y2) {
 var display = cm.display, snapMargin = textHeight(cm.display);
 if (y1 < 0) y1 = 0;
 var screen = display.scroller.clientHeight - scrollerCutOff, screentop = display.scroller.scrollTop, result = {};
 var docBottom = cm.doc.height + paddingVert(display);
 var atTop = y1 < snapMargin, atBottom = y2 > docBottom - snapMargin;
 if (y1 < screentop) {
 result.scrollTop = atTop ? 0 : y1;
 } else if (y2 > screentop + screen) {
 var newTop = Math.min(y1, (atBottom ? docBottom : y2) - screen);
 if (newTop != screentop) result.scrollTop = newTop;
 }

 var screenw = display.scroller.clientWidth - scrollerCutOff, screenleft = display.scroller.scrollLeft;
 x1 += display.gutters.offsetWidth; x2 += display.gutters.offsetWidth;
 var gutterw = display.gutters.offsetWidth;
 var atLeft = x1 < gutterw + 10;
 if (x1 < screenleft + gutterw || atLeft) {
 if (atLeft) x1 = 0;
 result.scrollLeft = Math.max(0, x1 - 10 - gutterw);
 } else if (x2 > screenw + screenleft - 3) {
 result.scrollLeft = x2 + 10 - screenw;
 }
 return result;
 }

 function updateScrollPos(cm, left, top) {
 cm.curOp.updateScrollPos = {scrollLeft: left == null ? cm.doc.scrollLeft : left,
 scrollTop: top == null ? cm.doc.scrollTop : top};
 }

 function addToScrollPos(cm, left, top) {
 var pos = cm.curOp.updateScrollPos || (cm.curOp.updateScrollPos = {scrollLeft: cm.doc.scrollLeft, scrollTop: cm.doc.scrollTop});
 var scroll = cm.display.scroller;
 pos.scrollTop = Math.max(0, Math.min(scroll.scrollHeight - scroll.clientHeight, pos.scrollTop + top));
 pos.scrollLeft = Math.max(0, Math.min(scroll.scrollWidth - scroll.clientWidth, pos.scrollLeft + left));
 }

 // API UTILITIES

 function indentLine(cm, n, how, aggressive) {
 var doc = cm.doc, state;
 if (how == null) how = "add";
 if (how == "smart") {
 if (!cm.doc.mode.indent) how = "prev";
 else state = getStateBefore(cm, n);
 }

 var tabSize = cm.options.tabSize;
 var line = getLine(doc, n), curSpace = countColumn(line.text, null, tabSize);
 if (line.stateAfter) line.stateAfter = null;
 var curSpaceString = line.text.match(/^\s*/)[0], indentation;
 if (!aggressive && !/\S/.test(line.text)) {
 indentation = 0;
 how = "not";
 } else if (how == "smart") {
 indentation = cm.doc.mode.indent(state, line.text.slice(curSpaceString.length), line.text);
 if (indentation == Pass) {
 if (!aggressive) return;
 how = "prev";
 }
 }
 if (how == "prev") {
 if (n > doc.first) indentation = countColumn(getLine(doc, n-1).text, null, tabSize);
 else indentation = 0;
 } else if (how == "add") {
 indentation = curSpace + cm.options.indentUnit;
 } else if (how == "subtract") {
 indentation = curSpace - cm.options.indentUnit;
 } else if (typeof how == "number") {
 indentation = curSpace + how;
 }
 indentation = Math.max(0, indentation);

 var indentString = "", pos = 0;
 if (cm.options.indentWithTabs)
 for (var i = Math.floor(indentation / tabSize); i; --i) {pos += tabSize; indentString += "\t";}
 if (pos < indentation) indentString += spaceStr(indentation - pos);

 if (indentString != curSpaceString)
 replaceRange(cm.doc, indentString, Pos(n, 0), Pos(n, curSpaceString.length), "+input");
 else if (doc.sel.head.line == n && doc.sel.head.ch < curSpaceString.length)
 setSelection(doc, Pos(n, curSpaceString.length), Pos(n, curSpaceString.length), 1);
 line.stateAfter = null;
 }

 function changeLine(cm, handle, op) {
 var no = handle, line = handle, doc = cm.doc;
 if (typeof handle == "number") line = getLine(doc, clipLine(doc, handle));
 else no = lineNo(handle);
 if (no == null) return null;
 if (op(line, no)) regChange(cm, no, no + 1);
 else return null;
 return line;
 }

 function findPosH(doc, pos, dir, unit, visually) {
 var line = pos.line, ch = pos.ch, origDir = dir;
 var lineObj = getLine(doc, line);
 var possible = true;
 function findNextLine() {
 var l = line + dir;
 if (l < doc.first || l >= doc.first + doc.size) return (possible = false);
 line = l;
 return lineObj = getLine(doc, l);
 }
 function moveOnce(boundToLine) {
 var next = (visually ? moveVisually : moveLogically)(lineObj, ch, dir, true);
 if (next == null) {
 if (!boundToLine && findNextLine()) {
 if (visually) ch = (dir < 0 ? lineRight : lineLeft)(lineObj);
 else ch = dir < 0 ? lineObj.text.length : 0;
 } else return (possible = false);
 } else ch = next;
 return true;
 }

 if (unit == "char") moveOnce();
 else if (unit == "column") moveOnce(true);
 else if (unit == "word" || unit == "group") {
 var sawType = null, group = unit == "group";
 for (var first = true;; first = false) {
 if (dir < 0 && !moveOnce(!first)) break;
 var cur = lineObj.text.charAt(ch) || "\n";
 var type = isWordChar(cur) ? "w"
 : group && cur == "\n" ? "n"
 : !group || /\s/.test(cur) ? null
 : "p";
 if (group && !first && !type) type = "s";
 if (sawType && sawType != type) {
 if (dir < 0) {dir = 1; moveOnce();}
 break;
 }

 if (type) sawType = type;
 if (dir > 0 && !moveOnce(!first)) break;
 }
 }
 var result = skipAtomic(doc, Pos(line, ch), origDir, true);
 if (!possible) result.hitSide = true;
 return result;
 }

 function findPosV(cm, pos, dir, unit) {
 var doc = cm.doc, x = pos.left, y;
 if (unit == "page") {
 var pageSize = Math.min(cm.display.wrapper.clientHeight, window.innerHeight || document.documentElement.clientHeight);
 y = pos.top + dir * (pageSize - (dir < 0 ? 1.5 : .5) * textHeight(cm.display));
 } else if (unit == "line") {
 y = dir > 0 ? pos.bottom + 3 : pos.top - 3;
 }
 for (;;) {
 var target = coordsChar(cm, x, y);
 if (!target.outside) break;
 if (dir < 0 ? y <= 0 : y >= doc.height) { target.hitSide = true; break; }
 y += dir * 5;
 }
 return target;
 }

 function findWordAt(line, pos) {
 var start = pos.ch, end = pos.ch;
 if (line) {
 if ((pos.xRel < 0 || end == line.length) && start) --start; else ++end;
 var startChar = line.charAt(start);
 var check = isWordChar(startChar) ? isWordChar
 : /\s/.test(startChar) ? function(ch) {return /\s/.test(ch);}
 : function(ch) {return !/\s/.test(ch) && !isWordChar(ch);};
 while (start > 0 && check(line.charAt(start - 1))) --start;
 while (end < line.length && check(line.charAt(end))) ++end;
 }
 return {from: Pos(pos.line, start), to: Pos(pos.line, end)};
 }

 function selectLine(cm, line) {
 extendSelection(cm.doc, Pos(line, 0), clipPos(cm.doc, Pos(line + 1, 0)));
 }

 // PROTOTYPE

 // The publicly visible API. Note that operation(null, f) means
 // 'wrap f in an operation, performed on its `this` parameter'

 CodeMirror.prototype = {
 constructor: CodeMirror,
 focus: function(){window.focus(); focusInput(this); fastPoll(this);},

 setOption: function(option, value) {
 var options = this.options, old = options[option];
 if (options[option] == value && option != "mode") return;
 options[option] = value;
 if (optionHandlers.hasOwnProperty(option))
 operation(this, optionHandlers[option])(this, value, old);
 },

 getOption: function(option) {return this.options[option];},
 getDoc: function() {return this.doc;},

 addKeyMap: function(map, bottom) {
 this.state.keyMaps[bottom ? "push" : "unshift"](map);
 },
 removeKeyMap: function(map) {
 var maps = this.state.keyMaps;
 for (var i = 0; i < maps.length; ++i)
 if (maps[i] == map || (typeof maps[i] != "string" && maps[i].name == map)) {
 maps.splice(i, 1);
 return true;
 }
 },

 addOverlay: operation(null, function(spec, options) {
 var mode = spec.token ? spec : CodeMirror.getMode(this.options, spec);
 if (mode.startState) throw new Error("Overlays may not be stateful.");
 this.state.overlays.push({mode: mode, modeSpec: spec, opaque: options && options.opaque});
 this.state.modeGen++;
 regChange(this);
 }),
 removeOverlay: operation(null, function(spec) {
 var overlays = this.state.overlays;
 for (var i = 0; i < overlays.length; ++i) {
 var cur = overlays[i].modeSpec;
 if (cur == spec || typeof spec == "string" && cur.name == spec) {
 overlays.splice(i, 1);
 this.state.modeGen++;
 regChange(this);
 return;
 }
 }
 }),

 indentLine: operation(null, function(n, dir, aggressive) {
 if (typeof dir != "string" && typeof dir != "number") {
 if (dir == null) dir = this.options.smartIndent ? "smart" : "prev";
 else dir = dir ? "add" : "subtract";
 }
 if (isLine(this.doc, n)) indentLine(this, n, dir, aggressive);
 }),
 indentSelection: operation(null, function(how) {
 var sel = this.doc.sel;
 if (posEq(sel.from, sel.to)) return indentLine(this, sel.from.line, how, true);
 var e = sel.to.line - (sel.to.ch ? 0 : 1);
 for (var i = sel.from.line; i <= e; ++i) indentLine(this, i, how);
 }),

 // Fetch the parser token for a given character. Useful for hacks
 // that want to inspect the mode state (say, for completion).
 getTokenAt: function(pos, precise) {
 var doc = this.doc;
 pos = clipPos(doc, pos);
 var state = getStateBefore(this, pos.line, precise), mode = this.doc.mode;
 var line = getLine(doc, pos.line);
 var stream = new StringStream(line.text, this.options.tabSize);
 while (stream.pos < pos.ch && !stream.eol()) {
 stream.start = stream.pos;
 var style = mode.token(stream, state);
 }
 return {start: stream.start,
 end: stream.pos,
 string: stream.current(),
 className: style || null, // Deprecated, use 'type' instead
 type: style || null,
 state: state};
 },

 getTokenTypeAt: function(pos) {
 pos = clipPos(this.doc, pos);
 var styles = getLineStyles(this, getLine(this.doc, pos.line));
 var before = 0, after = (styles.length - 1) / 2, ch = pos.ch;
 if (ch == 0) return styles[2];
 for (;;) {
 var mid = (before + after) >> 1;
 if ((mid ? styles[mid * 2 - 1] : 0) >= ch) after = mid;
 else if (styles[mid * 2 + 1] < ch) before = mid + 1;
 else return styles[mid * 2 + 2];
 }
 },

 getModeAt: function(pos) {
 var mode = this.doc.mode;
 if (!mode.innerMode) return mode;
 return CodeMirror.innerMode(mode, this.getTokenAt(pos).state).mode;
 },

 getHelper: function(pos, type) {
 return this.getHelpers(pos, type)[0];
 },

 getHelpers: function(pos, type) {
 var found = [];
 if (!helpers.hasOwnProperty(type)) return helpers;
 var help = helpers[type], mode = this.getModeAt(pos);
 if (typeof mode[type] == "string") {
 if (help[mode[type]]) found.push(help[mode[type]]);
 } else if (mode[type]) {
 for (var i = 0; i < mode[type].length; i++) {
 var val = help[mode[type][i]];
 if (val) found.push(val);
 }
 } else if (mode.helperType && help[mode.helperType]) {
 found.push(help[mode.helperType]);
 } else if (help[mode.name]) {
 found.push(help[mode.name]);
 }
 for (var i = 0; i < help._global.length; i++) {
 var cur = help._global[i];
 if (cur.pred(mode, this) && indexOf(found, cur.val) == -1)
 found.push(cur.val);
 }
 return found;
 },

 getStateAfter: function(line, precise) {
 var doc = this.doc;
 line = clipLine(doc, line == null ? doc.first + doc.size - 1: line);
 return getStateBefore(this, line + 1, precise);
 },

 cursorCoords: function(start, mode) {
 var pos, sel = this.doc.sel;
 if (start == null) pos = sel.head;
 else if (typeof start == "object") pos = clipPos(this.doc, start);
 else pos = start ? sel.from : sel.to;
 return cursorCoords(this, pos, mode || "page");
 },

 charCoords: function(pos, mode) {
 return charCoords(this, clipPos(this.doc, pos), mode || "page");
 },

 coordsChar: function(coords, mode) {
 coords = fromCoordSystem(this, coords, mode || "page");
 return coordsChar(this, coords.left, coords.top);
 },

 lineAtHeight: function(height, mode) {
 height = fromCoordSystem(this, {top: height, left: 0}, mode || "page").top;
 return lineAtHeight(this.doc, height + this.display.viewOffset);
 },
 heightAtLine: function(line, mode) {
 var end = false, last = this.doc.first + this.doc.size - 1;
 if (line < this.doc.first) line = this.doc.first;
 else if (line > last) { line = last; end = true; }
 var lineObj = getLine(this.doc, line);
 return intoCoordSystem(this, getLine(this.doc, line), {top: 0, left: 0}, mode || "page").top +
 (end ? lineObj.height : 0);
 },

 defaultTextHeight: function() { return textHeight(this.display); },
 defaultCharWidth: function() { return charWidth(this.display); },

 setGutterMarker: operation(null, function(line, gutterID, value) {
 return changeLine(this, line, function(line) {
 var markers = line.gutterMarkers || (line.gutterMarkers = {});
 markers[gutterID] = value;
 if (!value && isEmpty(markers)) line.gutterMarkers = null;
 return true;
 });
 }),

 clearGutter: operation(null, function(gutterID) {
 var cm = this, doc = cm.doc, i = doc.first;
 doc.iter(function(line) {
 if (line.gutterMarkers && line.gutterMarkers[gutterID]) {
 line.gutterMarkers[gutterID] = null;
 regChange(cm, i, i + 1);
 if (isEmpty(line.gutterMarkers)) line.gutterMarkers = null;
 }
 ++i;
 });
 }),

 addLineClass: operation(null, function(handle, where, cls) {
 return changeLine(this, handle, function(line) {
 var prop = where == "text" ? "textClass" : where == "background" ? "bgClass" : "wrapClass";
 if (!line[prop]) line[prop] = cls;
 else if (new RegExp("(?:^|\\s)" + cls + "(?:$|\\s)").test(line[prop])) return false;
 else line[prop] += " " + cls;
 return true;
 });
 }),

 removeLineClass: operation(null, function(handle, where, cls) {
 return changeLine(this, handle, function(line) {
 var prop = where == "text" ? "textClass" : where == "background" ? "bgClass" : "wrapClass";
 var cur = line[prop];
 if (!cur) return false;
 else if (cls == null) line[prop] = null;
 else {
 var found = cur.match(new RegExp("(?:^|\\s+)" + cls + "(?:$|\\s+)"));
 if (!found) return false;
 var end = found.index + found[0].length;
 line[prop] = cur.slice(0, found.index) + (!found.index || end == cur.length ? "" : " ") + cur.slice(end) || null;
 }
 return true;
 });
 }),

 addLineWidget: operation(null, function(handle, node, options) {
 return addLineWidget(this, handle, node, options);
 }),

 removeLineWidget: function(widget) { widget.clear(); },

 lineInfo: function(line) {
 if (typeof line == "number") {
 if (!isLine(this.doc, line)) return null;
 var n = line;
 line = getLine(this.doc, line);
 if (!line) return null;
 } else {
 var n = lineNo(line);
 if (n == null) return null;
 }
 return {line: n, handle: line, text: line.text, gutterMarkers: line.gutterMarkers,
 textClass: line.textClass, bgClass: line.bgClass, wrapClass: line.wrapClass,
 widgets: line.widgets};
 },

 getViewport: function() { return {from: this.display.showingFrom, to: this.display.showingTo};},

 addWidget: function(pos, node, scroll, vert, horiz) {
 var display = this.display;
 pos = cursorCoords(this, clipPos(this.doc, pos));
 var top = pos.bottom, left = pos.left;
 node.style.position = "absolute";
 display.sizer.appendChild(node);
 if (vert == "over") {
 top = pos.top;
 } else if (vert == "above" || vert == "near") {
 var vspace = Math.max(display.wrapper.clientHeight, this.doc.height),
 hspace = Math.max(display.sizer.clientWidth, display.lineSpace.clientWidth);
 // Default to positioning above (if specified and possible); otherwise default to positioning below
 if ((vert == 'above' || pos.bottom + node.offsetHeight > vspace) && pos.top > node.offsetHeight)
 top = pos.top - node.offsetHeight;
 else if (pos.bottom + node.offsetHeight <= vspace)
 top = pos.bottom;
 if (left + node.offsetWidth > hspace)
 left = hspace - node.offsetWidth;
 }
 node.style.top = top + "px";
 node.style.left = node.style.right = "";
 if (horiz == "right") {
 left = display.sizer.clientWidth - node.offsetWidth;
 node.style.right = "0px";
 } else {
 if (horiz == "left") left = 0;
 else if (horiz == "middle") left = (display.sizer.clientWidth - node.offsetWidth) / 2;
 node.style.left = left + "px";
 }
 if (scroll)
 scrollIntoView(this, left, top, left + node.offsetWidth, top + node.offsetHeight);
 },

 triggerOnKeyDown: operation(null, onKeyDown),
 triggerOnKeyPress: operation(null, onKeyPress),
 triggerOnKeyUp: operation(null, onKeyUp),

 execCommand: function(cmd) {
 if (commands.hasOwnProperty(cmd))
 return commands[cmd](this);
 },

 findPosH: function(from, amount, unit, visually) {
 var dir = 1;
 if (amount < 0) { dir = -1; amount = -amount; }
 for (var i = 0, cur = clipPos(this.doc, from); i < amount; ++i) {
 cur = findPosH(this.doc, cur, dir, unit, visually);
 if (cur.hitSide) break;
 }
 return cur;
 },

 moveH: operation(null, function(dir, unit) {
 var sel = this.doc.sel, pos;
 if (sel.shift || sel.extend || posEq(sel.from, sel.to))
 pos = findPosH(this.doc, sel.head, dir, unit, this.options.rtlMoveVisually);
 else
 pos = dir < 0 ? sel.from : sel.to;
 extendSelection(this.doc, pos, pos, dir);
 }),

 deleteH: operation(null, function(dir, unit) {
 var sel = this.doc.sel;
 if (!posEq(sel.from, sel.to)) replaceRange(this.doc, "", sel.from, sel.to, "+delete");
 else replaceRange(this.doc, "", sel.from, findPosH(this.doc, sel.head, dir, unit, false), "+delete");
 this.curOp.userSelChange = true;
 }),

 findPosV: function(from, amount, unit, goalColumn) {
 var dir = 1, x = goalColumn;
 if (amount < 0) { dir = -1; amount = -amount; }
 for (var i = 0, cur = clipPos(this.doc, from); i < amount; ++i) {
 var coords = cursorCoords(this, cur, "div");
 if (x == null) x = coords.left;
 else coords.left = x;
 cur = findPosV(this, coords, dir, unit);
 if (cur.hitSide) break;
 }
 return cur;
 },

 moveV: operation(null, function(dir, unit) {
 var sel = this.doc.sel, target, goal;
 if (sel.shift || sel.extend || posEq(sel.from, sel.to)) {
 var pos = cursorCoords(this, sel.head, "div");
 if (sel.goalColumn != null) pos.left = sel.goalColumn;
 target = findPosV(this, pos, dir, unit);
 if (unit == "page") addToScrollPos(this, 0, charCoords(this, target, "div").top - pos.top);
 goal = pos.left;
 } else {
 target = dir < 0 ? sel.from : sel.to;
 }
 extendSelection(this.doc, target, target, dir);
 if (goal != null) sel.goalColumn = goal;
 }),

 toggleOverwrite: function(value) {
 if (value != null && value == this.state.overwrite) return;
 if (this.state.overwrite = !this.state.overwrite)
 this.display.cursor.className += " CodeMirror-overwrite";
 else
 this.display.cursor.className = this.display.cursor.className.replace(" CodeMirror-overwrite", "");

 signal(this, "overwriteToggle", this, this.state.overwrite);
 },
 hasFocus: function() { return document.activeElement == this.display.input; },

 scrollTo: operation(null, function(x, y) {
 updateScrollPos(this, x, y);
 }),
 getScrollInfo: function() {
 var scroller = this.display.scroller, co = scrollerCutOff;
 return {left: scroller.scrollLeft, top: scroller.scrollTop,
 height: scroller.scrollHeight - co, width: scroller.scrollWidth - co,
 clientHeight: scroller.clientHeight - co, clientWidth: scroller.clientWidth - co};
 },

 scrollIntoView: operation(null, function(range, margin) {
 if (range == null) range = {from: this.doc.sel.head, to: null};
 else if (typeof range == "number") range = {from: Pos(range, 0), to: null};
 else if (range.from == null) range = {from: range, to: null};
 if (!range.to) range.to = range.from;
 if (!margin) margin = 0;

 var coords = range;
 if (range.from.line != null) {
 this.curOp.scrollToPos = {from: range.from, to: range.to, margin: margin};
 coords = {from: cursorCoords(this, range.from),
 to: cursorCoords(this, range.to)};
 }
 var sPos = calculateScrollPos(this, Math.min(coords.from.left, coords.to.left),
 Math.min(coords.from.top, coords.to.top) - margin,
 Math.max(coords.from.right, coords.to.right),
 Math.max(coords.from.bottom, coords.to.bottom) + margin);
 updateScrollPos(this, sPos.scrollLeft, sPos.scrollTop);
 }),

 setSize: operation(null, function(width, height) {
 function interpret(val) {
 return typeof val == "number" || /^\d+$/.test(String(val)) ? val + "px" : val;
 }
 if (width != null) this.display.wrapper.style.width = interpret(width);
 if (height != null) this.display.wrapper.style.height = interpret(height);
 if (this.options.lineWrapping)
 this.display.measureLineCache.length = this.display.measureLineCachePos = 0;
 this.curOp.forceUpdate = true;
 signal(this, "refresh", this);
 }),

 operation: function(f){return runInOp(this, f);},

 refresh: operation(null, function() {
 var oldHeight = this.display.cachedTextHeight;
 clearCaches(this);
 updateScrollPos(this, this.doc.scrollLeft, this.doc.scrollTop);
 regChange(this);
 if (oldHeight == null || Math.abs(oldHeight - textHeight(this.display)) > .5)
 estimateLineHeights(this);
 signal(this, "refresh", this);
 }),

 swapDoc: operation(null, function(doc) {
 var old = this.doc;
 old.cm = null;
 attachDoc(this, doc);
 clearCaches(this);
 resetInput(this, true);
 updateScrollPos(this, doc.scrollLeft, doc.scrollTop);
 signalLater(this, "swapDoc", this, old);
 return old;
 }),

 getInputField: function(){return this.display.input;},
 getWrapperElement: function(){return this.display.wrapper;},
 getScrollerElement: function(){return this.display.scroller;},
 getGutterElement: function(){return this.display.gutters;}
 };
 eventMixin(CodeMirror);

