 // KEYMAP DISPATCH

 function getKeyMap(val) {
 if (typeof val == "string") return keyMap[val];
 else return val;
 }

 function lookupKey(name, maps, handle) {
 function lookup(map) {
 map = getKeyMap(map);
 var found = map[name];
 if (found === false) return "stop";
 if (found != null && handle(found)) return true;
 if (map.nofallthrough) return "stop";

 var fallthrough = map.fallthrough;
 if (fallthrough == null) return false;
 if (Object.prototype.toString.call(fallthrough) != "[object Array]")
 return lookup(fallthrough);
 for (var i = 0, e = fallthrough.length; i < e; ++i) {
 var done = lookup(fallthrough[i]);
 if (done) return done;
 }
 return false;
 }

 for (var i = 0; i < maps.length; ++i) {
 var done = lookup(maps[i]);
 if (done) return done != "stop";
 }
 }
 function isModifierKey(event) {
 var name = keyNames[event.keyCode];
 return name == "Ctrl" || name == "Alt" || name == "Shift" || name == "Mod";
 }
 function keyName(event, noShift) {
 if (opera && event.keyCode == 34 && event["char"]) return false;
 var name = keyNames[event.keyCode];
 if (name == null || event.altGraphKey) return false;
 if (event.altKey) name = "Alt-" + name;
 if (flipCtrlCmd ? event.metaKey : event.ctrlKey) name = "Ctrl-" + name;
 if (flipCtrlCmd ? event.ctrlKey : event.metaKey) name = "Cmd-" + name;
 if (!noShift && event.shiftKey) name = "Shift-" + name;
 return name;
 }
 CodeMirror.lookupKey = lookupKey;
 CodeMirror.isModifierKey = isModifierKey;
 CodeMirror.keyName = keyName;

 // FROMTEXTAREA

 CodeMirror.fromTextArea = function(textarea, options) {
 if (!options) options = {};
 options.value = textarea.value;
 if (!options.tabindex && textarea.tabindex)
 options.tabindex = textarea.tabindex;
 if (!options.placeholder && textarea.placeholder)
 options.placeholder = textarea.placeholder;
 // Set autofocus to true if this textarea is focused, or if it has
 // autofocus and no other element is focused.
 if (options.autofocus == null) {
 var hasFocus = document.body;
 // doc.activeElement occasionally throws on IE
 try { hasFocus = document.activeElement; } catch(e) {}
 options.autofocus = hasFocus == textarea ||
 textarea.getAttribute("autofocus") != null && hasFocus == document.body;
 }

 function save() {textarea.value = cm.getValue();}
 if (textarea.form) {
 on(textarea.form, "submit", save);
 // Deplorable hack to make the submit method do the right thing.
 if (!options.leaveSubmitMethodAlone) {
 var form = textarea.form, realSubmit = form.submit;
 try {
 var wrappedSubmit = form.submit = function() {
 save();
 form.submit = realSubmit;
 form.submit();
 form.submit = wrappedSubmit;
 };
 } catch(e) {}
 }
 }

 textarea.style.display = "none";
 var cm = CodeMirror(function(node) {
 textarea.parentNode.insertBefore(node, textarea.nextSibling);
 }, options);
 cm.save = save;
 cm.getTextArea = function() { return textarea; };
 cm.toTextArea = function() {
 save();
 textarea.parentNode.removeChild(cm.getWrapperElement());
 textarea.style.display = "";
 if (textarea.form) {
 off(textarea.form, "submit", save);
 if (typeof textarea.form.submit == "function")
 textarea.form.submit = realSubmit;
 }
 };
 return cm;
 };

 // STRING STREAM

 // Fed to the mode parsers, provides helper functions to make
 // parsers more succinct.

 // The character stream used by a mode's parser.
 function StringStream(string, tabSize) {
 this.pos = this.start = 0;
 this.string = string;
 this.tabSize = tabSize || 8;
 this.lastColumnPos = this.lastColumnValue = 0;
 this.lineStart = 0;
 }

 StringStream.prototype = {
 eol: function() {return this.pos >= this.string.length;},
 sol: function() {return this.pos == this.lineStart;},
 peek: function() {return this.string.charAt(this.pos) || undefined;},
 next: function() {
 if (this.pos < this.string.length)
 return this.string.charAt(this.pos++);
 },
 eat: function(match) {
 var ch = this.string.charAt(this.pos);
 if (typeof match == "string") var ok = ch == match;
 else var ok = ch && (match.test ? match.test(ch) : match(ch));
 if (ok) {++this.pos; return ch;}
 },
 eatWhile: function(match) {
 var start = this.pos;
 while (this.eat(match)){}
 return this.pos > start;
 },
 eatSpace: function() {
 var start = this.pos;
 while (/[\s\u00a0]/.test(this.string.charAt(this.pos))) ++this.pos;
 return this.pos > start;
 },
 skipToEnd: function() {this.pos = this.string.length;},
 skipTo: function(ch) {
 var found = this.string.indexOf(ch, this.pos);
 if (found > -1) {this.pos = found; return true;}
 },
 backUp: function(n) {this.pos -= n;},
 column: function() {
 if (this.lastColumnPos < this.start) {
 this.lastColumnValue = countColumn(this.string, this.start, this.tabSize, this.lastColumnPos, this.lastColumnValue);
 this.lastColumnPos = this.start;
 }
 return this.lastColumnValue - (this.lineStart ? countColumn(this.string, this.lineStart, this.tabSize) : 0);
 },
 indentation: function() {
 return countColumn(this.string, null, this.tabSize) -
 (this.lineStart ? countColumn(this.string, this.lineStart, this.tabSize) : 0);
 },
 match: function(pattern, consume, caseInsensitive) {
 if (typeof pattern == "string") {
 var cased = function(str) {return caseInsensitive ? str.toLowerCase() : str;};
 var substr = this.string.substr(this.pos, pattern.length);
 if (cased(substr) == cased(pattern)) {
 if (consume !== false) this.pos += pattern.length;
 return true;
 }
 } else {
 var match = this.string.slice(this.pos).match(pattern);
 if (match && match.index > 0) return null;
 if (match && consume !== false) this.pos += match[0].length;
 return match;
 }
 },
 current: function(){return this.string.slice(this.start, this.pos);},
 hideFirstChars: function(n, inner) {
 this.lineStart += n;
 try { return inner(); }
 finally { this.lineStart -= n; }
 }
 };
 CodeMirror.StringStream = StringStream;

 // TEXTMARKERS

 function TextMarker(doc, type) {
 this.lines = [];
 this.type = type;
 this.doc = doc;
 }
 CodeMirror.TextMarker = TextMarker;
 eventMixin(TextMarker);

 TextMarker.prototype.clear = function() {
 if (this.explicitlyCleared) return;
 var cm = this.doc.cm, withOp = cm && !cm.curOp;
 if (withOp) startOperation(cm);
 if (hasHandler(this, "clear")) {
 var found = this.find();
 if (found) signalLater(this, "clear", found.from, found.to);
 }
 var min = null, max = null;
 for (var i = 0; i < this.lines.length; ++i) {
 var line = this.lines[i];
 var span = getMarkedSpanFor(line.markedSpans, this);
 if (span.to != null) max = lineNo(line);
 line.markedSpans = removeMarkedSpan(line.markedSpans, span);
 if (span.from != null)
 min = lineNo(line);
 else if (this.collapsed && !lineIsHidden(this.doc, line) && cm)
 updateLineHeight(line, textHeight(cm.display));
 }
 if (cm && this.collapsed && !cm.options.lineWrapping) for (var i = 0; i < this.lines.length; ++i) {
 var visual = visualLine(cm.doc, this.lines[i]), len = lineLength(cm.doc, visual);
 if (len > cm.display.maxLineLength) {
 cm.display.maxLine = visual;
 cm.display.maxLineLength = len;
 cm.display.maxLineChanged = true;
 }
 }

 if (min != null && cm) regChange(cm, min, max + 1);
 this.lines.length = 0;
 this.explicitlyCleared = true;
 if (this.atomic && this.doc.cantEdit) {
 this.doc.cantEdit = false;
 if (cm) reCheckSelection(cm);
 }
 if (withOp) endOperation(cm);
 };

 TextMarker.prototype.find = function(bothSides) {
 var from, to;
 for (var i = 0; i < this.lines.length; ++i) {
 var line = this.lines[i];
 var span = getMarkedSpanFor(line.markedSpans, this);
 if (span.from != null || span.to != null) {
 var found = lineNo(line);
 if (span.from != null) from = Pos(found, span.from);
 if (span.to != null) to = Pos(found, span.to);
 }
 }
 if (this.type == "bookmark" && !bothSides) return from;
 return from && {from: from, to: to};
 };

 TextMarker.prototype.changed = function() {
 var pos = this.find(), cm = this.doc.cm;
 if (!pos || !cm) return;
 if (this.type != "bookmark") pos = pos.from;
 var line = getLine(this.doc, pos.line);
 clearCachedMeasurement(cm, line);
 if (pos.line >= cm.display.showingFrom && pos.line < cm.display.showingTo) {
 for (var node = cm.display.lineDiv.firstChild; node; node = node.nextSibling) if (node.lineObj == line) {
 if (node.offsetHeight != line.height) updateLineHeight(line, node.offsetHeight);
 break;
 }
 runInOp(cm, function() {
 cm.curOp.selectionChanged = cm.curOp.forceUpdate = cm.curOp.updateMaxLine = true;
 });
 }
 };

 TextMarker.prototype.attachLine = function(line) {
 if (!this.lines.length && this.doc.cm) {
 var op = this.doc.cm.curOp;
 if (!op.maybeHiddenMarkers || indexOf(op.maybeHiddenMarkers, this) == -1)
 (op.maybeUnhiddenMarkers || (op.maybeUnhiddenMarkers = [])).push(this);
 }
 this.lines.push(line);
 };
 TextMarker.prototype.detachLine = function(line) {
 this.lines.splice(indexOf(this.lines, line), 1);
 if (!this.lines.length && this.doc.cm) {
 var op = this.doc.cm.curOp;
 (op.maybeHiddenMarkers || (op.maybeHiddenMarkers = [])).push(this);
 }
 };

 var nextMarkerId = 0;

 function markText(doc, from, to, options, type) {
 if (options && options.shared) return markTextShared(doc, from, to, options, type);
 if (doc.cm && !doc.cm.curOp) return operation(doc.cm, markText)(doc, from, to, options, type);

 var marker = new TextMarker(doc, type);
 if (options) copyObj(options, marker);
 if (posLess(to, from) || posEq(from, to) && marker.clearWhenEmpty !== false)
 return marker;
 if (marker.replacedWith) {
 marker.collapsed = true;
 marker.replacedWith = elt("span", [marker.replacedWith], "CodeMirror-widget");
 if (!options.handleMouseEvents) marker.replacedWith.ignoreEvents = true;
 }
 if (marker.collapsed) {
 if (conflictingCollapsedRange(doc, from.line, from, to, marker) ||
 from.line != to.line && conflictingCollapsedRange(doc, to.line, from, to, marker))
 throw new Error("Inserting collapsed marker partially overlapping an existing one");
 sawCollapsedSpans = true;
 }

 if (marker.addToHistory)
 addToHistory(doc, {from: from, to: to, origin: "markText"},
 {head: doc.sel.head, anchor: doc.sel.anchor}, NaN);

 var curLine = from.line, cm = doc.cm, updateMaxLine;
 doc.iter(curLine, to.line + 1, function(line) {
 if (cm && marker.collapsed && !cm.options.lineWrapping && visualLine(doc, line) == cm.display.maxLine)
 updateMaxLine = true;
 var span = {from: null, to: null, marker: marker};
 if (curLine == from.line) span.from = from.ch;
 if (curLine == to.line) span.to = to.ch;
 if (marker.collapsed && curLine != from.line) updateLineHeight(line, 0);
 addMarkedSpan(line, span);
 ++curLine;
 });
 if (marker.collapsed) doc.iter(from.line, to.line + 1, function(line) {
 if (lineIsHidden(doc, line)) updateLineHeight(line, 0);
 });

 if (marker.clearOnEnter) on(marker, "beforeCursorEnter", function() { marker.clear(); });

 if (marker.readOnly) {
 sawReadOnlySpans = true;
 if (doc.history.done.length || doc.history.undone.length)
 doc.clearHistory();
 }
 if (marker.collapsed) {
 marker.id = ++nextMarkerId;
 marker.atomic = true;
 }
 if (cm) {
 if (updateMaxLine) cm.curOp.updateMaxLine = true;
 if (marker.className || marker.title || marker.startStyle || marker.endStyle || marker.collapsed)
 regChange(cm, from.line, to.line + 1);
 if (marker.atomic) reCheckSelection(cm);
 }
 return marker;
 }

 // SHARED TEXTMARKERS

 function SharedTextMarker(markers, primary) {
 this.markers = markers;
 this.primary = primary;
 for (var i = 0, me = this; i < markers.length; ++i) {
 markers[i].parent = this;
 on(markers[i], "clear", function(){me.clear();});
 }
 }
 CodeMirror.SharedTextMarker = SharedTextMarker;
 eventMixin(SharedTextMarker);

 SharedTextMarker.prototype.clear = function() {
 if (this.explicitlyCleared) return;
 this.explicitlyCleared = true;
 for (var i = 0; i < this.markers.length; ++i)
 this.markers[i].clear();
 signalLater(this, "clear");
 };
 SharedTextMarker.prototype.find = function() {
 return this.primary.find();
 };

 function markTextShared(doc, from, to, options, type) {
 options = copyObj(options);
 options.shared = false;
 var markers = [markText(doc, from, to, options, type)], primary = markers[0];
 var widget = options.replacedWith;
 linkedDocs(doc, function(doc) {
 if (widget) options.replacedWith = widget.cloneNode(true);
 markers.push(markText(doc, clipPos(doc, from), clipPos(doc, to), options, type));
 for (var i = 0; i < doc.linked.length; ++i)
 if (doc.linked[i].isParent) return;
 primary = lst(markers);
 });
 return new SharedTextMarker(markers, primary);
 }

 // TEXTMARKER SPANS

 function getMarkedSpanFor(spans, marker) {
 if (spans) for (var i = 0; i < spans.length; ++i) {
 var span = spans[i];
 if (span.marker == marker) return span;
 }
 }
 function removeMarkedSpan(spans, span) {
 for (var r, i = 0; i < spans.length; ++i)
 if (spans[i] != span) (r || (r = [])).push(spans[i]);
 return r;
 }
 function addMarkedSpan(line, span) {
 line.markedSpans = line.markedSpans ? line.markedSpans.concat([span]) : [span];
 span.marker.attachLine(line);
 }

 function markedSpansBefore(old, startCh, isInsert) {
 if (old) for (var i = 0, nw; i < old.length; ++i) {
 var span = old[i], marker = span.marker;
 var startsBefore = span.from == null || (marker.inclusiveLeft ? span.from <= startCh : span.from < startCh);
 if (startsBefore || span.from == startCh && marker.type == "bookmark" && (!isInsert || !span.marker.insertLeft)) {
 var endsAfter = span.to == null || (marker.inclusiveRight ? span.to >= startCh : span.to > startCh);
 (nw || (nw = [])).push({from: span.from,
 to: endsAfter ? null : span.to,
 marker: marker});
 }
 }
 return nw;
 }

 function markedSpansAfter(old, endCh, isInsert) {
 if (old) for (var i = 0, nw; i < old.length; ++i) {
 var span = old[i], marker = span.marker;
 var endsAfter = span.to == null || (marker.inclusiveRight ? span.to >= endCh : span.to > endCh);
 if (endsAfter || span.from == endCh && marker.type == "bookmark" && (!isInsert || span.marker.insertLeft)) {
 var startsBefore = span.from == null || (marker.inclusiveLeft ? span.from <= endCh : span.from < endCh);
 (nw || (nw = [])).push({from: startsBefore ? null : span.from - endCh,
 to: span.to == null ? null : span.to - endCh,
 marker: marker});
 }
 }
 return nw;
 }

 function stretchSpansOverChange(doc, change) {
 var oldFirst = isLine(doc, change.from.line) && getLine(doc, change.from.line).markedSpans;
 var oldLast = isLine(doc, change.to.line) && getLine(doc, change.to.line).markedSpans;
 if (!oldFirst && !oldLast) return null;

 var startCh = change.from.ch, endCh = change.to.ch, isInsert = posEq(change.from, change.to);
 // Get the spans that 'stick out' on both sides
 var first = markedSpansBefore(oldFirst, startCh, isInsert);
 var last = markedSpansAfter(oldLast, endCh, isInsert);

 // Next, merge those two ends
 var sameLine = change.text.length == 1, offset = lst(change.text).length + (sameLine ? startCh : 0);
 if (first) {
 // Fix up .to properties of first
 for (var i = 0; i < first.length; ++i) {
 var span = first[i];
 if (span.to == null) {
 var found = getMarkedSpanFor(last, span.marker);
 if (!found) span.to = startCh;
 else if (sameLine) span.to = found.to == null ? null : found.to + offset;
 }
 }
 }
 if (last) {
 // Fix up .from in last (or move them into first in case of sameLine)
 for (var i = 0; i < last.length; ++i) {
 var span = last[i];
 if (span.to != null) span.to += offset;
 if (span.from == null) {
 var found = getMarkedSpanFor(first, span.marker);
 if (!found) {
 span.from = offset;
 if (sameLine) (first || (first = [])).push(span);
 }
 } else {
 span.from += offset;
 if (sameLine) (first || (first = [])).push(span);
 }
 }
 }
 // Make sure we didn't create any zero-length spans
 if (first) first = clearEmptySpans(first);
 if (last && last != first) last = clearEmptySpans(last);

 var newMarkers = [first];
 if (!sameLine) {
 // Fill gap with whole-line-spans
 var gap = change.text.length - 2, gapMarkers;
 if (gap > 0 && first)
 for (var i = 0; i < first.length; ++i)
 if (first[i].to == null)
 (gapMarkers || (gapMarkers = [])).push({from: null, to: null, marker: first[i].marker});
 for (var i = 0; i < gap; ++i)
 newMarkers.push(gapMarkers);
 newMarkers.push(last);
 }
 return newMarkers;
 }

 function clearEmptySpans(spans) {
 for (var i = 0; i < spans.length; ++i) {
 var span = spans[i];
 if (span.from != null && span.from == span.to && span.marker.clearWhenEmpty !== false)
 spans.splice(i--, 1);
 }
 if (!spans.length) return null;
 return spans;
 }

 function mergeOldSpans(doc, change) {
 var old = getOldSpans(doc, change);
 var stretched = stretchSpansOverChange(doc, change);
 if (!old) return stretched;
 if (!stretched) return old;

 for (var i = 0; i < old.length; ++i) {
 var oldCur = old[i], stretchCur = stretched[i];
 if (oldCur && stretchCur) {
 spans: for (var j = 0; j < stretchCur.length; ++j) {
 var span = stretchCur[j];
 for (var k = 0; k < oldCur.length; ++k)
 if (oldCur[k].marker == span.marker) continue spans;
 oldCur.push(span);
 }
 } else if (stretchCur) {
 old[i] = stretchCur;
 }
 }
 return old;
 }

 function removeReadOnlyRanges(doc, from, to) {
 var markers = null;
 doc.iter(from.line, to.line + 1, function(line) {
 if (line.markedSpans) for (var i = 0; i < line.markedSpans.length; ++i) {
 var mark = line.markedSpans[i].marker;
 if (mark.readOnly && (!markers || indexOf(markers, mark) == -1))
 (markers || (markers = [])).push(mark);
 }
 });
 if (!markers) return null;
 var parts = [{from: from, to: to}];
 for (var i = 0; i < markers.length; ++i) {
 var mk = markers[i], m = mk.find();
 for (var j = 0; j < parts.length; ++j) {
 var p = parts[j];
 if (posLess(p.to, m.from) || posLess(m.to, p.from)) continue;
 var newParts = [j, 1];
 if (posLess(p.from, m.from) || !mk.inclusiveLeft && posEq(p.from, m.from))
 newParts.push({from: p.from, to: m.from});
 if (posLess(m.to, p.to) || !mk.inclusiveRight && posEq(p.to, m.to))
 newParts.push({from: m.to, to: p.to});
 parts.splice.apply(parts, newParts);
 j += newParts.length - 1;
 }
 }
 return parts;
 }

 function extraLeft(marker) { return marker.inclusiveLeft ? -1 : 0; }
 function extraRight(marker) { return marker.inclusiveRight ? 1 : 0; }

 function compareCollapsedMarkers(a, b) {
 var lenDiff = a.lines.length - b.lines.length;
 if (lenDiff != 0) return lenDiff;
 var aPos = a.find(), bPos = b.find();
 var fromCmp = cmp(aPos.from, bPos.from) || extraLeft(a) - extraLeft(b);
 if (fromCmp) return -fromCmp;
 var toCmp = cmp(aPos.to, bPos.to) || extraRight(a) - extraRight(b);
 if (toCmp) return toCmp;
 return b.id - a.id;
 }

 function collapsedSpanAtSide(line, start) {
 var sps = sawCollapsedSpans && line.markedSpans, found;
 if (sps) for (var sp, i = 0; i < sps.length; ++i) {
 sp = sps[i];
 if (sp.marker.collapsed && (start ? sp.from : sp.to) == null &&
 (!found || compareCollapsedMarkers(found, sp.marker) < 0))
 found = sp.marker;
 }
 return found;
 }
 function collapsedSpanAtStart(line) { return collapsedSpanAtSide(line, true); }
 function collapsedSpanAtEnd(line) { return collapsedSpanAtSide(line, false); }

 function conflictingCollapsedRange(doc, lineNo, from, to, marker) {
 var line = getLine(doc, lineNo);
 var sps = sawCollapsedSpans && line.markedSpans;
 if (sps) for (var i = 0; i < sps.length; ++i) {
 var sp = sps[i];
 if (!sp.marker.collapsed) continue;
 var found = sp.marker.find(true);
 var fromCmp = cmp(found.from, from) || extraLeft(sp.marker) - extraLeft(marker);
 var toCmp = cmp(found.to, to) || extraRight(sp.marker) - extraRight(marker);
 if (fromCmp >= 0 && toCmp <= 0 || fromCmp <= 0 && toCmp >= 0) continue;
 if (fromCmp <= 0 && (cmp(found.to, from) || extraRight(sp.marker) - extraLeft(marker)) > 0 ||
 fromCmp >= 0 && (cmp(found.from, to) || extraLeft(sp.marker) - extraRight(marker)) < 0)
 return true;
 }
 }

 function visualLine(doc, line) {
 var merged;
 while (merged = collapsedSpanAtStart(line))
 line = getLine(doc, merged.find().from.line);
 return line;
 }

 function lineIsHidden(doc, line) {
 var sps = sawCollapsedSpans && line.markedSpans;
 if (sps) for (var sp, i = 0; i < sps.length; ++i) {
 sp = sps[i];
 if (!sp.marker.collapsed) continue;
 if (sp.from == null) return true;
 if (sp.marker.replacedWith) continue;
 if (sp.from == 0 && sp.marker.inclusiveLeft && lineIsHiddenInner(doc, line, sp))
 return true;
 }
 }
 function lineIsHiddenInner(doc, line, span) {
 if (span.to == null) {
 var end = span.marker.find().to, endLine = getLine(doc, end.line);
 return lineIsHiddenInner(doc, endLine, getMarkedSpanFor(endLine.markedSpans, span.marker));
 }
 if (span.marker.inclusiveRight && span.to == line.text.length)
 return true;
 for (var sp, i = 0; i < line.markedSpans.length; ++i) {
 sp = line.markedSpans[i];
 if (sp.marker.collapsed && !sp.marker.replacedWith && sp.from == span.to &&
 (sp.to == null || sp.to != span.from) &&
 (sp.marker.inclusiveLeft || span.marker.inclusiveRight) &&
 lineIsHiddenInner(doc, line, sp)) return true;
 }
 }

 function detachMarkedSpans(line) {
 var spans = line.markedSpans;
 if (!spans) return;
 for (var i = 0; i < spans.length; ++i)
 spans[i].marker.detachLine(line);
 line.markedSpans = null;
 }

 function attachMarkedSpans(line, spans) {
 if (!spans) return;
 for (var i = 0; i < spans.length; ++i)
 spans[i].marker.attachLine(line);
 line.markedSpans = spans;
 }

 // LINE WIDGETS

 var LineWidget = CodeMirror.LineWidget = function(cm, node, options) {
 if (options) for (var opt in options) if (options.hasOwnProperty(opt))
 this[opt] = options[opt];
 this.cm = cm;
 this.node = node;
 };
 eventMixin(LineWidget);
 function widgetOperation(f) {
 return function() {
 var withOp = !this.cm.curOp;
 if (withOp) startOperation(this.cm);
 try {var result = f.apply(this, arguments);}
 finally {if (withOp) endOperation(this.cm);}
 return result;
 };
 }
 LineWidget.prototype.clear = widgetOperation(function() {
 var ws = this.line.widgets, no = lineNo(this.line);
 if (no == null || !ws) return;
 for (var i = 0; i < ws.length; ++i) if (ws[i] == this) ws.splice(i--, 1);
 if (!ws.length) this.line.widgets = null;
 var aboveVisible = heightAtLine(this.cm, this.line) < this.cm.doc.scrollTop;
 updateLineHeight(this.line, Math.max(0, this.line.height - widgetHeight(this)));
 if (aboveVisible) addToScrollPos(this.cm, 0, -this.height);
 regChange(this.cm, no, no + 1);
 });
 LineWidget.prototype.changed = widgetOperation(function() {
 var oldH = this.height;
 this.height = null;
 var diff = widgetHeight(this) - oldH;
 if (!diff) return;
 updateLineHeight(this.line, this.line.height + diff);
 var no = lineNo(this.line);
 regChange(this.cm, no, no + 1);
 });

 function widgetHeight(widget) {
 if (widget.height != null) return widget.height;
 if (!widget.node.parentNode || widget.node.parentNode.nodeType != 1)
 removeChildrenAndAdd(widget.cm.display.measure, elt("div", [widget.node], null, "position: relative"));
 return widget.height = widget.node.offsetHeight;
 }

 function addLineWidget(cm, handle, node, options) {
 var widget = new LineWidget(cm, node, options);
 if (widget.noHScroll) cm.display.alignWidgets = true;
 changeLine(cm, handle, function(line) {
 var widgets = line.widgets || (line.widgets = []);
 if (widget.insertAt == null) widgets.push(widget);
 else widgets.splice(Math.min(widgets.length - 1, Math.max(0, widget.insertAt)), 0, widget);
 widget.line = line;
 if (!lineIsHidden(cm.doc, line) || widget.showIfHidden) {
 var aboveVisible = heightAtLine(cm, line) < cm.doc.scrollTop;
 updateLineHeight(line, line.height + widgetHeight(widget));
 if (aboveVisible) addToScrollPos(cm, 0, widget.height);
 cm.curOp.forceUpdate = true;
 }
 return true;
 });
 return widget;
 }

 // LINE DATA STRUCTURE

 // Line objects. These hold state related to a line, including
 // highlighting info (the styles array).
 var Line = CodeMirror.Line = function(text, markedSpans, estimateHeight) {
 this.text = text;
 attachMarkedSpans(this, markedSpans);
 this.height = estimateHeight ? estimateHeight(this) : 1;
 };
 eventMixin(Line);
 Line.prototype.lineNo = function() { return lineNo(this); };

 function updateLine(line, text, markedSpans, estimateHeight) {
 line.text = text;
 if (line.stateAfter) line.stateAfter = null;
 if (line.styles) line.styles = null;
 if (line.order != null) line.order = null;
 detachMarkedSpans(line);
 attachMarkedSpans(line, markedSpans);
 var estHeight = estimateHeight ? estimateHeight(line) : 1;
 if (estHeight != line.height) updateLineHeight(line, estHeight);
 }

 function cleanUpLine(line) {
 line.parent = null;
 detachMarkedSpans(line);
 }

 // Run the given mode's parser over a line, update the styles
 // array, which contains alternating fragments of text and CSS
 // classes.
 function runMode(cm, text, mode, state, f, forceToEnd) {
 var flattenSpans = mode.flattenSpans;
 if (flattenSpans == null) flattenSpans = cm.options.flattenSpans;
 var curStart = 0, curStyle = null;
 var stream = new StringStream(text, cm.options.tabSize), style;
 if (text == "" && mode.blankLine) mode.blankLine(state);
 while (!stream.eol()) {
 if (stream.pos > cm.options.maxHighlightLength) {
 flattenSpans = false;
 if (forceToEnd) processLine(cm, text, state, stream.pos);
 stream.pos = text.length;
 style = null;
 } else {
 style = mode.token(stream, state);
 }
 if (cm.options.addModeClass) {
 var mName = CodeMirror.innerMode(mode, state).mode.name;
 if (mName) style = "m-" + (style ? mName + " " + style : mName);
 }
 if (!flattenSpans || curStyle != style) {
 if (curStart < stream.start) f(stream.start, curStyle);
 curStart = stream.start; curStyle = style;
 }
 stream.start = stream.pos;
 }
 while (curStart < stream.pos) {
 // Webkit seems to refuse to render text nodes longer than 57444 characters
 var pos = Math.min(stream.pos, curStart + 50000);
 f(pos, curStyle);
 curStart = pos;
 }
 }

 function highlightLine(cm, line, state, forceToEnd) {
 // A styles array always starts with a number identifying the
 // mode/overlays that it is based on (for easy invalidation).
 var st = [cm.state.modeGen];
 // Compute the base array of styles
 runMode(cm, line.text, cm.doc.mode, state, function(end, style) {
 st.push(end, style);
 }, forceToEnd);

 // Run overlays, adjust style array.
 for (var o = 0; o < cm.state.overlays.length; ++o) {
 var overlay = cm.state.overlays[o], i = 1, at = 0;
 runMode(cm, line.text, overlay.mode, true, function(end, style) {
 var start = i;
 // Ensure there's a token end at the current position, and that i points at it
 while (at < end) {
 var i_end = st[i];
 if (i_end > end)
 st.splice(i, 1, end, st[i+1], i_end);
 i += 2;
 at = Math.min(end, i_end);
 }
 if (!style) return;
 if (overlay.opaque) {
 st.splice(start, i - start, end, style);
 i = start + 2;
 } else {
 for (; start < i; start += 2) {
 var cur = st[start+1];
 st[start+1] = cur ? cur + " " + style : style;
 }
 }
 });
 }

 return st;
 }

 function getLineStyles(cm, line) {
 if (!line.styles || line.styles[0] != cm.state.modeGen)
 line.styles = highlightLine(cm, line, line.stateAfter = getStateBefore(cm, lineNo(line)));
 return line.styles;
 }

 // Lightweight form of highlight -- proceed over this line and
 // update state, but don't save a style array.
 function processLine(cm, text, state, startAt) {
 var mode = cm.doc.mode;
 var stream = new StringStream(text, cm.options.tabSize);
 stream.start = stream.pos = startAt || 0;
 if (text == "" && mode.blankLine) mode.blankLine(state);
 while (!stream.eol() && stream.pos <= cm.options.maxHighlightLength) {
 mode.token(stream, state);
 stream.start = stream.pos;
 }
 }

 var styleToClassCache = {}, styleToClassCacheWithMode = {};
 function interpretTokenStyle(style, builder) {
 if (!style) return null;
 for (;;) {
 var lineClass = style.match(/(?:^|\s+)line-(background-)?(\S+)/);
 if (!lineClass) break;
 style = style.slice(0, lineClass.index) + style.slice(lineClass.index + lineClass[0].length);
 var prop = lineClass[1] ? "bgClass" : "textClass";
 if (builder[prop] == null)
 builder[prop] = lineClass[2];
 else if (!(new RegExp("(?:^|\s)" + lineClass[2] + "(?:$|\s)")).test(builder[prop]))
 builder[prop] += " " + lineClass[2];
 }
 if (/^\s*$/.test(style)) return null;
 var cache = builder.cm.options.addModeClass ? styleToClassCacheWithMode : styleToClassCache;
 return cache[style] ||
 (cache[style] = style.replace(/\S+/g, "cm-$&"));
 }

 function buildLineContent(cm, realLine, measure, copyWidgets) {
 var merged, line = realLine, empty = true;
 while (merged = collapsedSpanAtStart(line))
 line = getLine(cm.doc, merged.find().from.line);

 var builder = {pre: elt("pre"), col: 0, pos: 0,
 measure: null, measuredSomething: false, cm: cm,
 copyWidgets: copyWidgets};

 do {
 if (line.text) empty = false;
 builder.measure = line == realLine && measure;
 builder.pos = 0;
 builder.addToken = builder.measure ? buildTokenMeasure : buildToken;
 if ((ie || webkit) && cm.getOption("lineWrapping"))
 builder.addToken = buildTokenSplitSpaces(builder.addToken);
 var next = insertLineContent(line, builder, getLineStyles(cm, line));
 if (measure && line == realLine && !builder.measuredSomething) {
 measure[0] = builder.pre.appendChild(zeroWidthElement(cm.display.measure));
 builder.measuredSomething = true;
 }
 if (next) line = getLine(cm.doc, next.to.line);
 } while (next);

 if (measure && !builder.measuredSomething && !measure[0])
 measure[0] = builder.pre.appendChild(empty ? elt("span", "\u00a0") : zeroWidthElement(cm.display.measure));
 if (!builder.pre.firstChild && !lineIsHidden(cm.doc, realLine))
 builder.pre.appendChild(document.createTextNode("\u00a0"));

 var order;
 // Work around problem with the reported dimensions of single-char
 // direction spans on IE (issue #1129). See also the comment in
 // cursorCoords.
 if (measure && ie && (order = getOrder(line))) {
 var l = order.length - 1;
 if (order[l].from == order[l].to) --l;
 var last = order[l], prev = order[l - 1];
 if (last.from + 1 == last.to && prev && last.level < prev.level) {
 var span = measure[builder.pos - 1];
 if (span) span.parentNode.insertBefore(span.measureRight = zeroWidthElement(cm.display.measure),
 span.nextSibling);
 }
 }

 var textClass = builder.textClass ? builder.textClass + " " + (realLine.textClass || "") : realLine.textClass;
 if (textClass) builder.pre.className = textClass;

 signal(cm, "renderLine", cm, realLine, builder.pre);
 return builder;
 }

 function defaultSpecialCharPlaceholder(ch) {
 var token = elt("span", "\u2022", "cm-invalidchar");
 token.title = "\\u" + ch.charCodeAt(0).toString(16);
 return token;
 }

 function buildToken(builder, text, style, startStyle, endStyle, title) {
 if (!text) return;
 var special = builder.cm.options.specialChars;
 if (!special.test(text)) {
 builder.col += text.length;
 var content = document.createTextNode(text);
 } else {
 var content = document.createDocumentFragment(), pos = 0;
 while (true) {
 special.lastIndex = pos;
 var m = special.exec(text);
 var skipped = m ? m.index - pos : text.length - pos;
 if (skipped) {
 content.appendChild(document.createTextNode(text.slice(pos, pos + skipped)));
 builder.col += skipped;
 }
 if (!m) break;
 pos += skipped + 1;
 if (m[0] == "\t") {
 var tabSize = builder.cm.options.tabSize, tabWidth = tabSize - builder.col % tabSize;
 content.appendChild(elt("span", spaceStr(tabWidth), "cm-tab"));
 builder.col += tabWidth;
 } else {
 var token = builder.cm.options.specialCharPlaceholder(m[0]);
 content.appendChild(token);
 builder.col += 1;
 }
 }
 }
 if (style || startStyle || endStyle || builder.measure) {
 var fullStyle = style || "";
 if (startStyle) fullStyle += startStyle;
 if (endStyle) fullStyle += endStyle;
 var token = elt("span", [content], fullStyle);
 if (title) token.title = title;
 return builder.pre.appendChild(token);
 }
 builder.pre.appendChild(content);
 }

 function buildTokenMeasure(builder, text, style, startStyle, endStyle) {
 var wrapping = builder.cm.options.lineWrapping;
 for (var i = 0; i < text.length; ++i) {
 var start = i == 0, to = i + 1;
 while (to < text.length && isExtendingChar(text.charAt(to))) ++to;
 var ch = text.slice(i, to);
 i = to - 1;
 if (i && wrapping && spanAffectsWrapping(text, i))
 builder.pre.appendChild(elt("wbr"));
 var old = builder.measure[builder.pos];
 var span = builder.measure[builder.pos] =
 buildToken(builder, ch, style,
 start && startStyle, i == text.length - 1 && endStyle);
 if (old) span.leftSide = old.leftSide || old;
 // In IE single-space nodes wrap differently than spaces
 // embedded in larger text nodes, except when set to
 // white-space: normal (issue #1268).
 if (old_ie && wrapping && ch == " " && i && !/\s/.test(text.charAt(i - 1)) &&
 i < text.length - 1 && !/\s/.test(text.charAt(i + 1)))
 span.style.whiteSpace = "normal";
 builder.pos += ch.length;
 }
 if (text.length) builder.measuredSomething = true;
 }

 function buildTokenSplitSpaces(inner) {
 function split(old) {
 var out = " ";
 for (var i = 0; i < old.length - 2; ++i) out += i % 2 ? " " : "\u00a0";
 out += " ";
 return out;
 }
 return function(builder, text, style, startStyle, endStyle, title) {
 return inner(builder, text.replace(/ {3,}/g, split), style, startStyle, endStyle, title);
 };
 }

 function buildCollapsedSpan(builder, size, marker, ignoreWidget) {
 var widget = !ignoreWidget && marker.replacedWith;
 if (widget) {
 if (builder.copyWidgets) widget = widget.cloneNode(true);
 builder.pre.appendChild(widget);
 if (builder.measure) {
 if (size) {
 builder.measure[builder.pos] = widget;
 } else {
 var elt = zeroWidthElement(builder.cm.display.measure);
 if (marker.type == "bookmark" && !marker.insertLeft)
 builder.measure[builder.pos] = builder.pre.appendChild(elt);
 else if (builder.measure[builder.pos])
 return;
 else
 builder.measure[builder.pos] = builder.pre.insertBefore(elt, widget);
 }
 builder.measuredSomething = true;
 }
 }
 builder.pos += size;
 }

 // Outputs a number of spans to make up a line, taking highlighting
 // and marked text into account.
 function insertLineContent(line, builder, styles) {
 var spans = line.markedSpans, allText = line.text, at = 0;
 if (!spans) {
 for (var i = 1; i < styles.length; i+=2)
 builder.addToken(builder, allText.slice(at, at = styles[i]), interpretTokenStyle(styles[i+1], builder));
 return;
 }

 var len = allText.length, pos = 0, i = 1, text = "", style;
 var nextChange = 0, spanStyle, spanEndStyle, spanStartStyle, title, collapsed;
 for (;;) {
 if (nextChange == pos) { // Update current marker set
 spanStyle = spanEndStyle = spanStartStyle = title = "";
 collapsed = null; nextChange = Infinity;
 var foundBookmarks = [];
 for (var j = 0; j < spans.length; ++j) {
 var sp = spans[j], m = sp.marker;
 if (sp.from <= pos && (sp.to == null || sp.to > pos)) {
 if (sp.to != null && nextChange > sp.to) { nextChange = sp.to; spanEndStyle = ""; }
 if (m.className) spanStyle += " " + m.className;
 if (m.startStyle && sp.from == pos) spanStartStyle += " " + m.startStyle;
 if (m.endStyle && sp.to == nextChange) spanEndStyle += " " + m.endStyle;
 if (m.title && !title) title = m.title;
 if (m.collapsed && (!collapsed || compareCollapsedMarkers(collapsed.marker, m) < 0))
 collapsed = sp;
 } else if (sp.from > pos && nextChange > sp.from) {
 nextChange = sp.from;
 }
 if (m.type == "bookmark" && sp.from == pos && m.replacedWith) foundBookmarks.push(m);
 }
 if (collapsed && (collapsed.from || 0) == pos) {
 buildCollapsedSpan(builder, (collapsed.to == null ? len : collapsed.to) - pos,
 collapsed.marker, collapsed.from == null);
 if (collapsed.to == null) return collapsed.marker.find();
 }
 if (!collapsed && foundBookmarks.length) for (var j = 0; j < foundBookmarks.length; ++j)
 buildCollapsedSpan(builder, 0, foundBookmarks[j]);
 }
 if (pos >= len) break;

 var upto = Math.min(len, nextChange);
 while (true) {
 if (text) {
 var end = pos + text.length;
 if (!collapsed) {
 var tokenText = end > upto ? text.slice(0, upto - pos) : text;
 builder.addToken(builder, tokenText, style ? style + spanStyle : spanStyle,
 spanStartStyle, pos + tokenText.length == nextChange ? spanEndStyle : "", title);
 }
 if (end >= upto) {text = text.slice(upto - pos); pos = upto; break;}
 pos = end;
 spanStartStyle = "";
 }
 text = allText.slice(at, at = styles[i++]);
 style = interpretTokenStyle(styles[i++], builder);
 }
 }
 }

 // DOCUMENT DATA STRUCTURE

 function updateDoc(doc, change, markedSpans, selAfter, estimateHeight) {
 function spansFor(n) {return markedSpans ? markedSpans[n] : null;}
 function update(line, text, spans) {
 updateLine(line, text, spans, estimateHeight);
 signalLater(line, "change", line, change);
 }

 var from = change.from, to = change.to, text = change.text;
 var firstLine = getLine(doc, from.line), lastLine = getLine(doc, to.line);
 var lastText = lst(text), lastSpans = spansFor(text.length - 1), nlines = to.line - from.line;

 // First adjust the line structure
 if (from.ch == 0 && to.ch == 0 && lastText == "" &&
 (!doc.cm || doc.cm.options.wholeLineUpdateBefore)) {
 // This is a whole-line replace. Treated specially to make
 // sure line objects move the way they are supposed to.
 for (var i = 0, e = text.length - 1, added = []; i < e; ++i)
 added.push(new Line(text[i], spansFor(i), estimateHeight));
 update(lastLine, lastLine.text, lastSpans);
 if (nlines) doc.remove(from.line, nlines);
 if (added.length) doc.insert(from.line, added);
 } else if (firstLine == lastLine) {
 if (text.length == 1) {
 update(firstLine, firstLine.text.slice(0, from.ch) + lastText + firstLine.text.slice(to.ch), lastSpans);
 } else {
 for (var added = [], i = 1, e = text.length - 1; i < e; ++i)
 added.push(new Line(text[i], spansFor(i), estimateHeight));
 added.push(new Line(lastText + firstLine.text.slice(to.ch), lastSpans, estimateHeight));
 update(firstLine, firstLine.text.slice(0, from.ch) + text[0], spansFor(0));
 doc.insert(from.line + 1, added);
 }
 } else if (text.length == 1) {
 update(firstLine, firstLine.text.slice(0, from.ch) + text[0] + lastLine.text.slice(to.ch), spansFor(0));
 doc.remove(from.line + 1, nlines);
 } else {
 update(firstLine, firstLine.text.slice(0, from.ch) + text[0], spansFor(0));
 update(lastLine, lastText + lastLine.text.slice(to.ch), lastSpans);
 for (var i = 1, e = text.length - 1, added = []; i < e; ++i)
 added.push(new Line(text[i], spansFor(i), estimateHeight));
 if (nlines > 1) doc.remove(from.line + 1, nlines - 1);
 doc.insert(from.line + 1, added);
 }

 signalLater(doc, "change", doc, change);
 setSelection(doc, selAfter.anchor, selAfter.head, null, true);
 }

 function LeafChunk(lines) {
 this.lines = lines;
 this.parent = null;
 for (var i = 0, e = lines.length, height = 0; i < e; ++i) {
 lines[i].parent = this;
 height += lines[i].height;
 }
 this.height = height;
 }

 LeafChunk.prototype = {
 chunkSize: function() { return this.lines.length; },
 removeInner: function(at, n) {
 for (var i = at, e = at + n; i < e; ++i) {
 var line = this.lines[i];
 this.height -= line.height;
 cleanUpLine(line);
 signalLater(line, "delete");
 }
 this.lines.splice(at, n);
 },
 collapse: function(lines) {
 lines.splice.apply(lines, [lines.length, 0].concat(this.lines));
 },
 insertInner: function(at, lines, height) {
 this.height += height;
 this.lines = this.lines.slice(0, at).concat(lines).concat(this.lines.slice(at));
 for (var i = 0, e = lines.length; i < e; ++i) lines[i].parent = this;
 },
 iterN: function(at, n, op) {
 for (var e = at + n; at < e; ++at)
 if (op(this.lines[at])) return true;
 }
 };

 function BranchChunk(children) {
 this.children = children;
 var size = 0, height = 0;
 for (var i = 0, e = children.length; i < e; ++i) {
 var ch = children[i];
 size += ch.chunkSize(); height += ch.height;
 ch.parent = this;
 }
 this.size = size;
 this.height = height;
 this.parent = null;
 }

 BranchChunk.prototype = {
 chunkSize: function() { return this.size; },
 removeInner: function(at, n) {
 this.size -= n;
 for (var i = 0; i < this.children.length; ++i) {
 var child = this.children[i], sz = child.chunkSize();
 if (at < sz) {
 var rm = Math.min(n, sz - at), oldHeight = child.height;
 child.removeInner(at, rm);
 this.height -= oldHeight - child.height;
 if (sz == rm) { this.children.splice(i--, 1); child.parent = null; }
 if ((n -= rm) == 0) break;
 at = 0;
 } else at -= sz;
 }
 if (this.size - n < 25) {
 var lines = [];
 this.collapse(lines);
 this.children = [new LeafChunk(lines)];
 this.children[0].parent = this;
 }
 },
 collapse: function(lines) {
 for (var i = 0, e = this.children.length; i < e; ++i) this.children[i].collapse(lines);
 },
 insertInner: function(at, lines, height) {
 this.size += lines.length;
 this.height += height;
 for (var i = 0, e = this.children.length; i < e; ++i) {
 var child = this.children[i], sz = child.chunkSize();
 if (at <= sz) {
 child.insertInner(at, lines, height);
 if (child.lines && child.lines.length > 50) {
 while (child.lines.length > 50) {
 var spilled = child.lines.splice(child.lines.length - 25, 25);
 var newleaf = new LeafChunk(spilled);
 child.height -= newleaf.height;
 this.children.splice(i + 1, 0, newleaf);
 newleaf.parent = this;
 }
 this.maybeSpill();
 }
 break;
 }
 at -= sz;
 }
 },
 maybeSpill: function() {
 if (this.children.length <= 10) return;
 var me = this;
 do {
 var spilled = me.children.splice(me.children.length - 5, 5);
 var sibling = new BranchChunk(spilled);
 if (!me.parent) { // Become the parent node
 var copy = new BranchChunk(me.children);
 copy.parent = me;
 me.children = [copy, sibling];
 me = copy;
 } else {
 me.size -= sibling.size;
 me.height -= sibling.height;
 var myIndex = indexOf(me.parent.children, me);
 me.parent.children.splice(myIndex + 1, 0, sibling);
 }
 sibling.parent = me.parent;
 } while (me.children.length > 10);
 me.parent.maybeSpill();
 },
 iterN: function(at, n, op) {
 for (var i = 0, e = this.children.length; i < e; ++i) {
 var child = this.children[i], sz = child.chunkSize();
 if (at < sz) {
 var used = Math.min(n, sz - at);
 if (child.iterN(at, used, op)) return true;
 if ((n -= used) == 0) break;
 at = 0;
 } else at -= sz;
 }
 }
 };

 var nextDocId = 0;
 var Doc = CodeMirror.Doc = function(text, mode, firstLine) {
 if (!(this instanceof Doc)) return new Doc(text, mode, firstLine);
 if (firstLine == null) firstLine = 0;

 BranchChunk.call(this, [new LeafChunk([new Line("", null)])]);
 this.first = firstLine;
 this.scrollTop = this.scrollLeft = 0;
 this.cantEdit = false;
 this.history = makeHistory();
 this.cleanGeneration = 1;
 this.frontier = firstLine;
 var start = Pos(firstLine, 0);
 this.sel = {from: start, to: start, head: start, anchor: start, shift: false, extend: false, goalColumn: null};
 this.id = ++nextDocId;
 this.modeOption = mode;

 if (typeof text == "string") text = splitLines(text);
 updateDoc(this, {from: start, to: start, text: text}, null, {head: start, anchor: start});
 };

 Doc.prototype = createObj(BranchChunk.prototype, {
 constructor: Doc,
 iter: function(from, to, op) {
 if (op) this.iterN(from - this.first, to - from, op);
 else this.iterN(this.first, this.first + this.size, from);
 },

 insert: function(at, lines) {
 var height = 0;
 for (var i = 0, e = lines.length; i < e; ++i) height += lines[i].height;
 this.insertInner(at - this.first, lines, height);
 },
 remove: function(at, n) { this.removeInner(at - this.first, n); },

 getValue: function(lineSep) {
 var lines = getLines(this, this.first, this.first + this.size);
 if (lineSep === false) return lines;
 return lines.join(lineSep || "\n");
 },
 setValue: function(code) {
 var top = Pos(this.first, 0), last = this.first + this.size - 1;
 makeChange(this, {from: top, to: Pos(last, getLine(this, last).text.length),
 text: splitLines(code), origin: "setValue"},
 {head: top, anchor: top}, true);
 },
 replaceRange: function(code, from, to, origin) {
 from = clipPos(this, from);
 to = to ? clipPos(this, to) : from;
 replaceRange(this, code, from, to, origin);
 },
 getRange: function(from, to, lineSep) {
 var lines = getBetween(this, clipPos(this, from), clipPos(this, to));
 if (lineSep === false) return lines;
 return lines.join(lineSep || "\n");
 },

 getLine: function(line) {var l = this.getLineHandle(line); return l && l.text;},
 setLine: function(line, text) {
 if (isLine(this, line))
 replaceRange(this, text, Pos(line, 0), clipPos(this, Pos(line)));
 },
 removeLine: function(line) {
 if (line) replaceRange(this, "", clipPos(this, Pos(line - 1)), clipPos(this, Pos(line)));
 else replaceRange(this, "", Pos(0, 0), clipPos(this, Pos(1, 0)));
 },

 getLineHandle: function(line) {if (isLine(this, line)) return getLine(this, line);},
 getLineNumber: function(line) {return lineNo(line);},

 getLineHandleVisualStart: function(line) {
 if (typeof line == "number") line = getLine(this, line);
 return visualLine(this, line);
 },

 lineCount: function() {return this.size;},
 firstLine: function() {return this.first;},
 lastLine: function() {return this.first + this.size - 1;},

 clipPos: function(pos) {return clipPos(this, pos);},

 getCursor: function(start) {
 var sel = this.sel, pos;
 if (start == null || start == "head") pos = sel.head;
 else if (start == "anchor") pos = sel.anchor;
 else if (start == "end" || start === false) pos = sel.to;
 else pos = sel.from;
 return copyPos(pos);
 },
 somethingSelected: function() {return !posEq(this.sel.head, this.sel.anchor);},

 setCursor: docOperation(function(line, ch, extend) {
 var pos = clipPos(this, typeof line == "number" ? Pos(line, ch || 0) : line);
 if (extend) extendSelection(this, pos);
 else setSelection(this, pos, pos);
 }),
 setSelection: docOperation(function(anchor, head, bias) {
 setSelection(this, clipPos(this, anchor), clipPos(this, head || anchor), bias);
 }),
 extendSelection: docOperation(function(from, to, bias) {
 extendSelection(this, clipPos(this, from), to && clipPos(this, to), bias);
 }),

 getSelection: function(lineSep) {return this.getRange(this.sel.from, this.sel.to, lineSep);},
 replaceSelection: function(code, collapse, origin) {
 makeChange(this, {from: this.sel.from, to: this.sel.to, text: splitLines(code), origin: origin}, collapse || "around");
 },
 undo: docOperation(function() {makeChangeFromHistory(this, "undo");}),
 redo: docOperation(function() {makeChangeFromHistory(this, "redo");}),

 setExtending: function(val) {this.sel.extend = val;},

 historySize: function() {
 var hist = this.history;
 return {undo: hist.done.length, redo: hist.undone.length};
 },
 clearHistory: function() {this.history = makeHistory(this.history.maxGeneration);},

 markClean: function() {
 this.cleanGeneration = this.changeGeneration(true);
 },
 changeGeneration: function(forceSplit) {
 if (forceSplit)
 this.history.lastOp = this.history.lastOrigin = null;
 return this.history.generation;
 },
 isClean: function (gen) {
 return this.history.generation == (gen || this.cleanGeneration);
 },

 getHistory: function() {
 return {done: copyHistoryArray(this.history.done),
 undone: copyHistoryArray(this.history.undone)};
 },
 setHistory: function(histData) {
 var hist = this.history = makeHistory(this.history.maxGeneration);
 hist.done = histData.done.slice(0);
 hist.undone = histData.undone.slice(0);
 },

 markText: function(from, to, options) {
 return markText(this, clipPos(this, from), clipPos(this, to), options, "range");
 },
 setBookmark: function(pos, options) {
 var realOpts = {replacedWith: options && (options.nodeType == null ? options.widget : options),
 insertLeft: options && options.insertLeft,
 clearWhenEmpty: false};
 pos = clipPos(this, pos);
 return markText(this, pos, pos, realOpts, "bookmark");
 },
 findMarksAt: function(pos) {
 pos = clipPos(this, pos);
 var markers = [], spans = getLine(this, pos.line).markedSpans;
 if (spans) for (var i = 0; i < spans.length; ++i) {
 var span = spans[i];
 if ((span.from == null || span.from <= pos.ch) &&
 (span.to == null || span.to >= pos.ch))
 markers.push(span.marker.parent || span.marker);
 }
 return markers;
 },
 findMarks: function(from, to) {
 from = clipPos(this, from); to = clipPos(this, to);
 var found = [], lineNo = from.line;
 this.iter(from.line, to.line + 1, function(line) {
 var spans = line.markedSpans;
 if (spans) for (var i = 0; i < spans.length; i++) {
 var span = spans[i];
 if (!(lineNo == from.line && from.ch > span.to ||
 span.from == null && lineNo != from.line||
 lineNo == to.line && span.from > to.ch))
 found.push(span.marker.parent || span.marker);
 }
 ++lineNo;
 });
 return found;
 },
 getAllMarks: function() {
 var markers = [];
 this.iter(function(line) {
 var sps = line.markedSpans;
 if (sps) for (var i = 0; i < sps.length; ++i)
 if (sps[i].from != null) markers.push(sps[i].marker);
 });
 return markers;
 },

 posFromIndex: function(off) {
 var ch, lineNo = this.first;
 this.iter(function(line) {
 var sz = line.text.length + 1;
 if (sz > off) { ch = off; return true; }
 off -= sz;
 ++lineNo;
 });
 return clipPos(this, Pos(lineNo, ch));
 },
 indexFromPos: function (coords) {
 coords = clipPos(this, coords);
 var index = coords.ch;
 if (coords.line < this.first || coords.ch < 0) return 0;
 this.iter(this.first, coords.line, function (line) {
 index += line.text.length + 1;
 });
 return index;
 },

 copy: function(copyHistory) {
 var doc = new Doc(getLines(this, this.first, this.first + this.size), this.modeOption, this.first);
 doc.scrollTop = this.scrollTop; doc.scrollLeft = this.scrollLeft;
 doc.sel = {from: this.sel.from, to: this.sel.to, head: this.sel.head, anchor: this.sel.anchor,
 shift: this.sel.shift, extend: false, goalColumn: this.sel.goalColumn};
 if (copyHistory) {
 doc.history.undoDepth = this.history.undoDepth;
 doc.setHistory(this.getHistory());
 }
 return doc;
 },

 linkedDoc: function(options) {
 if (!options) options = {};
 var from = this.first, to = this.first + this.size;
 if (options.from != null && options.from > from) from = options.from;
 if (options.to != null && options.to < to) to = options.to;
 var copy = new Doc(getLines(this, from, to), options.mode || this.modeOption, from);
 if (options.sharedHist) copy.history = this.history;
 (this.linked || (this.linked = [])).push({doc: copy, sharedHist: options.sharedHist});
 copy.linked = [{doc: this, isParent: true, sharedHist: options.sharedHist}];
 return copy;
 },
 unlinkDoc: function(other) {
 if (other instanceof CodeMirror) other = other.doc;
 if (this.linked) for (var i = 0; i < this.linked.length; ++i) {
 var link = this.linked[i];
 if (link.doc != other) continue;
 this.linked.splice(i, 1);
 other.unlinkDoc(this);
 break;
 }
 // If the histories were shared, split them again
 if (other.history == this.history) {
 var splitIds = [other.id];
 linkedDocs(other, function(doc) {splitIds.push(doc.id);}, true);
 other.history = makeHistory();
 other.history.done = copyHistoryArray(this.history.done, splitIds);
 other.history.undone = copyHistoryArray(this.history.undone, splitIds);
 }
 },
 iterLinkedDocs: function(f) {linkedDocs(this, f);},

 getMode: function() {return this.mode;},
 getEditor: function() {return this.cm;}
 });

 Doc.prototype.eachLine = Doc.prototype.iter;

 // The Doc methods that should be available on CodeMirror instances
 var dontDelegate = "iter insert remove copy getEditor".split(" ");
 for (var prop in Doc.prototype) if (Doc.prototype.hasOwnProperty(prop) && indexOf(dontDelegate, prop) < 0)
 CodeMirror.prototype[prop] = (function(method) {
 return function() {return method.apply(this.doc, arguments);};
 })(Doc.prototype[prop]);

 eventMixin(Doc);

 function linkedDocs(doc, f, sharedHistOnly) {
 function propagate(doc, skip, sharedHist) {
 if (doc.linked) for (var i = 0; i < doc.linked.length; ++i) {
 var rel = doc.linked[i];
 if (rel.doc == skip) continue;
 var shared = sharedHist && rel.sharedHist;
 if (sharedHistOnly && !shared) continue;
 f(rel.doc, shared);
 propagate(rel.doc, doc, shared);
 }
 }
 propagate(doc, null, true);
 }

 function attachDoc(cm, doc) {
 if (doc.cm) throw new Error("This document is already in use.");
 cm.doc = doc;
 doc.cm = cm;
 estimateLineHeights(cm);
 loadMode(cm);
 if (!cm.options.lineWrapping) computeMaxLength(cm);
 cm.options.mode = doc.modeOption;
 regChange(cm);
 }

 // LINE UTILITIES

 function getLine(chunk, n) {
 n -= chunk.first;
 while (!chunk.lines) {
 for (var i = 0;; ++i) {
 var child = chunk.children[i], sz = child.chunkSize();
 if (n < sz) { chunk = child; break; }
 n -= sz;
 }
 }
 return chunk.lines[n];
 }

 function getBetween(doc, start, end) {
 var out = [], n = start.line;
 doc.iter(start.line, end.line + 1, function(line) {
 var text = line.text;
 if (n == end.line) text = text.slice(0, end.ch);
 if (n == start.line) text = text.slice(start.ch);
 out.push(text);
 ++n;
 });
 return out;
 }
 function getLines(doc, from, to) {
 var out = [];
 doc.iter(from, to, function(line) { out.push(line.text); });
 return out;
 }

 function updateLineHeight(line, height) {
 var diff = height - line.height;
 for (var n = line; n; n = n.parent) n.height += diff;
 }

 function lineNo(line) {
 if (line.parent == null) return null;
 var cur = line.parent, no = indexOf(cur.lines, line);
 for (var chunk = cur.parent; chunk; cur = chunk, chunk = chunk.parent) {
 for (var i = 0;; ++i) {
 if (chunk.children[i] == cur) break;
 no += chunk.children[i].chunkSize();
 }
 }
 return no + cur.first;
 }

 function lineAtHeight(chunk, h) {
 var n = chunk.first;
 outer: do {
 for (var i = 0, e = chunk.children.length; i < e; ++i) {
 var child = chunk.children[i], ch = child.height;
 if (h < ch) { chunk = child; continue outer; }
 h -= ch;
 n += child.chunkSize();
 }
 return n;
 } while (!chunk.lines);
 for (var i = 0, e = chunk.lines.length; i < e; ++i) {
 var line = chunk.lines[i], lh = line.height;
 if (h < lh) break;
 h -= lh;
 }
 return n + i;
 }

 function heightAtLine(cm, lineObj) {
 lineObj = visualLine(cm.doc, lineObj);

 var h = 0, chunk = lineObj.parent;
 for (var i = 0; i < chunk.lines.length; ++i) {
 var line = chunk.lines[i];
 if (line == lineObj) break;
 else h += line.height;
 }
 for (var p = chunk.parent; p; chunk = p, p = chunk.parent) {
 for (var i = 0; i < p.children.length; ++i) {
 var cur = p.children[i];
 if (cur == chunk) break;
 else h += cur.height;
 }
 }
 return h;
 }

 function getOrder(line) {
 var order = line.order;
 if (order == null) order = line.order = bidiOrdering(line.text);
 return order;
 }

 // HISTORY

 function makeHistory(startGen) {
 return {
 // Arrays of history events. Doing something adds an event to
 // done and clears undo. Undoing moves events from done to
 // undone, redoing moves them in the other direction.
 done: [], undone: [], undoDepth: Infinity,
 // Used to track when changes can be merged into a single undo
 // event
 lastTime: 0, lastOp: null, lastOrigin: null,
 // Used by the isClean() method
 generation: startGen || 1, maxGeneration: startGen || 1
 };
 }

 function attachLocalSpans(doc, change, from, to) {
 var existing = change["spans_" + doc.id], n = 0;
 doc.iter(Math.max(doc.first, from), Math.min(doc.first + doc.size, to), function(line) {
 if (line.markedSpans)
 (existing || (existing = change["spans_" + doc.id] = {}))[n] = line.markedSpans;
 ++n;
 });
 }

 function historyChangeFromChange(doc, change) {
 var from = { line: change.from.line, ch: change.from.ch };
 var histChange = {from: from, to: changeEnd(change), text: getBetween(doc, change.from, change.to)};
 attachLocalSpans(doc, histChange, change.from.line, change.to.line + 1);
 linkedDocs(doc, function(doc) {attachLocalSpans(doc, histChange, change.from.line, change.to.line + 1);}, true);
 return histChange;
 }

 function addToHistory(doc, change, selAfter, opId) {
 var hist = doc.history;
 hist.undone.length = 0;
 var time = +new Date, cur = lst(hist.done);

 if (cur &&
 (hist.lastOp == opId ||
 hist.lastOrigin == change.origin && change.origin &&
 ((change.origin.charAt(0) == "+" && doc.cm && hist.lastTime > time - doc.cm.options.historyEventDelay) ||
 change.origin.charAt(0) == "*"))) {
 // Merge this change into the last event
 var last = lst(cur.changes);
 if (posEq(change.from, change.to) && posEq(change.from, last.to)) {
 // Optimized case for simple insertion -- don't want to add
 // new changesets for every character typed
 last.to = changeEnd(change);
 } else {
 // Add new sub-event
 cur.changes.push(historyChangeFromChange(doc, change));
 }
 cur.anchorAfter = selAfter.anchor; cur.headAfter = selAfter.head;
 } else {
 // Can not be merged, start a new event.
 cur = {changes: [historyChangeFromChange(doc, change)],
 generation: hist.generation,
 anchorBefore: doc.sel.anchor, headBefore: doc.sel.head,
 anchorAfter: selAfter.anchor, headAfter: selAfter.head};
 hist.done.push(cur);
 while (hist.done.length > hist.undoDepth)
 hist.done.shift();
 }
 hist.generation = ++hist.maxGeneration;
 hist.lastTime = time;
 hist.lastOp = opId;
 hist.lastOrigin = change.origin;

 if (!last) signal(doc, "historyAdded");
 }

 function removeClearedSpans(spans) {
 if (!spans) return null;
 for (var i = 0, out; i < spans.length; ++i) {
 if (spans[i].marker.explicitlyCleared) { if (!out) out = spans.slice(0, i); }
 else if (out) out.push(spans[i]);
 }
 return !out ? spans : out.length ? out : null;
 }

 function getOldSpans(doc, change) {
 var found = change["spans_" + doc.id];
 if (!found) return null;
 for (var i = 0, nw = []; i < change.text.length; ++i)
 nw.push(removeClearedSpans(found[i]));
 return nw;
 }

 // Used both to provide a JSON-safe object in .getHistory, and, when
 // detaching a document, to split the history in two
 function copyHistoryArray(events, newGroup) {
 for (var i = 0, copy = []; i < events.length; ++i) {
 var event = events[i], changes = event.changes, newChanges = [];
 copy.push({changes: newChanges, anchorBefore: event.anchorBefore, headBefore: event.headBefore,
 anchorAfter: event.anchorAfter, headAfter: event.headAfter});
 for (var j = 0; j < changes.length; ++j) {
 var change = changes[j], m;
 newChanges.push({from: change.from, to: change.to, text: change.text});
 if (newGroup) for (var prop in change) if (m = prop.match(/^spans_(\d+)$/)) {
 if (indexOf(newGroup, Number(m[1])) > -1) {
 lst(newChanges)[prop] = change[prop];
 delete change[prop];
 }
 }
 }
 }
 return copy;
 }

 // Rebasing/resetting history to deal with externally-sourced changes

 function rebaseHistSel(pos, from, to, diff) {
 if (to < pos.line) {
 pos.line += diff;
 } else if (from < pos.line) {
 pos.line = from;
 pos.ch = 0;
 }
 }

 // Tries to rebase an array of history events given a change in the
 // document. If the change touches the same lines as the event, the
 // event, and everything 'behind' it, is discarded. If the change is
 // before the event, the event's positions are updated. Uses a
 // copy-on-write scheme for the positions, to avoid having to
 // reallocate them all on every rebase, but also avoid problems with
 // shared position objects being unsafely updated.
 function rebaseHistArray(array, from, to, diff) {
 for (var i = 0; i < array.length; ++i) {
 var sub = array[i], ok = true;
 for (var j = 0; j < sub.changes.length; ++j) {
 var cur = sub.changes[j];
 if (!sub.copied) { cur.from = copyPos(cur.from); cur.to = copyPos(cur.to); }
 if (to < cur.from.line) {
 cur.from.line += diff;
 cur.to.line += diff;
 } else if (from <= cur.to.line) {
 ok = false;
 break;
 }
 }
 if (!sub.copied) {
 sub.anchorBefore = copyPos(sub.anchorBefore); sub.headBefore = copyPos(sub.headBefore);
 sub.anchorAfter = copyPos(sub.anchorAfter); sub.readAfter = copyPos(sub.headAfter);
 sub.copied = true;
 }
 if (!ok) {
 array.splice(0, i + 1);
 i = 0;
 } else {
 rebaseHistSel(sub.anchorBefore); rebaseHistSel(sub.headBefore);
 rebaseHistSel(sub.anchorAfter); rebaseHistSel(sub.headAfter);
 }
 }
 }

 function rebaseHist(hist, change) {
 var from = change.from.line, to = change.to.line, diff = change.text.length - (to - from) - 1;
 rebaseHistArray(hist.done, from, to, diff);
 rebaseHistArray(hist.undone, from, to, diff);
 }

 // EVENT OPERATORS

 function stopMethod() {e_stop(this);}
 // Ensure an event has a stop method.
 function addStop(event) {
 if (!event.stop) event.stop = stopMethod;
 return event;
 }

 function e_preventDefault(e) {
 if (e.preventDefault) e.preventDefault();
 else e.returnValue = false;
 }
 function e_stopPropagation(e) {
 if (e.stopPropagation) e.stopPropagation();
 else e.cancelBubble = true;
 }
 function e_defaultPrevented(e) {
 return e.defaultPrevented != null ? e.defaultPrevented : e.returnValue == false;
 }
 function e_stop(e) {e_preventDefault(e); e_stopPropagation(e);}
 CodeMirror.e_stop = e_stop;
 CodeMirror.e_preventDefault = e_preventDefault;
 CodeMirror.e_stopPropagation = e_stopPropagation;

 function e_target(e) {return e.target || e.srcElement;}
 function e_button(e) {
 var b = e.which;
 if (b == null) {
 if (e.button & 1) b = 1;
 else if (e.button & 2) b = 3;
 else if (e.button & 4) b = 2;
 }
 if (mac && e.ctrlKey && b == 1) b = 3;
 return b;
 }

 // EVENT HANDLING

 function on(emitter, type, f) {
 if (emitter.addEventListener)
 emitter.addEventListener(type, f, false);
 else if (emitter.attachEvent)
 emitter.attachEvent("on" + type, f);
 else {
 var map = emitter._handlers || (emitter._handlers = {});
 var arr = map[type] || (map[type] = []);
 arr.push(f);
 }
 }

 function off(emitter, type, f) {
 if (emitter.removeEventListener)
 emitter.removeEventListener(type, f, false);
 else if (emitter.detachEvent)
 emitter.detachEvent("on" + type, f);
 else {
 var arr = emitter._handlers && emitter._handlers[type];
 if (!arr) return;
 for (var i = 0; i < arr.length; ++i)
 if (arr[i] == f) { arr.splice(i, 1); break; }
 }
 }

 function signal(emitter, type /*, values...*/) {
 var arr = emitter._handlers && emitter._handlers[type];
 if (!arr) return;
 var args = Array.prototype.slice.call(arguments, 2);
 for (var i = 0; i < arr.length; ++i) arr[i].apply(null, args);
 }

 var delayedCallbacks, delayedCallbackDepth = 0;
 function signalLater(emitter, type /*, values...*/) {
 var arr = emitter._handlers && emitter._handlers[type];
 if (!arr) return;
 var args = Array.prototype.slice.call(arguments, 2);
 if (!delayedCallbacks) {
 ++delayedCallbackDepth;
 delayedCallbacks = [];
 setTimeout(fireDelayed, 0);
 }
 function bnd(f) {return function(){f.apply(null, args);};};
 for (var i = 0; i < arr.length; ++i)
 delayedCallbacks.push(bnd(arr[i]));
 }

 function signalDOMEvent(cm, e, override) {
 signal(cm, override || e.type, cm, e);
 return e_defaultPrevented(e) || e.codemirrorIgnore;
 }

 function fireDelayed() {
 --delayedCallbackDepth;
 var delayed = delayedCallbacks;
 delayedCallbacks = null;
 for (var i = 0; i < delayed.length; ++i) delayed[i]();
 }

 function hasHandler(emitter, type) {
 var arr = emitter._handlers && emitter._handlers[type];
 return arr && arr.length > 0;
 }

 CodeMirror.on = on; CodeMirror.off = off; CodeMirror.signal = signal;

 function eventMixin(ctor) {
 ctor.prototype.on = function(type, f) {on(this, type, f);};
 ctor.prototype.off = function(type, f) {off(this, type, f);};
 }

